--
-- Name: get_comparativo_anos(integer, integer); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.get_comparativo_anos(p_ano_atual integer, p_ano_anterior integer) RETURNS TABLE(metrica character varying, valor_anterior numeric, valor_atual numeric, variacao numeric)
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_alunos_atual INTEGER;
    v_alunos_anterior INTEGER;
    v_matriculas_atual INTEGER;
    v_matriculas_anterior INTEGER;
    v_evasoes_atual INTEGER;
    v_evasoes_anterior INTEGER;
    v_churn_atual NUMERIC;
    v_churn_anterior NUMERIC;
    v_ticket_atual NUMERIC;
    v_ticket_anterior NUMERIC;
BEGIN
    -- Alunos dezembro
    SELECT SUM(alunos_pagantes) INTO v_alunos_atual FROM dados_mensais WHERE ano = p_ano_atual AND mes = 12;
    SELECT SUM(alunos_pagantes) INTO v_alunos_anterior FROM dados_mensais WHERE ano = p_ano_anterior AND mes = 12;
    
    -- Matrículas total
    SELECT SUM(novas_matriculas) INTO v_matriculas_atual FROM dados_mensais WHERE ano = p_ano_atual;
    SELECT SUM(novas_matriculas) INTO v_matriculas_anterior FROM dados_mensais WHERE ano = p_ano_anterior;
    
    -- Evasões total
    SELECT SUM(evasoes) INTO v_evasoes_atual FROM dados_mensais WHERE ano = p_ano_atual;
    SELECT SUM(evasoes) INTO v_evasoes_anterior FROM dados_mensais WHERE ano = p_ano_anterior;
    
    -- Churn médio
    SELECT ROUND(AVG(churn_rate), 2) INTO v_churn_atual FROM dados_mensais WHERE ano = p_ano_atual;
    SELECT ROUND(AVG(churn_rate), 2) INTO v_churn_anterior FROM dados_mensais WHERE ano = p_ano_anterior;
    
    -- Ticket médio
    SELECT ROUND(AVG(ticket_medio), 2) INTO v_ticket_atual FROM dados_mensais WHERE ano = p_ano_atual;
    SELECT ROUND(AVG(ticket_medio), 2) INTO v_ticket_anterior FROM dados_mensais WHERE ano = p_ano_anterior;
    
    RETURN QUERY
    SELECT 'alunos'::VARCHAR, v_alunos_anterior::NUMERIC, v_alunos_atual::NUMERIC, 
           calcular_variacao(v_alunos_atual, v_alunos_anterior)
    UNION ALL
    SELECT 'matriculas'::VARCHAR, v_matriculas_anterior::NUMERIC, v_matriculas_atual::NUMERIC,
           calcular_variacao(v_matriculas_atual, v_matriculas_anterior)
    UNION ALL
    SELECT 'evasoes'::VARCHAR, v_evasoes_anterior::NUMERIC, v_evasoes_atual::NUMERIC,
           calcular_variacao(v_evasoes_atual, v_evasoes_anterior)
    UNION ALL
    SELECT 'churn'::VARCHAR, v_churn_anterior::NUMERIC, v_churn_atual::NUMERIC,
           (v_churn_atual - v_churn_anterior)
    UNION ALL
    SELECT 'ticket'::VARCHAR, v_ticket_anterior::NUMERIC, v_ticket_atual::NUMERIC,
           calcular_variacao(v_ticket_atual, v_ticket_anterior);
END;
$$;


