        RETURN QUERY
        SELECT u.nome::VARCHAR, u.codigo::VARCHAR, dm.mes, dm.alunos_pagantes::INTEGER
        FROM dados_mensais dm
        JOIN unidades u ON dm.unidade_id = u.id
