    LANGUAGE plpgsql
    AS $$
BEGIN
  RETURN QUERY
  SELECT 
    COUNT(*)::BIGINT,
    COUNT(CASE WHEN e.tipo = 'Interrompido' THEN 1 END)::BIGINT,
    COUNT(CASE WHEN e.tipo = 'Não Renovação' THEN 1 END)::BIGINT,
    COALESCE(SUM(e.parcela), 0)::NUMERIC,
    ROUND(COALESCE(SUM(e.parcela), 0) / 12, 2)::NUMERIC,
    ROUND(COALESCE(AVG(e.parcela), 0), 2)::NUMERIC,
    (SELECT motivo_categoria FROM evasoes 
     WHERE EXTRACT(YEAR FROM competencia) = p_ano 
     AND (p_unidade IS NULL OR unidade = p_unidade) 
     GROUP BY motivo_categoria ORDER BY COUNT(*) DESC LIMIT 1)::VARCHAR,
    (SELECT professor FROM evasoes 
     WHERE EXTRACT(YEAR FROM competencia) = p_ano 
     AND (p_unidade IS NULL OR unidade = p_unidade) 
     AND professor IS NOT NULL AND professor != ''
     GROUP BY professor ORDER BY COUNT(*) DESC LIMIT 1)::VARCHAR
  FROM evasoes e
  WHERE EXTRACT(YEAR FROM e.competencia) = p_ano
    AND (p_unidade IS NULL OR e.unidade = p_unidade);
