CREATE FUNCTION public.get_metas_vs_realizado(p_ano integer) RETURNS TABLE(unidade character varying, codigo character varying, metrica character varying, meta numeric, realizado numeric, percentual_atingido numeric, status character varying)
    LANGUAGE plpgsql
    AS $$
BEGIN
    RETURN QUERY
    SELECT 
        u.nome::VARCHAR,
        u.codigo::VARCHAR,
        'alunos'::VARCHAR,
        m.meta_alunos::NUMERIC,
        MAX(CASE WHEN dm.mes = 12 THEN dm.alunos_pagantes ELSE 0 END)::NUMERIC,
        ROUND((MAX(CASE WHEN dm.mes = 12 THEN dm.alunos_pagantes ELSE 0 END)::NUMERIC / NULLIF(m.meta_alunos, 0) * 100), 1),
        CASE 
            WHEN MAX(CASE WHEN dm.mes = 12 THEN dm.alunos_pagantes ELSE 0 END) >= m.meta_alunos THEN 'atingida'
            WHEN MAX(CASE WHEN dm.mes = 12 THEN dm.alunos_pagantes ELSE 0 END) >= m.meta_alunos * 0.9 THEN 'proximo'
            ELSE 'nao_atingida'
        END::VARCHAR
    FROM metas m
    JOIN unidades u ON m.unidade_id = u.id
    JOIN dados_mensais dm ON dm.unidade_id = u.id AND dm.ano = m.ano
    WHERE m.ano = p_ano
    GROUP BY u.nome, u.codigo, m.meta_alunos;
END;
$$;


