    )
    ON CONFLICT (unidade_id, ano) DO UPDATE SET
        meta_alunos = COALESCE(p_meta_alunos, metas.meta_alunos),
        meta_matriculas_mes = COALESCE(p_meta_matriculas_mes, metas.meta_matriculas_mes),
        meta_evasoes_max = COALESCE(p_meta_evasoes_max, metas.meta_evasoes_max),
