        meta_inadimplencia = COALESCE(p_meta_inadimplencia, metas.meta_inadimplencia),
        meta_faturamento = COALESCE(p_meta_faturamento, metas.meta_faturamento),
        updated_at = NOW()
    RETURNING id INTO v_id;
    
    RETURN v_id;
