CREATE FUNCTION public.usuario_perfis_lista(p_usuario_id integer) RETURNS TABLE(perfil_id uuid, perfil_nome character varying, perfil_nivel integer, perfil_icone character varying, perfil_cor character varying, unidade_id uuid, unidade_nome character varying)
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
BEGIN
  RETURN QUERY
  SELECT 
    pf.id,
    pf.nome,
    pf.nivel,
    pf.icone,
    pf.cor,
    up.unidade_id,
    u.nome
  FROM usuario_perfis up
  JOIN perfis pf ON pf.id = up.perfil_id
  LEFT JOIN unidades u ON u.id = up.unidade_id
  WHERE up.usuario_id = p_usuario_id
    AND up.ativo = true
    AND pf.ativo = true
  ORDER BY pf.nivel DESC, pf.nome;
END;
$$;


