--
-- Name: vw_alertas; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.vw_alertas WITH (security_invoker='true') AS
 SELECT 'CHURN_ALTO'::text AS tipo_alerta,
    u.nome AS unidade,
    'Churn acima de 5%'::text AS descricao,
    rd.churn_rate_mes AS valor,
    rd.data_referencia
   FROM (public.relatorios_diarios rd
     JOIN public.unidades u ON ((rd.unidade_id = u.id)))
  WHERE ((rd.churn_rate_mes > (5)::numeric) AND (rd.data_referencia >= (CURRENT_DATE - '30 days'::interval)))
UNION ALL
 SELECT 'RENOVACAO_BAIXA'::text AS tipo_alerta,
    u.nome AS unidade,
    'Taxa de renovação abaixo de 70%'::text AS descricao,
    rd.taxa_renovacao_mes AS valor,
    rd.data_referencia
   FROM (public.relatorios_diarios rd
     JOIN public.unidades u ON ((rd.unidade_id = u.id)))
  WHERE ((rd.taxa_renovacao_mes < (70)::numeric) AND (rd.data_referencia >= (CURRENT_DATE - '30 days'::interval)))
UNION ALL
 SELECT 'INADIMPLENCIA_ALTA'::text AS tipo_alerta,
    u.nome AS unidade,
    'Inadimplência acima de 10%'::text AS descricao,
    rd.inadimplencia_percentual AS valor,
    rd.data_referencia
   FROM (public.relatorios_diarios rd
     JOIN public.unidades u ON ((rd.unidade_id = u.id)))
  WHERE ((rd.inadimplencia_percentual > (10)::numeric) AND (rd.data_referencia >= (CURRENT_DATE - '30 days'::interval)))
  ORDER BY 5 DESC;


