--
-- Name: vw_turmas_implicitas; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.vw_turmas_implicitas WITH (security_invoker='on') AS
 SELECT a.unidade_id,
    u.nome AS unidade_nome,
    a.professor_atual_id AS professor_id,
    p.nome AS professor_nome,
    a.curso_id,
    c.nome AS curso_nome,
    a.dia_aula AS dia_semana,
    a.horario_aula AS horario_inicio,
    count(*) AS total_alunos,
    array_agg(a.nome ORDER BY a.nome) AS nomes_alunos,
    array_agg(a.id ORDER BY a.nome) AS ids_alunos,
    round(avg(a.valor_parcela), 2) AS ticket_medio_turma,
    round(avg(a.tempo_permanencia_meses), 1) AS tempo_medio_turma
   FROM (((public.alunos a
     LEFT JOIN public.unidades u ON ((a.unidade_id = u.id)))
     LEFT JOIN public.professores p ON ((a.professor_atual_id = p.id)))
     LEFT JOIN public.cursos c ON ((a.curso_id = c.id)))
  WHERE (((a.status)::text = 'ativo'::text) AND (a.professor_atual_id IS NOT NULL) AND (a.dia_aula IS NOT NULL) AND (a.horario_aula IS NOT NULL))
  GROUP BY a.unidade_id, u.nome, a.professor_atual_id, p.nome, a.curso_id, c.nome, a.dia_aula, a.horario_aula
  ORDER BY p.nome, a.dia_aula, a.horario_aula;


