--
-- Name: vw_alertas_inteligentes; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.vw_alertas_inteligentes AS
 WITH churn_realtime AS (
         SELECT u.id AS unidade_id,
            u.nome AS unidade_nome,
            COALESCE(em.evasoes, (0)::bigint) AS evasoes,
            COALESCE(ama.alunos_pagantes, 0) AS alunos_anterior,
                CASE
                    WHEN (COALESCE(ama.alunos_pagantes, 0) > 0) THEN round((((COALESCE(em.evasoes, (0)::bigint))::numeric / (ama.alunos_pagantes)::numeric) * (100)::numeric), 2)
                    ELSE (0)::numeric
                END AS churn_rate
           FROM ((public.unidades u
             LEFT JOIN ( SELECT un.id AS unidade_id,
                    count(*) AS evasoes
                   FROM (public.evasoes e
                     JOIN public.unidades un ON (((un.nome)::text = (e.unidade)::text)))
                  WHERE ((EXTRACT(year FROM e.competencia) = EXTRACT(year FROM CURRENT_DATE)) AND (EXTRACT(month FROM e.competencia) = EXTRACT(month FROM CURRENT_DATE)))
                  GROUP BY un.id) em ON ((em.unidade_id = u.id)))
             LEFT JOIN ( SELECT dados_mensais.unidade_id,
                    dados_mensais.alunos_pagantes
                   FROM public.dados_mensais
                  WHERE ((((dados_mensais.ano)::numeric = EXTRACT(year FROM CURRENT_DATE)) AND ((dados_mensais.mes)::numeric = (EXTRACT(month FROM CURRENT_DATE) - (1)::numeric))) OR (((dados_mensais.ano)::numeric = (EXTRACT(year FROM CURRENT_DATE) - (1)::numeric)) AND (dados_mensais.mes = 12) AND (EXTRACT(month FROM CURRENT_DATE) = (1)::numeric)))) ama ON ((ama.unidade_id = u.id)))
          WHERE (u.ativo = true)
        ), ticket_realtime AS (
         SELECT a.unidade_id,
            avg(a.valor_parcela) FILTER (WHERE (tm.entra_ticket_medio = true)) AS ticket_atual
           FROM (public.alunos a
             LEFT JOIN public.tipos_matricula tm ON ((tm.id = a.tipo_matricula_id)))
          WHERE ((a.status)::text = 'ativo'::text)
          GROUP BY a.unidade_id
        )
 SELECT tipo_alerta,
    severidade,
    unidade_id,
    unidade_nome,
    quantidade,
    descricao,
    detalhe,
    valor_atual,
    valor_meta,
    data_referencia
   FROM ( SELECT 'CONTRATO_VENCENDO'::text AS tipo_alerta,
            'critico'::text AS severidade,
            a.unidade_id,
            u.nome AS unidade_nome,
            (count(*))::integer AS quantidade,
            'Contratos vencendo em 30 dias sem renovação'::text AS descricao,
            concat(count(*), ' alunos com contrato vencendo até ', to_char((CURRENT_DATE + '30 days'::interval), 'DD/MM'::text)) AS detalhe,
            NULL::numeric AS valor_atual,
            NULL::numeric AS valor_meta,
            CURRENT_DATE AS data_referencia
           FROM ((public.alunos a
             JOIN public.unidades u ON ((a.unidade_id = u.id)))
             LEFT JOIN public.renovacoes r ON (((r.aluno_id = a.id) AND (r.data_fim_novo_contrato > a.data_fim_contrato) AND ((r.status)::text = 'concluida'::text))))
          WHERE (((a.status)::text = 'ativo'::text) AND (a.data_fim_contrato >= CURRENT_DATE) AND (a.data_fim_contrato <= (CURRENT_DATE + '30 days'::interval)) AND (r.id IS NULL))
          GROUP BY a.unidade_id, u.nome
         HAVING (count(*) > 0)
        UNION ALL
         SELECT 'RENOVACOES_PENDENTES'::text AS text,
            'atencao'::text AS text,
            a.unidade_id,
            u.nome,
            (count(*))::integer AS count,
            'Renovações pendentes para este mês'::text AS text,
            concat(count(*), ' contratos vencem este mês') AS concat,
            NULL::numeric AS "numeric",
            NULL::numeric AS "numeric",
            CURRENT_DATE AS "current_date"
           FROM (public.alunos a
             JOIN public.unidades u ON ((a.unidade_id = u.id)))
          WHERE (((a.status)::text = 'ativo'::text) AND (EXTRACT(month FROM a.data_fim_contrato) = EXTRACT(month FROM CURRENT_DATE)) AND (EXTRACT(year FROM a.data_fim_contrato) = EXTRACT(year FROM CURRENT_DATE)))
          GROUP BY a.unidade_id, u.nome
         HAVING (count(*) > 0)
        UNION ALL
         SELECT 'CONVERSAO_BAIXA'::text AS text,
                CASE
                    WHEN ((((dc.novas_matriculas_total)::numeric / (NULLIF(dc.aulas_experimentais, 0))::numeric) * (100)::numeric) < (10)::numeric) THEN 'critico'::text
                    ELSE 'atencao'::text
                END AS "case",
            u.id,
            u.nome,
            1,
            'Taxa de conversão abaixo da meta'::text AS text,
            concat('Conversão: ', round((((dc.novas_matriculas_total)::numeric / (NULLIF(dc.aulas_experimentais, 0))::numeric) * (100)::numeric), 1), '% (meta: 13.5%)') AS concat,
            round((((dc.novas_matriculas_total)::numeric / (NULLIF(dc.aulas_experimentais, 0))::numeric) * (100)::numeric), 1) AS round,
            13.5,
            dc.competencia
           FROM (public.dados_comerciais dc
             JOIN public.unidades u ON ((lower((u.nome)::text) = lower((dc.unidade)::text))))
          WHERE ((dc.competencia = date_trunc('month'::text, (CURRENT_DATE)::timestamp with time zone)) AND (dc.aulas_experimentais > 0) AND ((((dc.novas_matriculas_total)::numeric / (dc.aulas_experimentais)::numeric) * (100)::numeric) < 13.5))
        UNION ALL
         SELECT 'CHURN_ALTO'::text AS text,
                CASE
                    WHEN (cr.churn_rate > (6)::numeric) THEN 'critico'::text
                    ELSE 'atencao'::text
                END AS "case",
            cr.unidade_id,
            cr.unidade_nome,
            1,
            'Churn acima da meta mensal'::text AS text,
            concat('Churn: ', cr.churn_rate, '% (meta: 4%)') AS concat,
            cr.churn_rate,
            4.0,
            CURRENT_DATE AS "current_date"
           FROM churn_realtime cr
          WHERE (cr.churn_rate > (4)::numeric)
        UNION ALL
         SELECT 'TICKET_CAINDO'::text AS text,
            'atencao'::text AS text,
            tr.unidade_id,
            u.nome,
            1,
            'Ticket médio caindo vs mês anterior'::text AS text,
            concat('Ticket caiu ', round((((dm.ticket_medio - tr.ticket_atual) / NULLIF(dm.ticket_medio, (0)::numeric)) * (100)::numeric), 1), '% (R$', round(dm.ticket_medio), ' → R$', round(tr.ticket_atual), ')') AS concat,
            tr.ticket_atual,
            dm.ticket_medio,
            CURRENT_DATE AS "current_date"
           FROM ((ticket_realtime tr
             JOIN public.unidades u ON ((u.id = tr.unidade_id)))
             JOIN public.dados_mensais dm ON (((dm.unidade_id = tr.unidade_id) AND ((((dm.ano)::numeric = EXTRACT(year FROM CURRENT_DATE)) AND ((dm.mes)::numeric = (EXTRACT(month FROM CURRENT_DATE) - (1)::numeric))) OR (((dm.ano)::numeric = (EXTRACT(year FROM CURRENT_DATE) - (1)::numeric)) AND (dm.mes = 12) AND (EXTRACT(month FROM CURRENT_DATE) = (1)::numeric))))))
          WHERE (tr.ticket_atual < dm.ticket_medio)
        UNION ALL
         SELECT 'PROFESSOR_TURMA_BAIXA'::text AS text,
            'informativo'::text AS text,
            pu.unidade_id,
            u.nome,
            (count(DISTINCT t.professor_id))::integer AS count,
            'Professores com média alunos/turma baixa'::text AS text,
            concat(count(DISTINCT t.professor_id), ' professores com média < 1.5 alunos/turma') AS concat,
            NULL::numeric AS "numeric",
            1.5,
            CURRENT_DATE AS "current_date"
           FROM ((( SELECT vw_turmas_implicitas.professor_id,
                    avg(vw_turmas_implicitas.total_alunos) AS media
                   FROM public.vw_turmas_implicitas
