                 HAVING (avg(vw_turmas_implicitas.total_alunos) < 1.5)) t
             JOIN public.professores_unidades pu ON ((t.professor_id = pu.professor_id)))
             JOIN public.unidades u ON ((pu.unidade_id = u.id)))
          GROUP BY pu.unidade_id, u.nome
         HAVING (count(DISTINCT t.professor_id) > 0)
        UNION ALL
         SELECT 'META_EM_RISCO'::text AS text,
            'critico'::text AS text,
            mk.unidade_id,
            u.nome,
