--
-- Name: vw_dashboard_unidade; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.vw_dashboard_unidade AS
 WITH alunos_ativos AS (
         SELECT a.unidade_id,
            count(*) AS total_ativos,
            count(*) FILTER (WHERE (tm.conta_como_pagante = true)) AS total_pagantes,
            avg(a.valor_parcela) FILTER (WHERE (tm.entra_ticket_medio = true)) AS ticket_medio,
            sum(a.valor_parcela) FILTER (WHERE (tm.conta_como_pagante = true)) AS mrr,
            avg(a.tempo_permanencia_meses) AS tempo_permanencia_medio
           FROM (public.alunos a
             LEFT JOIN public.tipos_matricula tm ON ((tm.id = a.tipo_matricula_id)))
          WHERE ((a.status)::text = 'ativo'::text)
          GROUP BY a.unidade_id
        ), evasoes_mes AS (
         SELECT u_1.id AS unidade_id,
            count(*) AS evasoes_realtime
           FROM (public.evasoes e
             JOIN public.unidades u_1 ON (((u_1.nome)::text = (e.unidade)::text)))
          WHERE ((EXTRACT(year FROM e.competencia) = EXTRACT(year FROM CURRENT_DATE)) AND (EXTRACT(month FROM e.competencia) = EXTRACT(month FROM CURRENT_DATE)))
          GROUP BY u_1.id
        ), matriculas_mes AS (
         SELECT leads_diarios.unidade_id,
            COALESCE(sum(leads_diarios.quantidade), (0)::bigint) AS matriculas_realtime
           FROM public.leads_diarios
          WHERE (((leads_diarios.tipo)::text = 'matricula'::text) AND (EXTRACT(year FROM leads_diarios.data) = EXTRACT(year FROM CURRENT_DATE)) AND (EXTRACT(month FROM leads_diarios.data) = EXTRACT(month FROM CURRENT_DATE)))
          GROUP BY leads_diarios.unidade_id
        ), renovacoes_mes AS (
         SELECT renovacoes.unidade_id,
            count(*) AS renovacoes_realtime,
            avg(renovacoes.percentual_reajuste) AS reajuste_medio
           FROM public.renovacoes
          WHERE ((EXTRACT(year FROM renovacoes.data_renovacao) = EXTRACT(year FROM CURRENT_DATE)) AND (EXTRACT(month FROM renovacoes.data_renovacao) = EXTRACT(month FROM CURRENT_DATE)) AND ((renovacoes.status)::text = 'renovado'::text))
          GROUP BY renovacoes.unidade_id
        ), contratos_vencer AS (
         SELECT alunos.unidade_id,
            count(*) AS total_vencer
           FROM public.alunos
          WHERE (((alunos.status)::text = 'ativo'::text) AND (EXTRACT(year FROM alunos.data_fim_contrato) = EXTRACT(year FROM CURRENT_DATE)) AND (EXTRACT(month FROM alunos.data_fim_contrato) = EXTRACT(month FROM CURRENT_DATE)))
          GROUP BY alunos.unidade_id
        ), alunos_mes_anterior AS (
         SELECT dados_mensais.unidade_id,
            dados_mensais.alunos_pagantes
           FROM public.dados_mensais
          WHERE ((((dados_mensais.ano)::numeric = EXTRACT(year FROM CURRENT_DATE)) AND ((dados_mensais.mes)::numeric = (EXTRACT(month FROM CURRENT_DATE) - (1)::numeric))) OR (((dados_mensais.ano)::numeric = (EXTRACT(year FROM CURRENT_DATE) - (1)::numeric)) AND (dados_mensais.mes = 12) AND (EXTRACT(month FROM CURRENT_DATE) = (1)::numeric)))
        ), inadimplencia_atual AS (
         SELECT a.unidade_id,
            count(*) FILTER (WHERE ((a.status_pagamento)::text = 'inadimplente'::text)) AS qtd_inadimplentes,
            count(*) AS total_pagantes_calc
           FROM (public.alunos a
             LEFT JOIN public.tipos_matricula tm ON ((tm.id = a.tipo_matricula_id)))
          WHERE (((a.status)::text = 'ativo'::text) AND ((tm.conta_como_pagante = true) OR (tm.id IS NULL)))
          GROUP BY a.unidade_id
        )
 SELECT u.id AS unidade_id,
    u.nome AS unidade_nome,
    u.codigo,
    (COALESCE(aa.total_ativos, (0)::bigint))::integer AS alunos_ativos,
    (COALESCE(aa.total_pagantes, (0)::bigint))::integer AS alunos_pagantes,
    (COALESCE(aa.ticket_medio, (0)::numeric))::numeric(10,2) AS ticket_medio,
    (COALESCE(aa.mrr, (0)::numeric))::numeric(12,2) AS mrr,
    (COALESCE(mm.matriculas_realtime, (0)::bigint))::integer AS matriculas_mes,
    (COALESCE(em.evasoes_realtime, (0)::bigint))::integer AS evasoes_mes,
    (
        CASE
            WHEN (COALESCE(ama.alunos_pagantes, 0) > 0) THEN round((((COALESCE(em.evasoes_realtime, (0)::bigint))::numeric / (ama.alunos_pagantes)::numeric) * (100)::numeric), 2)
            ELSE (0)::numeric
        END)::numeric(5,2) AS churn_rate,
    (
        CASE
            WHEN (COALESCE(cv.total_vencer, (0)::bigint) > 0) THEN round((((COALESCE(rm.renovacoes_realtime, (0)::bigint))::numeric / (cv.total_vencer)::numeric) * (100)::numeric), 2)
            ELSE (0)::numeric
        END)::numeric(5,2) AS taxa_renovacao,
    (
        CASE
            WHEN (COALESCE(ia.total_pagantes_calc, (0)::bigint) > 0) THEN round((((COALESCE(ia.qtd_inadimplentes, (0)::bigint))::numeric / (ia.total_pagantes_calc)::numeric) * (100)::numeric), 2)
            ELSE (0)::numeric
        END)::numeric(5,2) AS inadimplencia_pct,
    (COALESCE(aa.tempo_permanencia_medio, (0)::numeric))::numeric(5,1) AS tempo_permanencia,
    (COALESCE(rm.reajuste_medio, (0)::numeric))::numeric(5,2) AS reajuste_medio
   FROM (((((((public.unidades u
     LEFT JOIN alunos_ativos aa ON ((aa.unidade_id = u.id)))
     LEFT JOIN evasoes_mes em ON ((em.unidade_id = u.id)))
     LEFT JOIN matriculas_mes mm ON ((mm.unidade_id = u.id)))
     LEFT JOIN renovacoes_mes rm ON ((rm.unidade_id = u.id)))
     LEFT JOIN contratos_vencer cv ON ((cv.unidade_id = u.id)))
     LEFT JOIN alunos_mes_anterior ama ON ((ama.unidade_id = u.id)))
     LEFT JOIN inadimplencia_atual ia ON ((ia.unidade_id = u.id)))
  WHERE (u.ativo = true);


