        CASE
            WHEN ((tempo_permanencia_meses >= 4) AND (tempo_permanencia_meses <= 6)) THEN '04-06 meses'::text
            WHEN ((tempo_permanencia_meses >= 7) AND (tempo_permanencia_meses <= 12)) THEN '07-12 meses'::text
            WHEN ((tempo_permanencia_meses >= 13) AND (tempo_permanencia_meses <= 24)) THEN '13-24 meses'::text
            WHEN ((tempo_permanencia_meses >= 25) AND (tempo_permanencia_meses <= 36)) THEN '25-36 meses'::text
            WHEN ((tempo_permanencia_meses >= 37) AND (tempo_permanencia_meses <= 48)) THEN '37-48 meses'::text
            ELSE '49+ meses'::text
        END AS faixa,
    count(*) AS quantidade,
    round(((100.0 * (count(*))::numeric) / sum(count(*)) OVER ()), 1) AS percentual
   FROM public.alunos_historico
  GROUP BY
        CASE
            WHEN ((tempo_permanencia_meses >= 4) AND (tempo_permanencia_meses <= 6)) THEN '04-06 meses'::text
            WHEN ((tempo_permanencia_meses >= 7) AND (tempo_permanencia_meses <= 12)) THEN '07-12 meses'::text
            WHEN ((tempo_permanencia_meses >= 13) AND (tempo_permanencia_meses <= 24)) THEN '13-24 meses'::text
            WHEN ((tempo_permanencia_meses >= 25) AND (tempo_permanencia_meses <= 36)) THEN '25-36 meses'::text
            WHEN ((tempo_permanencia_meses >= 37) AND (tempo_permanencia_meses <= 48)) THEN '37-48 meses'::text
