-- Name: vw_farmer_renovacoes_proximas; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.vw_farmer_renovacoes_proximas AS
 SELECT a.id AS aluno_id,
    a.nome AS aluno_nome,
    a.whatsapp,
    a.unidade_id,
    a.data_fim_contrato AS data_vencimento,
    (a.data_fim_contrato - CURRENT_DATE) AS dias_para_vencer,
    a.valor_parcela,
    p.id AS professor_id,
    p.nome AS professor_nome,
    c.nome AS instrumento,
        CASE
            WHEN (a.data_fim_contrato < CURRENT_DATE) THEN 'vencido'::text
            WHEN (a.data_fim_contrato <= (CURRENT_DATE + '7 days'::interval)) THEN 'urgente'::text
            WHEN (a.data_fim_contrato <= (CURRENT_DATE + '15 days'::interval)) THEN 'atencao'::text
            ELSE 'normal'::text
        END AS urgencia
   FROM (((public.alunos a
     LEFT JOIN public.professores p ON ((a.professor_atual_id = p.id)))
