CREATE VIEW public.vw_fator_demanda_professor AS
 WITH alunos_por_curso AS (
         SELECT a.professor_atual_id AS professor_id,
            a.unidade_id,
            a.curso_id,
            c.nome AS curso_nome,
            COALESCE(c.fator_demanda, 1.0) AS fator_demanda_curso,
            count(*) AS quantidade_alunos
           FROM (public.alunos a
             JOIN public.cursos c ON ((c.id = a.curso_id)))
          WHERE (((a.status)::text = 'ativo'::text) AND (a.professor_atual_id IS NOT NULL) AND (a.curso_id IS NOT NULL))
          GROUP BY a.professor_atual_id, a.unidade_id, a.curso_id, c.nome, c.fator_demanda
        ), totais_professor AS (
         SELECT alunos_por_curso.professor_id,
            alunos_por_curso.unidade_id,
            sum(alunos_por_curso.quantidade_alunos) AS total_alunos
           FROM alunos_por_curso
          GROUP BY alunos_por_curso.professor_id, alunos_por_curso.unidade_id
        ), fator_ponderado AS (
         SELECT apc.professor_id,
            apc.unidade_id,
            tp.total_alunos,
            sum((((apc.quantidade_alunos)::numeric / NULLIF(tp.total_alunos, (0)::numeric)) * apc.fator_demanda_curso)) AS fator_demanda_ponderado,
            json_agg(json_build_object('curso_id', apc.curso_id, 'curso_nome', apc.curso_nome, 'quantidade', apc.quantidade_alunos, 'percentual', round((((apc.quantidade_alunos)::numeric / NULLIF(tp.total_alunos, (0)::numeric)) * (100)::numeric), 1), 'fator_curso', apc.fator_demanda_curso, 'contribuicao', round((((apc.quantidade_alunos)::numeric / NULLIF(tp.total_alunos, (0)::numeric)) * apc.fator_demanda_curso), 2)) ORDER BY apc.quantidade_alunos DESC) AS detalhamento_cursos
           FROM (alunos_por_curso apc
             JOIN totais_professor tp ON (((tp.professor_id = apc.professor_id) AND (tp.unidade_id = apc.unidade_id))))
          GROUP BY apc.professor_id, apc.unidade_id, tp.total_alunos
        )
 SELECT p.id AS professor_id,
    p.nome AS professor_nome,
    fp.unidade_id,
    (COALESCE(fp.total_alunos, (0)::numeric))::integer AS total_alunos,
    round(COALESCE(fp.fator_demanda_ponderado, 1.0), 2) AS fator_demanda_ponderado,
    COALESCE(fp.detalhamento_cursos, '[]'::json) AS detalhamento_cursos
   FROM (public.professores p
     LEFT JOIN fator_ponderado fp ON ((fp.professor_id = p.id)))
