 SELECT u.nome AS unidade,
    (EXTRACT(year FROM l.data_contato))::integer AS ano,
    (EXTRACT(month FROM l.data_contato))::integer AS mes,
    to_char((l.data_contato)::timestamp with time zone, 'YYYY-MM'::text) AS ano_mes,
    count(*) AS total_leads,
    count(*) FILTER (WHERE ((l.status)::text = 'arquivado'::text)) AS leads_arquivados,
    count(*) FILTER (WHERE (l.experimental_agendada = true)) AS experimentais_agendadas,
    count(*) FILTER (WHERE (l.experimental_realizada = true)) AS experimentais_realizadas,
    count(*) FILTER (WHERE (l.faltou_experimental = true)) AS faltaram,
    count(*) FILTER (WHERE (l.converteu = true)) AS matriculas,
    round(((100.0 * (count(*) FILTER (WHERE (l.experimental_agendada = true)))::numeric) / (NULLIF(count(*), 0))::numeric), 1) AS taxa_lead_experimental,
    round(((100.0 * (count(*) FILTER (WHERE (l.converteu = true)))::numeric) / (NULLIF(count(*) FILTER (WHERE (l.experimental_realizada = true)), 0))::numeric), 1) AS taxa_experimental_matricula,
    round(((100.0 * (count(*) FILTER (WHERE (l.converteu = true)))::numeric) / (NULLIF(count(*), 0))::numeric), 1) AS taxa_lead_matricula
   FROM (public.leads l
     JOIN public.unidades u ON ((l.unidade_id = u.id)))
  GROUP BY u.nome, ((EXTRACT(year FROM l.data_contato))::integer), ((EXTRACT(month FROM l.data_contato))::integer), (to_char((l.data_contato)::timestamp with time zone, 'YYYY-MM'::text))
