    (COALESCE(lm.faltaram, (0)::bigint))::integer AS faltaram,
        CASE
            WHEN (COALESCE(lm.experimentais_agendadas, (0)::bigint) > 0) THEN ((((COALESCE(lm.experimentais_realizadas, (0)::bigint))::numeric / (lm.experimentais_agendadas)::numeric) * (100)::numeric))::numeric(5,2)
            ELSE (0)::numeric
        END AS taxa_showup,
    (COALESCE(mm.total_matriculas, lm.novas_matriculas, (0)::bigint))::integer AS novas_matriculas,
        CASE
            WHEN (COALESCE(lm.total_leads, (0)::bigint) > 0) THEN ((((COALESCE(lm.experimentais_realizadas, (0)::bigint))::numeric / (lm.total_leads)::numeric) * (100)::numeric))::numeric(5,2)
            ELSE (0)::numeric
        END AS taxa_conversao_lead_exp,
        CASE
            WHEN (COALESCE(lm.experimentais_realizadas, (0)::bigint) > 0) THEN ((((COALESCE(mm.total_matriculas, lm.novas_matriculas, (0)::bigint))::numeric / (lm.experimentais_realizadas)::numeric) * (100)::numeric))::numeric(5,2)
            ELSE (0)::numeric
        END AS taxa_conversao_exp_mat,
        CASE
            WHEN (COALESCE(lm.total_leads, (0)::bigint) > 0) THEN ((((COALESCE(mm.total_matriculas, lm.novas_matriculas, (0)::bigint))::numeric / (lm.total_leads)::numeric) * (100)::numeric))::numeric(5,2)
            ELSE (0)::numeric
        END AS taxa_conversao_geral,
    (COALESCE(mm.faturamento_novos, (0)::numeric))::numeric(12,2) AS faturamento_novos,
