          WHERE (((a.status)::text = 'ativo'::text) AND (a.professor_atual_id IS NOT NULL))
          GROUP BY a.professor_atual_id, a.unidade_id
        ), experimentais AS (
         SELECT ld.professor_experimental_id AS professor_id,
            ld.unidade_id,
            (EXTRACT(year FROM ld.data))::integer AS ano,
            (EXTRACT(month FROM ld.data))::integer AS mes,
            sum(
                CASE
                    WHEN ((ld.tipo)::text = 'experimental_realizada'::text) THEN ld.quantidade
                    ELSE 0
                END) AS experimentais,
            sum(
                CASE
                    WHEN ((ld.tipo)::text = 'matricula'::text) THEN ld.quantidade
                    ELSE 0
                END) AS matriculas_leads
           FROM public.leads_diarios ld
          WHERE (ld.professor_experimental_id IS NOT NULL)
          GROUP BY ld.professor_experimental_id, ld.unidade_id, (EXTRACT(year FROM ld.data)), (EXTRACT(month FROM ld.data))
        ), matriculas_mes AS (
         SELECT a.professor_experimental_id AS professor_id,
            a.unidade_id,
            (EXTRACT(year FROM a.data_matricula))::integer AS ano,
            (EXTRACT(month FROM a.data_matricula))::integer AS mes,
            count(*) AS matriculas
           FROM public.alunos a
          WHERE ((a.professor_experimental_id IS NOT NULL) AND (a.data_matricula IS NOT NULL))
          GROUP BY a.professor_experimental_id, a.unidade_id, (EXTRACT(year FROM a.data_matricula)), (EXTRACT(month FROM a.data_matricula))
        ), renovacoes_mes AS (
         SELECT r_1.professor_id,
            r_1.unidade_id,
            (EXTRACT(year FROM r_1.data_renovacao))::integer AS ano,
            (EXTRACT(month FROM r_1.data_renovacao))::integer AS mes,
            count(*) FILTER (WHERE ((r_1.status)::text = 'realizada'::text)) AS renovacoes,
            count(*) FILTER (WHERE ((r_1.status)::text = 'nao_renovada'::text)) AS nao_renovacoes,
            count(*) AS total_renovacoes
           FROM public.renovacoes r_1
          WHERE (r_1.professor_id IS NOT NULL)
          GROUP BY r_1.professor_id, r_1.unidade_id, (EXTRACT(year FROM r_1.data_renovacao)), (EXTRACT(month FROM r_1.data_renovacao))
        ), evasoes_mes AS (
         SELECT ev_1.professor_id,
            ev_1.unidade_id,
            (EXTRACT(year FROM ev_1.data_evasao))::integer AS ano,
            (EXTRACT(month FROM ev_1.data_evasao))::integer AS mes,
            count(*) AS evasoes,
            sum(ev_1.valor_parcela) AS mrr_perdido
           FROM public.evasoes_v2 ev_1
          WHERE (ev_1.professor_id IS NOT NULL)
          GROUP BY ev_1.professor_id, ev_1.unidade_id, (EXTRACT(year FROM ev_1.data_evasao)), (EXTRACT(month FROM ev_1.data_evasao))
        )
 SELECT p.id AS professor_id,
    p.nome AS professor_nome,
    COALESCE(c.unidade_id, e.unidade_id, m.unidade_id) AS unidade_id,
    COALESCE(e.ano, (EXTRACT(year FROM CURRENT_DATE))::integer) AS ano,
    COALESCE(e.mes, (EXTRACT(month FROM CURRENT_DATE))::integer) AS mes,
    (COALESCE(c.carteira_alunos, (0)::bigint))::integer AS carteira_alunos,
    (COALESCE(c.ticket_medio, (0)::numeric))::numeric(10,2) AS ticket_medio,
    (COALESCE(c.media_presenca, (0)::numeric))::numeric(5,2) AS media_presenca,
    (COALESCE(((100)::numeric - c.media_presenca), (0)::numeric))::numeric(5,2) AS taxa_faltas,
    (COALESCE(c.mrr_carteira, (0)::numeric))::numeric(12,2) AS mrr_carteira,
    (COALESCE(p.nps_medio, (0)::numeric))::numeric(5,2) AS nps_medio,
    (COALESCE(p.media_alunos_turma, (0)::numeric))::numeric(5,2) AS media_alunos_turma,
    (COALESCE(e.experimentais, (0)::bigint))::integer AS experimentais,
    (COALESCE(m.matriculas, e.matriculas_leads, (0)::bigint))::integer AS matriculas,
        CASE
            WHEN (COALESCE(e.experimentais, (0)::bigint) > 0) THEN round((((COALESCE(m.matriculas, e.matriculas_leads, (0)::bigint))::numeric / (e.experimentais)::numeric) * (100)::numeric), 2)
            ELSE (0)::numeric
        END AS taxa_conversao,
    (COALESCE(r.renovacoes, (0)::bigint))::integer AS renovacoes,
    (COALESCE(r.nao_renovacoes, (0)::bigint))::integer AS nao_renovacoes,
        CASE
            WHEN (COALESCE(r.total_renovacoes, (0)::bigint) > 0) THEN round((((r.renovacoes)::numeric / (r.total_renovacoes)::numeric) * (100)::numeric), 2)
            ELSE (0)::numeric
        END AS taxa_renovacao,
    (COALESCE(ev.evasoes, (0)::bigint))::integer AS evasoes,
    (COALESCE(ev.mrr_perdido, (0)::numeric))::numeric(12,2) AS mrr_perdido,
        CASE
            WHEN (COALESCE(c.carteira_alunos, (0)::bigint) > 0) THEN round((((COALESCE(ev.evasoes, (0)::bigint))::numeric / (c.carteira_alunos)::numeric) * (100)::numeric), 2)
            ELSE (0)::numeric
        END AS taxa_cancelamento,
    rank() OVER (ORDER BY
        CASE
            WHEN (COALESCE(e.experimentais, (0)::bigint) > 0) THEN ((COALESCE(m.matriculas, (0)::bigint))::numeric / (e.experimentais)::numeric)
            ELSE (0)::numeric
        END DESC) AS ranking_matriculador,
    rank() OVER (ORDER BY
