            WHEN (COALESCE(r.total_renovacoes, (0)::bigint) > 0) THEN ((r.renovacoes)::numeric / (r.total_renovacoes)::numeric)
            ELSE (0)::numeric
        END DESC) AS ranking_renovador,
    rank() OVER (ORDER BY
