20260110004143	{"-- ============================================\n-- FASE 3: TABELA DE MOVIMENTAÇÕES\n-- ============================================\n\n-- Tabela principal de movimentações\nCREATE TABLE IF NOT EXISTS movimentacoes (\n    id SERIAL PRIMARY KEY,\n    \n    -- Relacionamentos\n    aluno_id INTEGER REFERENCES alunos(id),\n    unidade_id UUID REFERENCES unidades(id) NOT NULL,\n    curso_id INTEGER REFERENCES cursos(id),\n    professor_id INTEGER REFERENCES professores(id),\n    \n    -- Tipo de movimentação com CHECK constraint\n    tipo VARCHAR(50) NOT NULL CHECK (tipo IN ('matricula', 'renovacao', 'evasao', 'transferencia', 'troca_curso', 'troca_professor')),\n    \n    -- Datas\n    data_movimentacao DATE NOT NULL DEFAULT CURRENT_DATE,\n    data_referencia DATE, -- Mês/ano de referência (ex: competência)\n    \n    -- Para evasões\n    motivo_saida_id INTEGER REFERENCES motivos_saida(id),\n    tipo_saida_id INTEGER REFERENCES tipos_saida(id),\n    \n    -- Para transferências e trocas\n    unidade_origem_id UUID REFERENCES unidades(id),\n    unidade_destino_id UUID REFERENCES unidades(id),\n    curso_anterior_id INTEGER REFERENCES cursos(id),\n    professor_anterior_id INTEGER REFERENCES professores(id),\n    \n    -- Valores\n    valor_mensalidade NUMERIC(10,2),\n    \n    -- Canal de origem (para matrículas)\n    canal_origem_id INTEGER REFERENCES canais_origem(id),\n    \n    -- Observações\n    observacoes TEXT,\n    \n    -- Auditoria\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    created_by VARCHAR(100)\n);\n\n-- Trigger para updated_at\nCREATE TRIGGER update_movimentacoes_updated_at\n    BEFORE UPDATE ON movimentacoes\n    FOR EACH ROW\n    EXECUTE FUNCTION update_updated_at_column();\n\n-- Índices para performance\nCREATE INDEX idx_movimentacoes_aluno ON movimentacoes(aluno_id);\nCREATE INDEX idx_movimentacoes_unidade ON movimentacoes(unidade_id);\nCREATE INDEX idx_movimentacoes_tipo ON movimentacoes(tipo);\nCREATE INDEX idx_movimentacoes_data ON movimentacoes(data_movimentacao);\nCREATE INDEX idx_movimentacoes_data_ref ON movimentacoes(data_referencia);\nCREATE INDEX idx_movimentacoes_mes_ano ON movimentacoes(EXTRACT(YEAR FROM data_movimentacao), EXTRACT(MONTH FROM data_movimentacao));\nCREATE INDEX idx_movimentacoes_unidade_data_tipo ON movimentacoes(unidade_id, data_movimentacao, tipo);\n\n-- Comentários\nCOMMENT ON TABLE movimentacoes IS 'Registro de todas as movimentações de alunos: matrículas, renovações, evasões, transferências, trocas de curso/professor.';\nCOMMENT ON COLUMN movimentacoes.tipo IS 'Tipos válidos: matricula, renovacao, evasao, transferencia, troca_curso, troca_professor';\nCOMMENT ON COLUMN movimentacoes.data_referencia IS 'Mês/ano de competência da movimentação (para relatórios mensais)';"}	fase3_criar_tabela_movimentacoes	producoes.emla@gmail.com	\N	\N
20260110004211	{"-- ============================================\n-- VIEWS DE ANÁLISE DE MOVIMENTAÇÕES\n-- ============================================\n\n-- VIEW 1: Resumo mensal de movimentações\nCREATE OR REPLACE VIEW vw_movimentacoes_mensal AS\nSELECT \n    u.nome AS unidade,\n    EXTRACT(YEAR FROM m.data_movimentacao)::INTEGER AS ano,\n    EXTRACT(MONTH FROM m.data_movimentacao)::INTEGER AS mes,\n    TO_CHAR(m.data_movimentacao, 'YYYY-MM') AS ano_mes,\n    m.tipo,\n    COUNT(*) AS quantidade\nFROM movimentacoes m\nJOIN unidades u ON m.unidade_id = u.id\nGROUP BY u.nome, ano, mes, ano_mes, m.tipo\nORDER BY ano DESC, mes DESC, u.nome, m.tipo;\n\n-- VIEW 2: KPIs mensais por unidade\nCREATE OR REPLACE VIEW vw_kpis_mensais AS\nSELECT \n    u.nome AS unidade,\n    TO_CHAR(m.data_movimentacao, 'YYYY-MM') AS ano_mes,\n    SUM(CASE WHEN m.tipo = 'matricula' THEN 1 ELSE 0 END) AS matriculas,\n    SUM(CASE WHEN m.tipo = 'renovacao' THEN 1 ELSE 0 END) AS renovacoes,\n    SUM(CASE WHEN m.tipo = 'evasao' THEN 1 ELSE 0 END) AS evasoes,\n    SUM(CASE WHEN m.tipo = 'transferencia' THEN 1 ELSE 0 END) AS transferencias\nFROM movimentacoes m\nJOIN unidades u ON m.unidade_id = u.id\nGROUP BY u.nome, ano_mes\nORDER BY ano_mes DESC, u.nome;\n\n-- VIEW 3: Taxa de evasão por motivo\nCREATE OR REPLACE VIEW vw_evasao_por_motivo AS\nSELECT \n    u.nome AS unidade,\n    COALESCE(ms.nome, 'Não informado') AS motivo,\n    COUNT(*) AS quantidade,\n    ROUND(100.0 * COUNT(*) / NULLIF(SUM(COUNT(*)) OVER (PARTITION BY u.nome), 0), 1) AS percentual\nFROM movimentacoes m\nJOIN unidades u ON m.unidade_id = u.id\nLEFT JOIN motivos_saida ms ON m.motivo_saida_id = ms.id\nWHERE m.tipo = 'evasao'\nGROUP BY u.nome, ms.nome\nORDER BY u.nome, quantidade DESC;\n\n-- VIEW 4: Taxa de evasão por tipo (Interrompido vs Não renovou)\nCREATE OR REPLACE VIEW vw_evasao_por_tipo AS\nSELECT \n    u.nome AS unidade,\n    COALESCE(ts.nome, 'Não informado') AS tipo_saida,\n    COUNT(*) AS quantidade,\n    ROUND(100.0 * COUNT(*) / NULLIF(SUM(COUNT(*)) OVER (PARTITION BY u.nome), 0), 1) AS percentual\nFROM movimentacoes m\nJOIN unidades u ON m.unidade_id = u.id\nLEFT JOIN tipos_saida ts ON m.tipo_saida_id = ts.id\nWHERE m.tipo = 'evasao'\nGROUP BY u.nome, ts.nome\nORDER BY u.nome, quantidade DESC;\n\n-- VIEW 5: Matrículas por canal de origem\nCREATE OR REPLACE VIEW vw_matriculas_por_canal AS\nSELECT \n    u.nome AS unidade,\n    TO_CHAR(m.data_movimentacao, 'YYYY-MM') AS ano_mes,\n    COALESCE(co.nome, 'Não informado') AS canal_origem,\n    COUNT(*) AS quantidade\nFROM movimentacoes m\nJOIN unidades u ON m.unidade_id = u.id\nLEFT JOIN canais_origem co ON m.canal_origem_id = co.id\nWHERE m.tipo = 'matricula'\nGROUP BY u.nome, ano_mes, co.nome\nORDER BY ano_mes DESC, u.nome, quantidade DESC;\n\n-- VIEW 6: Movimentações recentes (últimos 30 dias)\nCREATE OR REPLACE VIEW vw_movimentacoes_recentes AS\nSELECT \n    m.id,\n    a.nome AS aluno,\n    u.nome AS unidade,\n    c.nome AS curso,\n    m.tipo,\n    m.data_movimentacao,\n    ms.nome AS motivo_saida,\n    m.observacoes,\n    m.created_by\nFROM movimentacoes m\nJOIN unidades u ON m.unidade_id = u.id\nLEFT JOIN alunos a ON m.aluno_id = a.id\nLEFT JOIN cursos c ON m.curso_id = c.id\nLEFT JOIN motivos_saida ms ON m.motivo_saida_id = ms.id\nWHERE m.data_movimentacao >= CURRENT_DATE - INTERVAL '30 days'\nORDER BY m.data_movimentacao DESC, m.created_at DESC;\n\n-- VIEW 7: Evolução de alunos (saldo mensal)\nCREATE OR REPLACE VIEW vw_evolucao_alunos AS\nSELECT \n    u.nome AS unidade,\n    TO_CHAR(m.data_movimentacao, 'YYYY-MM') AS ano_mes,\n    SUM(CASE WHEN m.tipo = 'matricula' THEN 1 ELSE 0 END) AS entradas,\n    SUM(CASE WHEN m.tipo = 'evasao' THEN 1 ELSE 0 END) AS saidas,\n    SUM(CASE WHEN m.tipo = 'matricula' THEN 1 ELSE 0 END) - SUM(CASE WHEN m.tipo = 'evasao' THEN 1 ELSE 0 END) AS saldo\nFROM movimentacoes m\nJOIN unidades u ON m.unidade_id = u.id\nGROUP BY u.nome, ano_mes\nORDER BY ano_mes, u.nome;"}	fase3_criar_views_movimentacoes	producoes.emla@gmail.com	\N	\N
20260110004234	{"-- ============================================\n-- FUNÇÃO PARA REGISTRAR MOVIMENTAÇÃO (COM AJUSTES)\n-- ============================================\n\nCREATE OR REPLACE FUNCTION registrar_movimentacao(\n    p_aluno_id INTEGER,\n    p_unidade_id UUID,\n    p_tipo VARCHAR(50),\n    p_curso_id INTEGER DEFAULT NULL,\n    p_professor_id INTEGER DEFAULT NULL,\n    p_motivo_saida_id INTEGER DEFAULT NULL,\n    p_tipo_saida_id INTEGER DEFAULT NULL,\n    p_canal_origem_id INTEGER DEFAULT NULL,\n    p_valor_mensalidade NUMERIC DEFAULT NULL,\n    p_observacoes TEXT DEFAULT NULL,\n    p_created_by VARCHAR DEFAULT NULL\n) RETURNS INTEGER AS $$\nDECLARE\n    v_id INTEGER;\nBEGIN\n    -- Validar tipo de movimentação\n    IF p_tipo NOT IN ('matricula', 'renovacao', 'evasao', 'transferencia', 'troca_curso', 'troca_professor') THEN\n        RAISE EXCEPTION 'Tipo de movimentação inválido: %. Tipos válidos: matricula, renovacao, evasao, transferencia, troca_curso, troca_professor', p_tipo;\n    END IF;\n\n    -- Inserir movimentação\n    INSERT INTO movimentacoes (\n        aluno_id, unidade_id, tipo, curso_id, professor_id,\n        motivo_saida_id, tipo_saida_id, canal_origem_id,\n        valor_mensalidade, observacoes, created_by,\n        data_movimentacao, data_referencia\n    ) VALUES (\n        p_aluno_id, p_unidade_id, p_tipo, p_curso_id, p_professor_id,\n        p_motivo_saida_id, p_tipo_saida_id, p_canal_origem_id,\n        p_valor_mensalidade, p_observacoes, p_created_by,\n        CURRENT_DATE, DATE_TRUNC('month', CURRENT_DATE)\n    ) RETURNING id INTO v_id;\n    \n    -- Se for evasão, atualizar status do aluno (AJUSTE: inclui is_ex_aluno e data_saida)\n    IF p_tipo = 'evasao' AND p_aluno_id IS NOT NULL THEN\n        UPDATE alunos \n        SET \n            status = 'inativo', \n            is_ex_aluno = true,\n            data_saida = CURRENT_DATE,\n            motivo_saida_id = p_motivo_saida_id,\n            tipo_saida_id = p_tipo_saida_id,\n            updated_at = NOW() \n        WHERE id = p_aluno_id;\n    END IF;\n    \n    -- Se for matrícula, garantir que aluno está ativo\n    IF p_tipo = 'matricula' AND p_aluno_id IS NOT NULL THEN\n        UPDATE alunos \n        SET \n            status = 'ativo', \n            is_ex_aluno = false,\n            data_saida = NULL,\n            updated_at = NOW() \n        WHERE id = p_aluno_id;\n    END IF;\n    \n    RETURN v_id;\nEND;\n$$ LANGUAGE plpgsql;\n\nCOMMENT ON FUNCTION registrar_movimentacao IS 'Registra uma movimentação e atualiza automaticamente o status do aluno. Em evasão: marca como inativo, is_ex_aluno=true e preenche data_saida. Em matrícula: marca como ativo.';"}	fase3_criar_funcao_registrar_movimentacao	producoes.emla@gmail.com	\N	\N
20260116090613	{"-- ============================================\n-- FASE 4.1: BACKUP E PREPARAÇÃO\n-- Renomear tabela metas existente para preservar dados\n-- ============================================\n\n-- Renomear tabela metas para metas_legado\nALTER TABLE metas RENAME TO metas_legado;\n\n-- Comentário para documentação\nCOMMENT ON TABLE metas_legado IS 'Backup da tabela metas original (estrutura anual). Dados migrados para nova tabela metas em 2026-01-16.';"}	fase4_1_backup_renomear_metas	producoes.emla@gmail.com	\N	\N
20260116090713	{"-- ============================================\n-- FASE 4.2: CRIAR NOVA TABELA METAS\n-- Renomear índices antigos e criar nova tabela\n-- ============================================\n\n-- Renomear índices existentes para evitar conflito\nALTER INDEX idx_metas_unidade_ano RENAME TO idx_metas_legado_unidade_ano;\nALTER INDEX idx_metas_ano RENAME TO idx_metas_legado_ano;\n\n-- Criar nova tabela metas\nCREATE TABLE metas (\n    id SERIAL PRIMARY KEY,\n    \n    -- Escopo\n    unidade_id UUID REFERENCES unidades(id), -- NULL = meta da rede (consolidado)\n    ano INTEGER NOT NULL CHECK (ano >= 2020 AND ano <= 2035),\n    mes INTEGER CHECK (mes >= 1 AND mes <= 12), -- NULL = meta anual ou trimestral\n    trimestre INTEGER CHECK (trimestre >= 1 AND trimestre <= 4), -- NULL = meta mensal ou anual\n    \n    -- Tipo de período\n    tipo_periodo VARCHAR(20) NOT NULL DEFAULT 'mensal' \n        CHECK (tipo_periodo IN ('mensal', 'trimestral', 'anual')),\n    \n    -- METAS COMERCIAIS\n    meta_leads INTEGER,\n    meta_experimentais INTEGER,\n    meta_matriculas INTEGER,\n    meta_taxa_conversao_experimental NUMERIC(5,2),\n    meta_taxa_conversao_lead NUMERIC(5,2),\n    meta_faturamento_passaportes NUMERIC(12,2),\n    \n    -- METAS DE RETENÇÃO\n    meta_renovacoes INTEGER,\n    meta_taxa_renovacao NUMERIC(5,2),\n    meta_churn_maximo NUMERIC(5,2),\n    meta_evasoes_maximo INTEGER,\n    meta_ltv_meses NUMERIC(5,1),\n    \n    -- METAS FINANCEIRAS\n    meta_faturamento_parcelas NUMERIC(12,2),\n    meta_ticket_medio NUMERIC(10,2),\n    meta_inadimplencia_maxima NUMERIC(5,2),\n    \n    -- METAS DE ALUNOS\n    meta_alunos_ativos INTEGER,\n    meta_alunos_pagantes INTEGER,\n    \n    -- Status\n    ativo BOOLEAN DEFAULT TRUE,\n    \n    -- Auditoria\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    created_by INTEGER\n);\n\n-- Trigger para updated_at\nCREATE TRIGGER update_metas_updated_at\n    BEFORE UPDATE ON metas\n    FOR EACH ROW\n    EXECUTE FUNCTION update_updated_at_column();\n\n-- Índices para performance\nCREATE INDEX idx_metas_unidade ON metas(unidade_id);\nCREATE INDEX idx_metas_ano ON metas(ano);\nCREATE INDEX idx_metas_periodo ON metas(ano, mes, trimestre);\nCREATE INDEX idx_metas_tipo_periodo ON metas(tipo_periodo);\nCREATE UNIQUE INDEX idx_metas_unique ON metas(unidade_id, ano, COALESCE(mes, 0), COALESCE(trimestre, 0), tipo_periodo);\n\n-- Comentários\nCOMMENT ON TABLE metas IS 'Metas e OKRs unificados por unidade - mensais, trimestrais e anuais.';\nCOMMENT ON COLUMN metas.unidade_id IS 'NULL = meta consolidada da rede';\nCOMMENT ON COLUMN metas.mes IS 'NULL para metas trimestrais ou anuais';\nCOMMENT ON COLUMN metas.trimestre IS 'NULL para metas mensais ou anuais';"}	fase4_2_criar_nova_tabela_metas_v2	producoes.emla@gmail.com	\N	\N
20260116090747	{"-- ============================================\n-- FASE 4.3: MIGRAR DADOS PARA NOVA TABELA METAS\n-- Consolidar metas_legado + metas_comerciais\n-- ============================================\n\n-- 1. Migrar dados de metas_legado (6 registros) como metas ANUAIS\nINSERT INTO metas (\n    unidade_id, \n    ano, \n    tipo_periodo,\n    meta_alunos_ativos,\n    meta_matriculas,\n    meta_evasoes_maximo,\n    meta_churn_maximo,\n    meta_taxa_renovacao,\n    meta_ticket_medio,\n    meta_ltv_meses,\n    meta_inadimplencia_maxima,\n    meta_faturamento_parcelas,\n    created_at\n)\nSELECT \n    unidade_id,\n    ano,\n    'anual',\n    meta_alunos,\n    meta_matriculas_mes * 12, -- Converter mensal para anual\n    meta_evasoes_max,\n    meta_churn,\n    meta_renovacao,\n    meta_ticket,\n    meta_permanencia,\n    meta_inadimplencia,\n    meta_faturamento,\n    created_at\nFROM metas_legado;\n\n-- 2. Atualizar com dados de metas_comerciais (leads, experimentais, taxa conversão)\n-- Campo Grande\nUPDATE metas m\nSET \n    meta_leads = mc.meta_leads,\n    meta_experimentais = mc.meta_experimentais,\n    meta_taxa_conversao_lead = mc.meta_taxa_conversao\nFROM metas_comerciais mc\nWHERE mc.unidade = 'Campo Grande' \n  AND m.unidade_id = '2ec861f6-023f-4d7b-9927-3960ad8c2a92'\n  AND m.ano = mc.ano;\n\n-- Recreio\nUPDATE metas m\nSET \n    meta_leads = mc.meta_leads,\n    meta_experimentais = mc.meta_experimentais,\n    meta_taxa_conversao_lead = mc.meta_taxa_conversao\nFROM metas_comerciais mc\nWHERE mc.unidade = 'Recreio' \n  AND m.unidade_id = '95553e96-971b-4590-a6eb-0201d013c14d'\n  AND m.ano = mc.ano;\n\n-- Barra\nUPDATE metas m\nSET \n    meta_leads = mc.meta_leads,\n    meta_experimentais = mc.meta_experimentais,\n    meta_taxa_conversao_lead = mc.meta_taxa_conversao\nFROM metas_comerciais mc\nWHERE mc.unidade = 'Barra' \n  AND m.unidade_id = '368d47f5-2d88-4475-bc14-ba084a9a348e'\n  AND m.ano = mc.ano;\n\n-- 3. Criar registro consolidado da rede (unidade_id = NULL) para 2026\nINSERT INTO metas (\n    unidade_id,\n    ano,\n    tipo_periodo,\n    meta_leads,\n    meta_experimentais,\n    meta_matriculas,\n    meta_alunos_ativos,\n    meta_evasoes_maximo,\n    meta_churn_maximo,\n    meta_taxa_renovacao,\n    meta_ticket_medio,\n    meta_ltv_meses,\n    meta_inadimplencia_maxima\n)\nSELECT \n    NULL,\n    2026,\n    'anual',\n    SUM(meta_leads),\n    SUM(meta_experimentais),\n    SUM(meta_matriculas),\n    SUM(meta_alunos_ativos),\n    SUM(meta_evasoes_maximo),\n    ROUND(AVG(meta_churn_maximo), 2),\n    ROUND(AVG(meta_taxa_renovacao), 2),\n    ROUND(AVG(meta_ticket_medio), 2),\n    ROUND(AVG(meta_ltv_meses), 1),\n    ROUND(AVG(meta_inadimplencia_maxima), 2)\nFROM metas\nWHERE ano = 2026 AND unidade_id IS NOT NULL;"}	fase4_3_migrar_dados_metas	producoes.emla@gmail.com	\N	\N
