20260116090817	{"-- ============================================\n-- FASE 4.4: ADICIONAR CAMPOS NA TABELA ALUNOS\n-- Campos para aula experimental, renovação e NPS\n-- ============================================\n\n-- Adicionar campos faltantes na tabela alunos\nALTER TABLE alunos \nADD COLUMN IF NOT EXISTS professor_experimental_id INTEGER REFERENCES professores(id),\nADD COLUMN IF NOT EXISTS agente_comercial VARCHAR(100),\nADD COLUMN IF NOT EXISTS is_aluno_retorno BOOLEAN DEFAULT FALSE,\nADD COLUMN IF NOT EXISTS data_ultima_renovacao DATE,\nADD COLUMN IF NOT EXISTS numero_renovacoes INTEGER DEFAULT 0,\nADD COLUMN IF NOT EXISTS nps_saida INTEGER CHECK (nps_saida >= 0 AND nps_saida <= 10);\n\n-- Comentários\nCOMMENT ON COLUMN alunos.professor_experimental_id IS 'Professor que deu a aula experimental (pode ser diferente do professor atual)';\nCOMMENT ON COLUMN alunos.agente_comercial IS 'Nome do agente comercial que fechou a matrícula';\nCOMMENT ON COLUMN alunos.is_aluno_retorno IS 'Se é um ex-aluno que voltou';\nCOMMENT ON COLUMN alunos.data_ultima_renovacao IS 'Data da última renovação de contrato';\nCOMMENT ON COLUMN alunos.numero_renovacoes IS 'Quantas vezes o aluno renovou o contrato';\nCOMMENT ON COLUMN alunos.nps_saida IS 'Nota NPS coletada na saída do aluno (0-10)';"}	fase4_4_adicionar_campos_alunos	producoes.emla@gmail.com	\N	\N
20260116090841	{"-- ============================================\n-- FASE 4.5A: CRIAR TABELA USUARIOS\n-- Controle de acesso por unidade\n-- ============================================\n\nCREATE TABLE IF NOT EXISTS usuarios (\n    id SERIAL PRIMARY KEY,\n    \n    -- Dados básicos\n    nome VARCHAR(255) NOT NULL,\n    email VARCHAR(255) UNIQUE NOT NULL,\n    senha_hash VARCHAR(255),\n    \n    -- Perfil e acesso\n    perfil VARCHAR(50) NOT NULL DEFAULT 'unidade' CHECK (perfil IN ('admin', 'unidade')),\n    unidade_id UUID REFERENCES unidades(id),\n    cargo VARCHAR(100),\n    \n    -- Status\n    ativo BOOLEAN DEFAULT TRUE,\n    ultimo_acesso TIMESTAMP WITH TIME ZONE,\n    \n    -- Auditoria\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()\n);\n\n-- Trigger para updated_at\nCREATE TRIGGER update_usuarios_updated_at\n    BEFORE UPDATE ON usuarios\n    FOR EACH ROW\n    EXECUTE FUNCTION update_updated_at_column();\n\n-- Índices\nCREATE INDEX idx_usuarios_email ON usuarios(email);\nCREATE INDEX idx_usuarios_unidade ON usuarios(unidade_id);\nCREATE INDEX idx_usuarios_perfil ON usuarios(perfil);\n\n-- Comentários\nCOMMENT ON TABLE usuarios IS 'Usuários do sistema com controle de acesso por unidade';\nCOMMENT ON COLUMN usuarios.perfil IS 'admin = vê tudo (consolidado + todas unidades), unidade = só a própria unidade';\nCOMMENT ON COLUMN usuarios.unidade_id IS 'NULL para admin, preenchido para perfil unidade';\n\n-- Inserir usuário admin inicial\nINSERT INTO usuarios (nome, email, perfil, cargo) VALUES \n('Administrador', 'admin@lamusic.com.br', 'admin', 'Administrador');"}	fase4_5a_criar_tabela_usuarios	producoes.emla@gmail.com	\N	\N
20260116090857	{"-- ============================================\n-- FASE 4.5B: CRIAR TABELA LEADS\n-- Funil comercial completo\n-- ============================================\n\nCREATE TABLE IF NOT EXISTS leads (\n    id SERIAL PRIMARY KEY,\n    \n    -- Dados do lead\n    nome VARCHAR(255),\n    telefone VARCHAR(20),\n    whatsapp VARCHAR(20),\n    email VARCHAR(255),\n    idade INTEGER,\n    \n    -- Relacionamentos\n    unidade_id UUID REFERENCES unidades(id) NOT NULL,\n    curso_interesse_id INTEGER REFERENCES cursos(id),\n    canal_origem_id INTEGER REFERENCES canais_origem(id),\n    \n    -- Datas\n    data_contato DATE NOT NULL DEFAULT CURRENT_DATE,\n    data_primeiro_contato TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    data_ultimo_contato TIMESTAMP WITH TIME ZONE,\n    \n    -- Status do lead\n    status VARCHAR(50) NOT NULL DEFAULT 'novo' \n        CHECK (status IN ('novo', 'em_contato', 'agendado', 'realizado', 'convertido', 'arquivado', 'perdido')),\n    motivo_arquivamento VARCHAR(255),\n    \n    -- Experimental\n    experimental_agendada BOOLEAN DEFAULT FALSE,\n    data_experimental DATE,\n    horario_experimental TIME,\n    professor_experimental_id INTEGER REFERENCES professores(id),\n    experimental_realizada BOOLEAN DEFAULT FALSE,\n    faltou_experimental BOOLEAN DEFAULT FALSE,\n    \n    -- Conversão\n    converteu BOOLEAN DEFAULT FALSE,\n    data_conversao DATE,\n    aluno_id INTEGER REFERENCES alunos(id),\n    motivo_nao_matricula TEXT,\n    \n    -- Responsável\n    agente_comercial VARCHAR(100),\n    \n    -- Observações\n    observacoes TEXT,\n    \n    -- Auditoria\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    created_by INTEGER REFERENCES usuarios(id)\n);\n\n-- Trigger para updated_at\nCREATE TRIGGER update_leads_updated_at\n    BEFORE UPDATE ON leads\n    FOR EACH ROW\n    EXECUTE FUNCTION update_updated_at_column();\n\n-- Índices\nCREATE INDEX idx_leads_unidade ON leads(unidade_id);\nCREATE INDEX idx_leads_status ON leads(status);\nCREATE INDEX idx_leads_data_contato ON leads(data_contato);\nCREATE INDEX idx_leads_canal ON leads(canal_origem_id);\nCREATE INDEX idx_leads_curso ON leads(curso_interesse_id);\nCREATE INDEX idx_leads_converteu ON leads(converteu);\nCREATE INDEX idx_leads_mes ON leads(EXTRACT(YEAR FROM data_contato), EXTRACT(MONTH FROM data_contato));\n\n-- Comentários\nCOMMENT ON TABLE leads IS 'Leads comerciais - do primeiro contato até a conversão ou arquivamento';\nCOMMENT ON COLUMN leads.status IS 'Funil: novo → em_contato → agendado → realizado → convertido/arquivado/perdido';"}	fase4_5b_criar_tabela_leads	producoes.emla@gmail.com	\N	\N
20260116090915	{"-- ============================================\n-- FASE 4.5C: CRIAR TABELA RENOVACOES\n-- Histórico detalhado de renovações\n-- ============================================\n\nCREATE TABLE IF NOT EXISTS renovacoes (\n    id SERIAL PRIMARY KEY,\n    \n    -- Relacionamentos\n    aluno_id INTEGER REFERENCES alunos(id) NOT NULL,\n    unidade_id UUID REFERENCES unidades(id) NOT NULL,\n    \n    -- Datas\n    data_renovacao DATE NOT NULL DEFAULT CURRENT_DATE,\n    data_fim_contrato_anterior DATE,\n    data_inicio_novo_contrato DATE,\n    data_fim_novo_contrato DATE,\n    \n    -- Valores\n    valor_parcela_anterior NUMERIC(10,2),\n    valor_parcela_novo NUMERIC(10,2),\n    percentual_reajuste NUMERIC(5,2),\n    \n    -- Status\n    status VARCHAR(50) NOT NULL DEFAULT 'renovado' \n        CHECK (status IN ('renovado', 'nao_renovou', 'pendente', 'negociando')),\n    \n    -- Se não renovou\n    motivo_nao_renovacao_id INTEGER REFERENCES motivos_saida(id),\n    \n    -- Responsável\n    agente VARCHAR(100),\n    \n    -- Observações\n    observacoes TEXT,\n    \n    -- Auditoria\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    created_by INTEGER REFERENCES usuarios(id)\n);\n\n-- Trigger para updated_at\nCREATE TRIGGER update_renovacoes_updated_at\n    BEFORE UPDATE ON renovacoes\n    FOR EACH ROW\n    EXECUTE FUNCTION update_updated_at_column();\n\n-- Trigger para calcular percentual de reajuste automaticamente\nCREATE OR REPLACE FUNCTION calcular_reajuste_renovacao()\nRETURNS TRIGGER AS $$\nBEGIN\n    IF NEW.valor_parcela_anterior IS NOT NULL AND NEW.valor_parcela_anterior > 0 THEN\n        NEW.percentual_reajuste := ROUND(\n            ((NEW.valor_parcela_novo - NEW.valor_parcela_anterior) / NEW.valor_parcela_anterior) * 100, \n            2\n        );\n    ELSE\n        NEW.percentual_reajuste := 0;\n    END IF;\n    RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql;\n\nCREATE TRIGGER trigger_calcular_reajuste\n    BEFORE INSERT OR UPDATE ON renovacoes\n    FOR EACH ROW\n    EXECUTE FUNCTION calcular_reajuste_renovacao();\n\n-- Índices\nCREATE INDEX idx_renovacoes_aluno ON renovacoes(aluno_id);\nCREATE INDEX idx_renovacoes_unidade ON renovacoes(unidade_id);\nCREATE INDEX idx_renovacoes_data ON renovacoes(data_renovacao);\nCREATE INDEX idx_renovacoes_status ON renovacoes(status);\nCREATE INDEX idx_renovacoes_mes ON renovacoes(EXTRACT(YEAR FROM data_renovacao), EXTRACT(MONTH FROM data_renovacao));\n\n-- Comentário\nCOMMENT ON TABLE renovacoes IS 'Histórico de renovações de contrato com valores e reajustes';"}	fase4_5c_criar_tabela_renovacoes	producoes.emla@gmail.com	\N	\N
20260116090934	{"-- ============================================\n-- FASE 4.5D: CRIAR TABELA RELATORIOS_DIARIOS\n-- Snapshot diário para histórico\n-- ============================================\n\nCREATE TABLE IF NOT EXISTS relatorios_diarios (\n    id SERIAL PRIMARY KEY,\n    \n    -- Identificação\n    unidade_id UUID REFERENCES unidades(id) NOT NULL,\n    data_referencia DATE NOT NULL DEFAULT CURRENT_DATE,\n    \n    -- NÚMEROS GERAIS\n    total_alunos_ativos INTEGER,\n    total_alunos_pagantes INTEGER,\n    total_bolsistas_integral INTEGER,\n    total_bolsistas_parcial INTEGER,\n    total_matriculas_ativas INTEGER,\n    total_matriculas_banda INTEGER,\n    total_matriculas_segundo_curso INTEGER,\n    \n    -- COMERCIAL DO DIA\n    leads_novos_dia INTEGER DEFAULT 0,\n    leads_acumulado_mes INTEGER,\n    experimentais_agendadas_dia INTEGER DEFAULT 0,\n    experimentais_realizadas_dia INTEGER DEFAULT 0,\n    faltaram_experimental_dia INTEGER DEFAULT 0,\n    matriculas_dia INTEGER DEFAULT 0,\n    matriculas_acumulado_mes INTEGER,\n    visitas_escola_dia INTEGER DEFAULT 0,\n    \n    -- RETENÇÃO DO DIA\n    renovacoes_dia INTEGER DEFAULT 0,\n    renovacoes_acumulado_mes INTEGER,\n    nao_renovacoes_dia INTEGER DEFAULT 0,\n    nao_renovacoes_acumulado_mes INTEGER,\n    evasoes_dia INTEGER DEFAULT 0,\n    evasoes_acumulado_mes INTEGER,\n    avisos_previos_mes INTEGER,\n    \n    -- FINANCEIRO\n    ticket_medio_atual NUMERIC(10,2),\n    faturamento_previsto_mes NUMERIC(12,2),\n    faturamento_realizado_mes NUMERIC(12,2),\n    inadimplencia_valor NUMERIC(12,2),\n    inadimplencia_percentual NUMERIC(5,2),\n    \n    -- MÉTRICAS CALCULADAS\n    churn_rate_mes NUMERIC(5,2),\n    taxa_renovacao_mes NUMERIC(5,2),\n    taxa_conversao_experimental_mes NUMERIC(5,2),\n    ltv_atual NUMERIC(5,1),\n    \n    -- Observações\n    observacoes TEXT,\n    \n    -- Auditoria\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    created_by INTEGER REFERENCES usuarios(id)\n);\n\n-- Índices\nCREATE INDEX idx_relatorios_unidade ON relatorios_diarios(unidade_id);\nCREATE INDEX idx_relatorios_data ON relatorios_diarios(data_referencia);\nCREATE UNIQUE INDEX idx_relatorios_unique ON relatorios_diarios(unidade_id, data_referencia);\n\n-- Comentário\nCOMMENT ON TABLE relatorios_diarios IS 'Snapshot diário dos números de cada unidade - histórico para análise';"}	fase4_5d_criar_tabela_relatorios_diarios	producoes.emla@gmail.com	\N	\N
20260116121142	{"-- Corrigir política de SELECT da tabela usuarios para evitar loop infinito\n-- Permite que usuário veja seu próprio registro OU que admin veja todos\nDROP POLICY IF EXISTS \\"usuarios_select_policy\\" ON usuarios;\nCREATE POLICY \\"usuarios_select_policy\\" ON usuarios\n  FOR SELECT USING (\n    -- Usuário pode ver seu próprio registro\n    auth_user_id = auth.uid() \n    OR\n    -- Admin pode ver todos os registros (usando subquery para evitar recursão)\n    EXISTS (\n      SELECT 1 FROM usuarios u2\n      WHERE u2.auth_user_id = auth.uid()\n      AND u2.perfil = 'admin'\n      AND u2.ativo = true\n    )\n  );"}	fase5_5b_corrigir_politica_usuarios_final	producoes.emla@gmail.com	\N	\N
20260126164231	{"-- Atualizar função get_dados_relatorio_coordenacao com TODOS os dados do planejamento\nCREATE OR REPLACE FUNCTION get_dados_relatorio_coordenacao(\n  p_unidade_id UUID,\n  p_ano INTEGER,\n  p_mes INTEGER\n)\nRETURNS JSONB\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  v_result JSONB := '{}'::jsonb;\n  v_unidade_nome TEXT;\n  v_mes_anterior INTEGER;\n  v_ano_mes_anterior INTEGER;\nBEGIN\n  -- Calcular mês anterior\n  IF p_mes = 1 THEN\n    v_mes_anterior := 12;\n    v_ano_mes_anterior := p_ano - 1;\n  ELSE\n    v_mes_anterior := p_mes - 1;\n    v_ano_mes_anterior := p_ano;\n  END IF;\n\n  -- Buscar nome da unidade\n  IF p_unidade_id IS NOT NULL THEN\n    SELECT nome INTO v_unidade_nome FROM unidades WHERE id = p_unidade_id;\n  ELSE\n    v_unidade_nome := 'Consolidado';\n  END IF;\n\n  -- Período\n  v_result := v_result || jsonb_build_object('periodo', jsonb_build_object(\n    'unidade_id', p_unidade_id,\n    'unidade_nome', v_unidade_nome,\n    'ano', p_ano,\n    'mes', p_mes,\n    'coordenadores', ARRAY['Quintela', 'Juliana']\n  ));\n\n  -- KPIs consolidados dos professores COM HEALTH SCORE\n  v_result := v_result || jsonb_build_object('kpis_professores', (\n    SELECT COALESCE(jsonb_agg(row_to_json(k)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_id,\n        v.professor_nome,\n        v.carteira_alunos,\n        v.media_alunos_turma,\n        v.media_presenca,\n        v.nps_medio,\n        v.taxa_conversao,\n        v.taxa_renovacao,\n        v.evasoes,\n        v.matriculas,\n        v.mrr_carteira,\n        v.ranking_matriculador,\n        v.ranking_renovador,\n        v.ranking_churn,\n        -- Cursos do professor\n        (SELECT COALESCE(array_agg(DISTINCT c.nome), ARRAY[]::text[])\n         FROM professores_cursos pc\n         JOIN cursos c ON c.id = pc.curso_id\n         WHERE pc.professor_id = v.professor_id) as cursos,\n        -- HEALTH SCORE (será calculado no frontend, mas já preparamos os dados)\n        0 as health_score,\n        'critico' as health_status\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n      ORDER BY v.carteira_alunos DESC\n    ) k\n  ));\n\n  -- Totais consolidados COM TOTAL DE TURMAS\n  v_result := v_result || jsonb_build_object('totais', (\n    SELECT row_to_json(t)\n    FROM (\n      SELECT \n        COUNT(DISTINCT v.professor_id) as total_professores,\n        SUM(v.carteira_alunos) as total_alunos,\n        ROUND(AVG(v.carteira_alunos), 1) as media_alunos_professor,\n        ROUND(AVG(NULLIF(v.media_alunos_turma, 0)), 2) as media_alunos_turma,\n        -- TOTAL DE TURMAS\n        (SELECT COUNT(*) FROM turmas tu \n         WHERE tu.ativo = true \n           AND (p_unidade_id IS NULL OR tu.unidade_id = p_unidade_id)) as total_turmas,\n        ROUND(AVG(v.media_presenca), 1) as media_presenca,\n        ROUND(AVG(NULLIF(v.nps_medio, 0)), 1) as nps_medio,\n        ROUND(AVG(v.taxa_conversao), 1) as taxa_conversao_media,\n        ROUND(AVG(v.taxa_renovacao), 1) as taxa_renovacao_media,\n        SUM(v.evasoes) as total_evasoes,\n        SUM(v.matriculas) as total_matriculas,\n        SUM(v.mrr_carteira) as mrr_total,\n        -- RENOVAÇÕES REALIZADAS\n        SUM(v.renovacoes) as renovacoes_realizadas,\n        -- CHURN POR PROFESSOR (média)\n        ROUND(AVG(NULLIF(v.taxa_cancelamento, 0)), 1) as churn_medio_professor,\n        -- HEALTH SCORE MÉDIO (será calculado no frontend)\n        0 as health_score_medio\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n    ) t\n  ));\n\n  -- Top 5 professores por carteira\n  v_result := v_result || jsonb_build_object('top_carteira', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_nome as professor,\n        v.carteira_alunos as alunos\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n      ORDER BY v.carteira_alunos DESC\n      LIMIT 5\n    ) t\n  ));\n\n  -- TOP 3 HEALTH SCORE (será preenchido no frontend com cálculo)\n  v_result := v_result || jsonb_build_object('top_health_score', '[]'::jsonb);\n\n  -- Top 5 professores por média de alunos/turma\n  v_result := v_result || jsonb_build_object('top_media_turma', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_nome as professor,\n        v.media_alunos_turma as media,\n        (SELECT COUNT(*) FROM turmas tu WHERE tu.professor_id = v.professor_id AND tu.ativo = true) as turmas\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n        AND v.media_alunos_turma > 0\n      ORDER BY v.media_alunos_turma DESC\n      LIMIT 5\n    ) t\n  ));\n\n  -- Top 5 professores por presença\n  v_result := v_result || jsonb_build_object('top_presenca', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_nome as professor,\n        v.media_presenca as presenca\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n      ORDER BY v.media_presenca DESC\n      LIMIT 5\n    ) t\n  ));\n\n  -- Top 5 professores matriculadores\n  v_result := v_result || jsonb_build_object('top_matriculadores', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_nome as professor,\n        v.matriculas\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n        AND v.matriculas > 0\n      ORDER BY v.matriculas DESC\n      LIMIT 5\n    ) t\n  ));\n\n  -- Top 5 professores por retenção (tempo médio de permanência dos alunos)\n  v_result := v_result || jsonb_build_object('top_retencao', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        p.nome as professor,\n        ROUND(AVG(a.tempo_permanencia_meses), 1) as tempo_medio\n      FROM alunos a\n      JOIN professores p ON a.professor_atual_id = p.id\n      WHERE a.status = 'ativo'\n        AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n      GROUP BY p.id, p.nome\n      HAVING COUNT(*) >= 3\n      ORDER BY AVG(a.tempo_permanencia_meses) DESC\n      LIMIT 5\n    ) t\n  ));\n\n  -- Professores em alerta (baixa presença ou evasões)\n  v_result := v_result || jsonb_build_object('professores_alerta', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_id,\n        v.professor_nome as professor,\n        v.media_presenca as presenca,\n        v.evasoes,\n        v.carteira_alunos as alunos,\n        0 as health_score,\n        CASE \n          WHEN v.media_presenca < 70 OR v.evasoes > 2 THEN 'critico'\n          WHEN v.media_presenca < 80 OR v.evasoes > 0 THEN 'atencao'\n          ELSE 'ok'\n        END as status\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n        AND (v.media_presenca < 80 OR v.evasoes > 0)\n      ORDER BY v.media_presenca ASC\n    ) t\n  ));\n\n  -- Agendamentos/Treinamentos do mês\n  v_result := v_result || jsonb_build_object('agenda', (\n    SELECT row_to_json(a)\n    FROM (\n      SELECT \n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.tipo = 'treinamento' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as treinamentos_agendados,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.tipo = 'reuniao' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as reunioes_agendadas,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.tipo = 'checkpoint' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as checkpoints_agendados,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.status = 'concluido' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as concluidos,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.status = 'pendente' \n           AND pa.data_agendada < CURRENT_DATE\n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)) as atrasados\n    ) a\n  ));\n\n  -- PROFESSORES EM TREINAMENTO ATIVO\n  v_result := v_result || jsonb_build_object('professores_em_treinamento', (\n    SELECT COALESCE(jsonb_agg(row_to_json(pt)), '[]'::jsonb)\n    FROM (\n      SELECT \n        p.nome as professor,\n        pa.titulo as treinamento,\n        pa.data_agendada,\n        pa.status\n      FROM professor_acoes pa\n      JOIN professores p ON pa.professor_id = p.id\n      WHERE pa.tipo = 'treinamento'\n        AND pa.status IN ('pendente', 'em_andamento')\n        AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n      ORDER BY pa.data_agendada\n      LIMIT 10\n    ) pt\n  ));\n\n  -- Catálogo de treinamentos disponíveis\n  v_result := v_result || jsonb_build_object('catalogo_treinamentos', (\n    SELECT COALESCE(jsonb_agg(row_to_json(ct)), '[]'::jsonb)\n    FROM (\n      SELECT id, nome, descricao\n      FROM catalogo_treinamentos\n      WHERE ativo = true\n      ORDER BY nome\n    ) ct\n  ));\n\n  -- Metas de professores (SEMPRE RETORNA, MESMO VAZIO)\n  v_result := v_result || jsonb_build_object('metas_professores', COALESCE(\n    (SELECT jsonb_object_agg(mk.tipo, mk.valor)\n     FROM metas_kpi mk\n     WHERE mk.ano = p_ano AND mk.mes = p_mes\n       AND (p_unidade_id IS NULL OR mk.unidade_id = p_unidade_id)\n       AND mk.tipo IN ('media_alunos_turma', 'media_alunos_professor', 'taxa_renovacao_prof', \n                       'nps_medio', 'presenca_media', 'taxa_conversao_exp', 'melhor_retencao')),\n    '{}'::jsonb\n  ));\n\n  -- Comparativo com mês anterior (COM HEALTH SCORE)\n  v_result := v_result || jsonb_build_object('mes_anterior', (\n    SELECT row_to_json(t)\n    FROM (\n      SELECT \n        COUNT(DISTINCT v.professor_id) as total_professores,\n        SUM(v.carteira_alunos) as total_alunos,\n        ROUND(AVG(v.media_presenca), 1) as media_presenca,\n        SUM(v.evasoes) as total_evasoes,\n        SUM(v.matriculas) as total_matriculas,\n        0 as health_score_medio,\n        0 as professores_criticos\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = v_ano_mes_anterior AND v.mes = v_mes_anterior\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n    ) t\n  ));\n\n  -- Comparativo com mesmo mês ano passado (COM HEALTH SCORE)\n  v_result := v_result || jsonb_build_object('ano_anterior', (\n    SELECT row_to_json(t)\n    FROM (\n      SELECT \n        COUNT(DISTINCT v.professor_id) as total_professores,\n        SUM(v.carteira_alunos) as total_alunos,\n        ROUND(AVG(v.media_presenca), 1) as media_presenca,\n        SUM(v.evasoes) as total_evasoes,\n        SUM(v.matriculas) as total_matriculas,\n        ROUND(AVG(v.carteira_alunos), 1) as media_alunos_professor\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano - 1 AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n    ) t\n  ));\n\n  RETURN v_result;\nEND;\n$$;"}	update_get_dados_relatorio_coordenacao_completo	producoes.emla@gmail.com	\N	\N
20260116091006	{"-- ============================================\n-- FASE 4.6: VIEWS DE KPIs COMERCIAIS\n-- ============================================\n\n-- VIEW 1: Funil de conversão por mês\nCREATE OR REPLACE VIEW vw_funil_conversao_mensal AS\nSELECT \n    u.nome AS unidade,\n    EXTRACT(YEAR FROM l.data_contato)::INTEGER AS ano,\n    EXTRACT(MONTH FROM l.data_contato)::INTEGER AS mes,\n    TO_CHAR(l.data_contato, 'YYYY-MM') AS ano_mes,\n    \n    -- Leads\n    COUNT(*) AS total_leads,\n    COUNT(*) FILTER (WHERE l.status = 'arquivado') AS leads_arquivados,\n    \n    -- Experimentais\n    COUNT(*) FILTER (WHERE l.experimental_agendada = TRUE) AS experimentais_agendadas,\n    COUNT(*) FILTER (WHERE l.experimental_realizada = TRUE) AS experimentais_realizadas,\n    COUNT(*) FILTER (WHERE l.faltou_experimental = TRUE) AS faltaram,\n    \n    -- Conversão\n    COUNT(*) FILTER (WHERE l.converteu = TRUE) AS matriculas,\n    \n    -- Taxas\n    ROUND(100.0 * COUNT(*) FILTER (WHERE l.experimental_agendada = TRUE) / NULLIF(COUNT(*), 0), 1) AS taxa_lead_experimental,\n    ROUND(100.0 * COUNT(*) FILTER (WHERE l.converteu = TRUE) / NULLIF(COUNT(*) FILTER (WHERE l.experimental_realizada = TRUE), 0), 1) AS taxa_experimental_matricula,\n    ROUND(100.0 * COUNT(*) FILTER (WHERE l.converteu = TRUE) / NULLIF(COUNT(*), 0), 1) AS taxa_lead_matricula\n\nFROM leads l\nJOIN unidades u ON l.unidade_id = u.id\nGROUP BY u.nome, ano, mes, ano_mes\nORDER BY ano_mes DESC, u.nome;\n\n-- VIEW 2: Leads por canal de origem\nCREATE OR REPLACE VIEW vw_leads_por_canal AS\nSELECT \n    u.nome AS unidade,\n    TO_CHAR(l.data_contato, 'YYYY-MM') AS ano_mes,\n    COALESCE(co.nome, 'Não informado') AS canal,\n    COUNT(*) AS total_leads,\n    COUNT(*) FILTER (WHERE l.converteu = TRUE) AS matriculas,\n    ROUND(100.0 * COUNT(*) FILTER (WHERE l.converteu = TRUE) / NULLIF(COUNT(*), 0), 1) AS taxa_conversao\nFROM leads l\nJOIN unidades u ON l.unidade_id = u.id\nLEFT JOIN canais_origem co ON l.canal_origem_id = co.id\nGROUP BY u.nome, ano_mes, co.nome\nORDER BY ano_mes DESC, u.nome, total_leads DESC;\n\n-- VIEW 3: Performance por professor (experimental)\nCREATE OR REPLACE VIEW vw_performance_professor_experimental AS\nSELECT \n    u.nome AS unidade,\n    TO_CHAR(l.data_contato, 'YYYY-MM') AS ano_mes,\n    p.nome AS professor,\n    COUNT(*) FILTER (WHERE l.experimental_realizada = TRUE) AS experimentais_realizadas,\n    COUNT(*) FILTER (WHERE l.converteu = TRUE) AS matriculas,\n    ROUND(100.0 * COUNT(*) FILTER (WHERE l.converteu = TRUE) / NULLIF(COUNT(*) FILTER (WHERE l.experimental_realizada = TRUE), 0), 1) AS taxa_conversao\nFROM leads l\nJOIN unidades u ON l.unidade_id = u.id\nLEFT JOIN professores p ON l.professor_experimental_id = p.id\nWHERE l.professor_experimental_id IS NOT NULL\nGROUP BY u.nome, ano_mes, p.nome\nORDER BY ano_mes DESC, taxa_conversao DESC;\n\n-- VIEW 4: Motivos de não matrícula\nCREATE OR REPLACE VIEW vw_motivos_nao_matricula AS\nSELECT \n    u.nome AS unidade,\n    TO_CHAR(l.data_contato, 'YYYY-MM') AS ano_mes,\n    l.motivo_nao_matricula,\n    COUNT(*) AS quantidade\nFROM leads l\nJOIN unidades u ON l.unidade_id = u.id\nWHERE l.converteu = FALSE \n  AND l.experimental_realizada = TRUE\n  AND l.motivo_nao_matricula IS NOT NULL\nGROUP BY u.nome, ano_mes, l.motivo_nao_matricula\nORDER BY ano_mes DESC, quantidade DESC;"}	fase4_6_criar_views_kpis_comerciais	producoes.emla@gmail.com	\N	\N
20260116091047	{"-- ============================================\n-- FASE 4.7: VIEWS DE METAS VS REALIZADO\n-- ============================================\n\n-- VIEW 1: Acompanhamento de metas mensais\nCREATE OR REPLACE VIEW vw_metas_vs_realizado AS\nWITH realizado AS (\n    SELECT \n        unidade_id,\n        EXTRACT(YEAR FROM data_referencia)::INTEGER AS ano,\n        EXTRACT(MONTH FROM data_referencia)::INTEGER AS mes,\n        MAX(matriculas_acumulado_mes) AS matriculas_realizadas,\n        MAX(renovacoes_acumulado_mes) AS renovacoes_realizadas,\n        MAX(evasoes_acumulado_mes) AS evasoes_realizadas,\n        MAX(leads_acumulado_mes) AS leads_realizados,\n        MAX(faturamento_realizado_mes) AS faturamento_realizado,\n        MAX(total_alunos_ativos) AS alunos_ativos,\n        MAX(total_alunos_pagantes) AS alunos_pagantes,\n        MAX(ticket_medio_atual) AS ticket_medio,\n        MAX(churn_rate_mes) AS churn_rate,\n        MAX(taxa_renovacao_mes) AS taxa_renovacao\n    FROM relatorios_diarios\n    GROUP BY unidade_id, ano, mes\n)\nSELECT \n    u.nome AS unidade,\n    m.ano,\n    m.mes,\n    TO_CHAR(MAKE_DATE(m.ano, m.mes, 1), 'YYYY-MM') AS ano_mes,\n    \n    -- Matrículas\n    m.meta_matriculas,\n    r.matriculas_realizadas,\n    ROUND(100.0 * r.matriculas_realizadas / NULLIF(m.meta_matriculas, 0), 1) AS pct_matriculas,\n    \n    -- Renovações\n    m.meta_renovacoes,\n    r.renovacoes_realizadas,\n    ROUND(100.0 * r.renovacoes_realizadas / NULLIF(m.meta_renovacoes, 0), 1) AS pct_renovacoes,\n    \n    -- Churn\n    m.meta_churn_maximo,\n    r.churn_rate AS churn_realizado,\n    CASE WHEN r.churn_rate <= m.meta_churn_maximo THEN 'OK' ELSE 'ALERTA' END AS status_churn,\n    \n    -- Faturamento\n    m.meta_faturamento_parcelas,\n    r.faturamento_realizado,\n    ROUND(100.0 * r.faturamento_realizado / NULLIF(m.meta_faturamento_parcelas, 0), 1) AS pct_faturamento,\n    \n    -- Alunos\n    m.meta_alunos_ativos,\n    r.alunos_ativos,\n    ROUND(100.0 * r.alunos_ativos / NULLIF(m.meta_alunos_ativos, 0), 1) AS pct_alunos\n\nFROM metas m\nJOIN unidades u ON m.unidade_id = u.id\nLEFT JOIN realizado r ON m.unidade_id = r.unidade_id \n    AND m.ano = r.ano \n    AND m.mes = r.mes\nWHERE m.tipo_periodo = 'mensal'\nORDER BY m.ano DESC, m.mes DESC, u.nome;\n\n-- VIEW 2: Projeção de atingimento de meta\nCREATE OR REPLACE VIEW vw_projecao_metas AS\nWITH dados_mes AS (\n    SELECT \n        unidade_id,\n        EXTRACT(YEAR FROM data_referencia)::INTEGER AS ano,\n        EXTRACT(MONTH FROM data_referencia)::INTEGER AS mes,\n        MAX(data_referencia) AS ultima_data,\n        EXTRACT(DAY FROM MAX(data_referencia))::INTEGER AS dias_passados,\n        EXTRACT(DAY FROM (DATE_TRUNC('month', MAX(data_referencia)) + INTERVAL '1 month - 1 day'))::INTEGER AS dias_no_mes,\n        MAX(matriculas_acumulado_mes) AS matriculas_ate_agora,\n        MAX(evasoes_acumulado_mes) AS evasoes_ate_agora,\n        MAX(faturamento_realizado_mes) AS faturamento_ate_agora\n    FROM relatorios_diarios\n    GROUP BY unidade_id, ano, mes\n)\nSELECT \n    u.nome AS unidade,\n    d.ano,\n    d.mes,\n    d.dias_passados,\n    d.dias_no_mes,\n    \n    -- Projeção de matrículas\n    m.meta_matriculas,\n    d.matriculas_ate_agora,\n    ROUND(d.matriculas_ate_agora::NUMERIC / NULLIF(d.dias_passados, 0) * d.dias_no_mes) AS matriculas_projetadas,\n    CASE \n        WHEN d.dias_passados = 0 THEN '⚪ Sem dados'\n        WHEN ROUND(d.matriculas_ate_agora::NUMERIC / d.dias_passados * d.dias_no_mes) >= m.meta_matriculas \n        THEN '🟢 No caminho'\n        WHEN ROUND(d.matriculas_ate_agora::NUMERIC / d.dias_passados * d.dias_no_mes) >= m.meta_matriculas * 0.8 \n        THEN '🟡 Atenção'\n        ELSE '🔴 Crítico'\n    END AS status_matriculas,\n    \n    -- Projeção de faturamento\n    m.meta_faturamento_parcelas,\n    d.faturamento_ate_agora,\n    ROUND(d.faturamento_ate_agora / NULLIF(d.dias_passados, 0) * d.dias_no_mes, 2) AS faturamento_projetado,\n    CASE \n        WHEN d.dias_passados = 0 THEN '⚪ Sem dados'\n        WHEN ROUND(d.faturamento_ate_agora / d.dias_passados * d.dias_no_mes, 2) >= m.meta_faturamento_parcelas \n        THEN '🟢 No caminho'\n        WHEN ROUND(d.faturamento_ate_agora / d.dias_passados * d.dias_no_mes, 2) >= m.meta_faturamento_parcelas * 0.8 \n        THEN '🟡 Atenção'\n        ELSE '🔴 Crítico'\n    END AS status_faturamento\n\nFROM dados_mes d\nJOIN unidades u ON d.unidade_id = u.id\nJOIN metas m ON d.unidade_id = m.unidade_id \n    AND d.ano = m.ano \n    AND d.mes = m.mes\nWHERE m.tipo_periodo = 'mensal'\nORDER BY d.ano DESC, d.mes DESC, u.nome;"}	fase4_7_criar_views_metas_realizado	producoes.emla@gmail.com	\N	\N
20260116091127	{"-- ============================================\n-- FASE 4.8: VIEWS CONSOLIDADAS E ALERTAS\n-- ============================================\n\n-- VIEW 1: Dashboard resumo por unidade\nCREATE OR REPLACE VIEW vw_dashboard_unidade AS\nSELECT \n    u.nome AS unidade,\n    u.id AS unidade_id,\n    \n    -- Alunos (da tabela alunos)\n    COUNT(a.id) FILTER (WHERE a.status = 'ativo') AS alunos_ativos,\n    COUNT(a.id) FILTER (WHERE a.status = 'ativo' AND tm.conta_como_pagante = TRUE) AS alunos_pagantes,\n    COUNT(a.id) FILTER (WHERE a.status = 'ativo' AND tm.codigo = 'BOLSISTA_INT') AS bolsistas_integral,\n    COUNT(a.id) FILTER (WHERE a.status = 'ativo' AND tm.codigo = 'BOLSISTA_PARC') AS bolsistas_parcial,\n    \n    -- Ticket médio (só pagantes que entram no cálculo)\n    ROUND(AVG(a.valor_parcela) FILTER (WHERE a.status = 'ativo' AND tm.entra_ticket_medio = TRUE), 2) AS ticket_medio,\n    \n    -- Faturamento previsto\n    ROUND(SUM(a.valor_parcela) FILTER (WHERE a.status = 'ativo' AND tm.conta_como_pagante = TRUE), 2) AS faturamento_previsto,\n    \n    -- Tempo médio de permanência (alunos ativos)\n    ROUND(AVG(a.tempo_permanencia_meses) FILTER (WHERE a.status = 'ativo'), 1) AS tempo_medio_permanencia\n\nFROM unidades u\nLEFT JOIN alunos a ON u.id = a.unidade_id\nLEFT JOIN tipos_matricula tm ON a.tipo_matricula_id = tm.id\nGROUP BY u.id, u.nome\nORDER BY u.nome;\n\n-- VIEW 2: Ranking de professores por retenção\nCREATE OR REPLACE VIEW vw_ranking_professores_retencao AS\nSELECT \n    u.nome AS unidade,\n    p.nome AS professor,\n    COUNT(a.id) AS total_alunos,\n    COUNT(a.id) FILTER (WHERE a.status = 'ativo') AS alunos_ativos,\n    COUNT(a.id) FILTER (WHERE a.is_ex_aluno = TRUE) AS alunos_perdidos,\n    ROUND(AVG(a.tempo_permanencia_meses), 1) AS tempo_medio_permanencia,\n    ROUND(AVG(a.percentual_presenca), 1) AS presenca_media,\n    ROUND(AVG(a.valor_parcela) FILTER (WHERE a.status = 'ativo'), 2) AS ticket_medio\nFROM professores p\nJOIN alunos a ON p.id = a.professor_atual_id\nJOIN unidades u ON a.unidade_id = u.id\nGROUP BY u.nome, p.nome\nHAVING COUNT(a.id) >= 3 -- Mínimo de 3 alunos para entrar no ranking\nORDER BY u.nome, tempo_medio_permanencia DESC;\n\n-- VIEW 3: Alertas e insights\nCREATE OR REPLACE VIEW vw_alertas AS\nSELECT \n    'CHURN_ALTO' AS tipo_alerta,\n    u.nome AS unidade,\n    'Churn acima de 5%' AS descricao,\n    rd.churn_rate_mes AS valor,\n    rd.data_referencia\nFROM relatorios_diarios rd\nJOIN unidades u ON rd.unidade_id = u.id\nWHERE rd.churn_rate_mes > 5\n  AND rd.data_referencia >= CURRENT_DATE - INTERVAL '30 days'\n\nUNION ALL\n\nSELECT \n    'RENOVACAO_BAIXA' AS tipo_alerta,\n    u.nome AS unidade,\n    'Taxa de renovação abaixo de 70%' AS descricao,\n    rd.taxa_renovacao_mes AS valor,\n    rd.data_referencia\nFROM relatorios_diarios rd\nJOIN unidades u ON rd.unidade_id = u.id\nWHERE rd.taxa_renovacao_mes < 70\n  AND rd.data_referencia >= CURRENT_DATE - INTERVAL '30 days'\n\nUNION ALL\n\nSELECT \n    'INADIMPLENCIA_ALTA' AS tipo_alerta,\n    u.nome AS unidade,\n    'Inadimplência acima de 10%' AS descricao,\n    rd.inadimplencia_percentual AS valor,\n    rd.data_referencia\nFROM relatorios_diarios rd\nJOIN unidades u ON rd.unidade_id = u.id\nWHERE rd.inadimplencia_percentual > 10\n  AND rd.data_referencia >= CURRENT_DATE - INTERVAL '30 days'\n\nORDER BY data_referencia DESC;\n\n-- VIEW 4: Resumo de renovações por mês\nCREATE OR REPLACE VIEW vw_renovacoes_mensal AS\nSELECT \n    u.nome AS unidade,\n    EXTRACT(YEAR FROM r.data_renovacao)::INTEGER AS ano,\n    EXTRACT(MONTH FROM r.data_renovacao)::INTEGER AS mes,\n    TO_CHAR(r.data_renovacao, 'YYYY-MM') AS ano_mes,\n    COUNT(*) FILTER (WHERE r.status = 'renovado') AS renovacoes,\n    COUNT(*) FILTER (WHERE r.status = 'nao_renovou') AS nao_renovacoes,\n    COUNT(*) FILTER (WHERE r.status = 'pendente') AS pendentes,\n    COUNT(*) FILTER (WHERE r.status = 'negociando') AS negociando,\n    ROUND(AVG(r.percentual_reajuste) FILTER (WHERE r.status = 'renovado'), 2) AS reajuste_medio,\n    ROUND(100.0 * COUNT(*) FILTER (WHERE r.status = 'renovado') / NULLIF(COUNT(*), 0), 1) AS taxa_renovacao\nFROM renovacoes r\nJOIN unidades u ON r.unidade_id = u.id\nGROUP BY u.nome, ano, mes, ano_mes\nORDER BY ano_mes DESC, u.nome;"}	fase4_8_criar_views_consolidadas_alertas	producoes.emla@gmail.com	\N	\N
20260116091220	{"-- ============================================\n-- FASE 4.10: DOCUMENTAÇÃO DAS TABELAS LEGADO\n-- Manter como backup por 30 dias\n-- ============================================\n\n-- Documentar tabela metas_legado\nCOMMENT ON TABLE metas_legado IS 'BACKUP: Tabela metas original (estrutura anual). Dados migrados para nova tabela metas em 2026-01-16. Pode ser removida após 30 dias de validação.';\n\n-- Documentar tabela metas_comerciais (mantida como backup)\nCOMMENT ON TABLE metas_comerciais IS 'BACKUP: Metas comerciais originais. Dados consolidados na nova tabela metas em 2026-01-16. Pode ser removida após 30 dias de validação.';"}	fase4_10_documentar_legado	producoes.emla@gmail.com	\N	\N
20260116110805	{"-- Ajustar estrutura da tabela usuarios\nALTER TABLE usuarios DROP COLUMN IF EXISTS perfil CASCADE;\nALTER TABLE usuarios ADD COLUMN IF NOT EXISTS perfil VARCHAR(20) DEFAULT 'unidade';\n\n-- Validar perfis\nALTER TABLE usuarios DROP CONSTRAINT IF EXISTS usuarios_perfil_check;\nALTER TABLE usuarios ADD CONSTRAINT usuarios_perfil_check \n  CHECK (perfil IN ('admin', 'unidade'));\n\n-- Permitir unidade_id NULL para admin\nALTER TABLE usuarios ALTER COLUMN unidade_id DROP NOT NULL;\n\n-- Índices\nCREATE INDEX IF NOT EXISTS idx_usuarios_perfil ON usuarios(perfil);\nCREATE INDEX IF NOT EXISTS idx_usuarios_unidade_id ON usuarios(unidade_id);\nCREATE INDEX IF NOT EXISTS idx_usuarios_email ON usuarios(email);\n\n-- Comentários\nCOMMENT ON COLUMN usuarios.perfil IS 'Perfil do usuário: admin (acesso total) ou unidade (acesso restrito)';\nCOMMENT ON COLUMN usuarios.unidade_id IS 'Unidade do usuário. NULL para admin, preenchido para usuários de unidade';"}	fase5_5a_ajustar_usuarios	producoes.emla@gmail.com	\N	\N
20260116110815	{"-- Habilitar RLS em todas as tabelas\nALTER TABLE alunos ENABLE ROW LEVEL SECURITY;\nALTER TABLE leads ENABLE ROW LEVEL SECURITY;\nALTER TABLE movimentacoes ENABLE ROW LEVEL SECURITY;\nALTER TABLE renovacoes ENABLE ROW LEVEL SECURITY;\nALTER TABLE evasoes ENABLE ROW LEVEL SECURITY;\nALTER TABLE relatorios_diarios ENABLE ROW LEVEL SECURITY;\nALTER TABLE metas ENABLE ROW LEVEL SECURITY;\nALTER TABLE usuarios ENABLE ROW LEVEL SECURITY;\n\n-- Funções auxiliares\nCREATE OR REPLACE FUNCTION is_admin()\nRETURNS BOOLEAN AS $$\nBEGIN\n  RETURN EXISTS (\n    SELECT 1 FROM usuarios \n    WHERE id = auth.uid() \n    AND perfil = 'admin' \n    AND ativo = true\n  );\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER;\n\nCREATE OR REPLACE FUNCTION get_user_unidade_id()\nRETURNS UUID AS $$\nBEGIN\n  RETURN (\n    SELECT unidade_id FROM usuarios \n    WHERE id = auth.uid() \n    AND ativo = true\n  );\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER;"}	fase5_5b_criar_politicas_rls_parte1	producoes.emla@gmail.com	\N	\N
20260116121300	{"-- SOLUÇÃO DEFINITIVA: Política RLS sem recursão\n-- Remove a política problemática\nDROP POLICY IF EXISTS \\"usuarios_select_policy\\" ON usuarios;\n\n-- Cria política simples: qualquer usuário autenticado pode ler qualquer registro\n-- O controle de acesso será feito no nível da aplicação\nCREATE POLICY \\"usuarios_select_policy\\" ON usuarios\n  FOR SELECT USING (true);\n\n-- Manter as outras políticas restritivas para INSERT/UPDATE/DELETE\n-- Apenas admin pode inserir/atualizar/deletar (verificado na aplicação)"}	fase5_5b_solucao_definitiva_rls_usuarios	producoes.emla@gmail.com	\N	\N
20260126165033	{"-- Corrigir função get_dados_relatorio_coordenacao para buscar média de alunos/turma corretamente\nCREATE OR REPLACE FUNCTION get_dados_relatorio_coordenacao(\n  p_unidade_id UUID,\n  p_ano INTEGER,\n  p_mes INTEGER\n)\nRETURNS JSONB\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  v_result JSONB := '{}'::jsonb;\n  v_unidade_nome TEXT;\n  v_mes_anterior INTEGER;\n  v_ano_mes_anterior INTEGER;\nBEGIN\n  IF p_mes = 1 THEN\n    v_mes_anterior := 12;\n    v_ano_mes_anterior := p_ano - 1;\n  ELSE\n    v_mes_anterior := p_mes - 1;\n    v_ano_mes_anterior := p_ano;\n  END IF;\n\n  IF p_unidade_id IS NOT NULL THEN\n    SELECT nome INTO v_unidade_nome FROM unidades WHERE id = p_unidade_id;\n  ELSE\n    v_unidade_nome := 'Consolidado';\n  END IF;\n\n  v_result := v_result || jsonb_build_object('periodo', jsonb_build_object(\n    'unidade_id', p_unidade_id,\n    'unidade_nome', v_unidade_nome,\n    'ano', p_ano,\n    'mes', p_mes,\n    'coordenadores', ARRAY['Quintela', 'Juliana']\n  ));\n\n  -- KPIs consolidados COM MÉDIA ALUNOS/TURMA DA VIEW CORRETA\n  v_result := v_result || jsonb_build_object('kpis_professores', (\n    SELECT COALESCE(jsonb_agg(row_to_json(k)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_id,\n        v.professor_nome,\n        v.carteira_alunos,\n        -- BUSCAR MÉDIA DE ALUNOS/TURMA DA VIEW CORRETA\n        COALESCE((\n          SELECT ROUND(AVG(ti.total_alunos), 2)\n          FROM vw_turmas_implicitas ti\n          WHERE ti.professor_id = v.professor_id\n            AND (p_unidade_id IS NULL OR ti.unidade_id = p_unidade_id)\n        ), 0) as media_alunos_turma,\n        v.media_presenca,\n        v.nps_medio,\n        v.taxa_conversao,\n        v.taxa_renovacao,\n        v.evasoes,\n        v.matriculas,\n        v.mrr_carteira,\n        v.ranking_matriculador,\n        v.ranking_renovador,\n        v.ranking_churn,\n        (SELECT COALESCE(array_agg(DISTINCT c.nome), ARRAY[]::text[])\n         FROM professores_cursos pc\n         JOIN cursos c ON c.id = pc.curso_id\n         WHERE pc.professor_id = v.professor_id) as cursos,\n        0 as health_score,\n        'critico' as health_status\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n      ORDER BY v.carteira_alunos DESC\n    ) k\n  ));\n\n  -- Totais consolidados COM TOTAL DE TURMAS E MÉDIA CORRETA\n  v_result := v_result || jsonb_build_object('totais', (\n    SELECT row_to_json(t)\n    FROM (\n      SELECT \n        COUNT(DISTINCT v.professor_id) as total_professores,\n        SUM(v.carteira_alunos) as total_alunos,\n        ROUND(AVG(v.carteira_alunos), 1) as media_alunos_professor,\n        -- MÉDIA ALUNOS/TURMA CORRETA DA VIEW\n        COALESCE((\n          SELECT ROUND(AVG(ti.total_alunos), 2)\n          FROM vw_turmas_implicitas ti\n          WHERE (p_unidade_id IS NULL OR ti.unidade_id = p_unidade_id)\n        ), 0) as media_alunos_turma,\n        -- TOTAL DE TURMAS DA VIEW\n        (SELECT COUNT(*) FROM vw_turmas_implicitas ti \n         WHERE (p_unidade_id IS NULL OR ti.unidade_id = p_unidade_id)) as total_turmas,\n        ROUND(AVG(v.media_presenca), 1) as media_presenca,\n        ROUND(AVG(NULLIF(v.nps_medio, 0)), 1) as nps_medio,\n        ROUND(AVG(v.taxa_conversao), 1) as taxa_conversao_media,\n        ROUND(AVG(v.taxa_renovacao), 1) as taxa_renovacao_media,\n        SUM(v.evasoes) as total_evasoes,\n        SUM(v.matriculas) as total_matriculas,\n        SUM(v.mrr_carteira) as mrr_total,\n        SUM(v.renovacoes) as renovacoes_realizadas,\n        ROUND(AVG(NULLIF(v.taxa_cancelamento, 0)), 1) as churn_medio_professor,\n        0 as health_score_medio\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('top_carteira', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_nome as professor,\n        v.carteira_alunos as alunos\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n      ORDER BY v.carteira_alunos DESC\n      LIMIT 5\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('top_health_score', '[]'::jsonb);\n\n  -- TOP MÉDIA ALUNOS/TURMA DA VIEW CORRETA\n  v_result := v_result || jsonb_build_object('top_media_turma', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        p.nome as professor,\n        ROUND(AVG(ti.total_alunos), 2) as media,\n        COUNT(ti.id) as turmas\n      FROM vw_turmas_implicitas ti\n      JOIN professores p ON p.id = ti.professor_id\n      WHERE (p_unidade_id IS NULL OR ti.unidade_id = p_unidade_id)\n      GROUP BY p.id, p.nome\n      HAVING COUNT(ti.id) > 0\n      ORDER BY AVG(ti.total_alunos) DESC\n      LIMIT 5\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('top_presenca', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_nome as professor,\n        v.media_presenca as presenca\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n      ORDER BY v.media_presenca DESC\n      LIMIT 5\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('top_matriculadores', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_nome as professor,\n        v.matriculas\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n        AND v.matriculas > 0\n      ORDER BY v.matriculas DESC\n      LIMIT 5\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('top_retencao', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        p.nome as professor,\n        ROUND(AVG(a.tempo_permanencia_meses), 1) as tempo_medio\n      FROM alunos a\n      JOIN professores p ON a.professor_atual_id = p.id\n      WHERE a.status = 'ativo'\n        AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n      GROUP BY p.id, p.nome\n      HAVING COUNT(*) >= 3\n      ORDER BY AVG(a.tempo_permanencia_meses) DESC\n      LIMIT 5\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('professores_alerta', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_id,\n        v.professor_nome as professor,\n        v.media_presenca as presenca,\n        v.evasoes,\n        v.carteira_alunos as alunos,\n        0 as health_score,\n        CASE \n          WHEN v.media_presenca < 70 OR v.evasoes > 2 THEN 'critico'\n          WHEN v.media_presenca < 80 OR v.evasoes > 0 THEN 'atencao'\n          ELSE 'ok'\n        END as status\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n        AND (v.media_presenca < 80 OR v.evasoes > 0)\n      ORDER BY v.media_presenca ASC\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('agenda', (\n    SELECT row_to_json(a)\n    FROM (\n      SELECT \n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.tipo = 'treinamento' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as treinamentos_agendados,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.tipo = 'reuniao' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as reunioes_agendadas,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.tipo = 'checkpoint' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as checkpoints_agendados,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.status = 'concluido' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as concluidos,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.status = 'pendente' \n           AND pa.data_agendada < CURRENT_DATE\n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)) as atrasados\n    ) a\n  ));\n\n  v_result := v_result || jsonb_build_object('professores_em_treinamento', (\n    SELECT COALESCE(jsonb_agg(row_to_json(pt)), '[]'::jsonb)\n    FROM (\n      SELECT \n        p.nome as professor,\n        pa.titulo as treinamento,\n        pa.data_agendada,\n        pa.status\n      FROM professor_acoes pa\n      JOIN professores p ON pa.professor_id = p.id\n      WHERE pa.tipo = 'treinamento'\n        AND pa.status IN ('pendente', 'em_andamento')\n        AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n      ORDER BY pa.data_agendada\n      LIMIT 10\n    ) pt\n  ));\n\n  v_result := v_result || jsonb_build_object('catalogo_treinamentos', (\n    SELECT COALESCE(jsonb_agg(row_to_json(ct)), '[]'::jsonb)\n    FROM (\n      SELECT id, nome, descricao\n      FROM catalogo_treinamentos\n      WHERE ativo = true\n      ORDER BY nome\n    ) ct\n  ));\n\n  v_result := v_result || jsonb_build_object('metas_professores', COALESCE(\n    (SELECT jsonb_object_agg(mk.tipo, mk.valor)\n     FROM metas_kpi mk\n     WHERE mk.ano = p_ano AND mk.mes = p_mes\n       AND (p_unidade_id IS NULL OR mk.unidade_id = p_unidade_id)\n       AND mk.tipo IN ('media_alunos_turma', 'media_alunos_professor', 'taxa_renovacao_prof', \n                       'nps_medio', 'presenca_media', 'taxa_conversao_exp', 'melhor_retencao')),\n    '{}'::jsonb\n  ));\n\n  v_result := v_result || jsonb_build_object('mes_anterior', (\n    SELECT row_to_json(t)\n    FROM (\n      SELECT \n        COUNT(DISTINCT v.professor_id) as total_professores,\n        SUM(v.carteira_alunos) as total_alunos,\n        ROUND(AVG(v.media_presenca), 1) as media_presenca,\n        SUM(v.evasoes) as total_evasoes,\n        SUM(v.matriculas) as total_matriculas,\n        0 as health_score_medio,\n        0 as professores_criticos\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = v_ano_mes_anterior AND v.mes = v_mes_anterior\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('ano_anterior', (\n    SELECT row_to_json(t)\n    FROM (\n      SELECT \n        COUNT(DISTINCT v.professor_id) as total_professores,\n        SUM(v.carteira_alunos) as total_alunos,\n        ROUND(AVG(v.media_presenca), 1) as media_presenca,\n        SUM(v.evasoes) as total_evasoes,\n        SUM(v.matriculas) as total_matriculas,\n        ROUND(AVG(v.carteira_alunos), 1) as media_alunos_professor\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano - 1 AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n    ) t\n  ));\n\n  RETURN v_result;\nEND;\n$$;"}	fix_media_alunos_turma_relatorio_coordenacao	producoes.emla@gmail.com	\N	\N
