20260116111025	{"-- Recriar funções auxiliares usando auth_user_id\nCREATE OR REPLACE FUNCTION is_admin()\nRETURNS BOOLEAN AS $$\nBEGIN\n  RETURN EXISTS (\n    SELECT 1 FROM usuarios \n    WHERE auth_user_id = auth.uid() \n    AND perfil = 'admin' \n    AND ativo = true\n  );\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER;\n\nCREATE OR REPLACE FUNCTION get_user_unidade_id()\nRETURNS UUID AS $$\nBEGIN\n  RETURN (\n    SELECT unidade_id FROM usuarios \n    WHERE auth_user_id = auth.uid() \n    AND ativo = true\n  );\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER;"}	fase5_5b_recriar_funcoes_auxiliares	producoes.emla@gmail.com	\N	\N
