20260116111056	{"-- Tabelas de referência: acesso público para leitura\nALTER TABLE unidades ENABLE ROW LEVEL SECURITY;\nDROP POLICY IF EXISTS \\"unidades_select_policy\\" ON unidades;\nCREATE POLICY \\"unidades_select_policy\\" ON unidades FOR SELECT USING (true);\n\nALTER TABLE cursos ENABLE ROW LEVEL SECURITY;\nDROP POLICY IF EXISTS \\"cursos_select_policy\\" ON cursos;\nCREATE POLICY \\"cursos_select_policy\\" ON cursos FOR SELECT USING (true);\n\nALTER TABLE professores ENABLE ROW LEVEL SECURITY;\nDROP POLICY IF EXISTS \\"professores_select_policy\\" ON professores;\nCREATE POLICY \\"professores_select_policy\\" ON professores FOR SELECT USING (true);\n\nALTER TABLE canais_origem ENABLE ROW LEVEL SECURITY;\nDROP POLICY IF EXISTS \\"canais_origem_select_policy\\" ON canais_origem;\nCREATE POLICY \\"canais_origem_select_policy\\" ON canais_origem FOR SELECT USING (true);"}	fase5_5b_politicas_tabelas_referencia_v2	producoes.emla@gmail.com	\N	\N
20260126165141	{"-- Corrigir query do top_media_turma\nCREATE OR REPLACE FUNCTION get_dados_relatorio_coordenacao(\n  p_unidade_id UUID,\n  p_ano INTEGER,\n  p_mes INTEGER\n)\nRETURNS JSONB\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  v_result JSONB := '{}'::jsonb;\n  v_unidade_nome TEXT;\n  v_mes_anterior INTEGER;\n  v_ano_mes_anterior INTEGER;\nBEGIN\n  IF p_mes = 1 THEN\n    v_mes_anterior := 12;\n    v_ano_mes_anterior := p_ano - 1;\n  ELSE\n    v_mes_anterior := p_mes - 1;\n    v_ano_mes_anterior := p_ano;\n  END IF;\n\n  IF p_unidade_id IS NOT NULL THEN\n    SELECT nome INTO v_unidade_nome FROM unidades WHERE id = p_unidade_id;\n  ELSE\n    v_unidade_nome := 'Consolidado';\n  END IF;\n\n  v_result := v_result || jsonb_build_object('periodo', jsonb_build_object(\n    'unidade_id', p_unidade_id,\n    'unidade_nome', v_unidade_nome,\n    'ano', p_ano,\n    'mes', p_mes,\n    'coordenadores', ARRAY['Quintela', 'Juliana']\n  ));\n\n  v_result := v_result || jsonb_build_object('kpis_professores', (\n    SELECT COALESCE(jsonb_agg(row_to_json(k)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_id,\n        v.professor_nome,\n        v.carteira_alunos,\n        COALESCE((\n          SELECT ROUND(AVG(ti.total_alunos), 2)\n          FROM vw_turmas_implicitas ti\n          WHERE ti.professor_id = v.professor_id\n            AND (p_unidade_id IS NULL OR ti.unidade_id = p_unidade_id)\n        ), 0) as media_alunos_turma,\n        v.media_presenca,\n        v.nps_medio,\n        v.taxa_conversao,\n        v.taxa_renovacao,\n        v.evasoes,\n        v.matriculas,\n        v.mrr_carteira,\n        v.ranking_matriculador,\n        v.ranking_renovador,\n        v.ranking_churn,\n        (SELECT COALESCE(array_agg(DISTINCT c.nome), ARRAY[]::text[])\n         FROM professores_cursos pc\n         JOIN cursos c ON c.id = pc.curso_id\n         WHERE pc.professor_id = v.professor_id) as cursos,\n        0 as health_score,\n        'critico' as health_status\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n      ORDER BY v.carteira_alunos DESC\n    ) k\n  ));\n\n  v_result := v_result || jsonb_build_object('totais', (\n    SELECT row_to_json(t)\n    FROM (\n      SELECT \n        COUNT(DISTINCT v.professor_id) as total_professores,\n        SUM(v.carteira_alunos) as total_alunos,\n        ROUND(AVG(v.carteira_alunos), 1) as media_alunos_professor,\n        COALESCE((\n          SELECT ROUND(AVG(ti.total_alunos), 2)\n          FROM vw_turmas_implicitas ti\n          WHERE (p_unidade_id IS NULL OR ti.unidade_id = p_unidade_id)\n        ), 0) as media_alunos_turma,\n        (SELECT COUNT(*) FROM vw_turmas_implicitas ti \n         WHERE (p_unidade_id IS NULL OR ti.unidade_id = p_unidade_id)) as total_turmas,\n        ROUND(AVG(v.media_presenca), 1) as media_presenca,\n        ROUND(AVG(NULLIF(v.nps_medio, 0)), 1) as nps_medio,\n        ROUND(AVG(v.taxa_conversao), 1) as taxa_conversao_media,\n        ROUND(AVG(v.taxa_renovacao), 1) as taxa_renovacao_media,\n        SUM(v.evasoes) as total_evasoes,\n        SUM(v.matriculas) as total_matriculas,\n        SUM(v.mrr_carteira) as mrr_total,\n        SUM(v.renovacoes) as renovacoes_realizadas,\n        ROUND(AVG(NULLIF(v.taxa_cancelamento, 0)), 1) as churn_medio_professor,\n        0 as health_score_medio\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('top_carteira', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_nome as professor,\n        v.carteira_alunos as alunos\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n      ORDER BY v.carteira_alunos DESC\n      LIMIT 5\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('top_health_score', '[]'::jsonb);\n\n  v_result := v_result || jsonb_build_object('top_media_turma', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        p.nome as professor,\n        ROUND(AVG(ti.total_alunos), 2) as media,\n        COUNT(*) as turmas\n      FROM vw_turmas_implicitas ti\n      JOIN professores p ON p.id = ti.professor_id\n      WHERE (p_unidade_id IS NULL OR ti.unidade_id = p_unidade_id)\n      GROUP BY p.id, p.nome\n      HAVING COUNT(*) > 0\n      ORDER BY AVG(ti.total_alunos) DESC\n      LIMIT 5\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('top_presenca', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_nome as professor,\n        v.media_presenca as presenca\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n      ORDER BY v.media_presenca DESC\n      LIMIT 5\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('top_matriculadores', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_nome as professor,\n        v.matriculas\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n        AND v.matriculas > 0\n      ORDER BY v.matriculas DESC\n      LIMIT 5\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('top_retencao', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        p.nome as professor,\n        ROUND(AVG(a.tempo_permanencia_meses), 1) as tempo_medio\n      FROM alunos a\n      JOIN professores p ON a.professor_atual_id = p.id\n      WHERE a.status = 'ativo'\n        AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n      GROUP BY p.id, p.nome\n      HAVING COUNT(*) >= 3\n      ORDER BY AVG(a.tempo_permanencia_meses) DESC\n      LIMIT 5\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('professores_alerta', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_id,\n        v.professor_nome as professor,\n        v.media_presenca as presenca,\n        v.evasoes,\n        v.carteira_alunos as alunos,\n        0 as health_score,\n        CASE \n          WHEN v.media_presenca < 70 OR v.evasoes > 2 THEN 'critico'\n          WHEN v.media_presenca < 80 OR v.evasoes > 0 THEN 'atencao'\n          ELSE 'ok'\n        END as status\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n        AND (v.media_presenca < 80 OR v.evasoes > 0)\n      ORDER BY v.media_presenca ASC\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('agenda', (\n    SELECT row_to_json(a)\n    FROM (\n      SELECT \n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.tipo = 'treinamento' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as treinamentos_agendados,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.tipo = 'reuniao' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as reunioes_agendadas,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.tipo = 'checkpoint' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as checkpoints_agendados,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.status = 'concluido' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as concluidos,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.status = 'pendente' \n           AND pa.data_agendada < CURRENT_DATE\n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)) as atrasados\n    ) a\n  ));\n\n  v_result := v_result || jsonb_build_object('professores_em_treinamento', (\n    SELECT COALESCE(jsonb_agg(row_to_json(pt)), '[]'::jsonb)\n    FROM (\n      SELECT \n        p.nome as professor,\n        pa.titulo as treinamento,\n        pa.data_agendada,\n        pa.status\n      FROM professor_acoes pa\n      JOIN professores p ON pa.professor_id = p.id\n      WHERE pa.tipo = 'treinamento'\n        AND pa.status IN ('pendente', 'em_andamento')\n        AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n      ORDER BY pa.data_agendada\n      LIMIT 10\n    ) pt\n  ));\n\n  v_result := v_result || jsonb_build_object('catalogo_treinamentos', (\n    SELECT COALESCE(jsonb_agg(row_to_json(ct)), '[]'::jsonb)\n    FROM (\n      SELECT id, nome, descricao\n      FROM catalogo_treinamentos\n      WHERE ativo = true\n      ORDER BY nome\n    ) ct\n  ));\n\n  v_result := v_result || jsonb_build_object('metas_professores', COALESCE(\n    (SELECT jsonb_object_agg(mk.tipo, mk.valor)\n     FROM metas_kpi mk\n     WHERE mk.ano = p_ano AND mk.mes = p_mes\n       AND (p_unidade_id IS NULL OR mk.unidade_id = p_unidade_id)\n       AND mk.tipo IN ('media_alunos_turma', 'media_alunos_professor', 'taxa_renovacao_prof', \n                       'nps_medio', 'presenca_media', 'taxa_conversao_exp', 'melhor_retencao')),\n    '{}'::jsonb\n  ));\n\n  v_result := v_result || jsonb_build_object('mes_anterior', (\n    SELECT row_to_json(t)\n    FROM (\n      SELECT \n        COUNT(DISTINCT v.professor_id) as total_professores,\n        SUM(v.carteira_alunos) as total_alunos,\n        ROUND(AVG(v.media_presenca), 1) as media_presenca,\n        SUM(v.evasoes) as total_evasoes,\n        SUM(v.matriculas) as total_matriculas,\n        0 as health_score_medio,\n        0 as professores_criticos\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = v_ano_mes_anterior AND v.mes = v_mes_anterior\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('ano_anterior', (\n    SELECT row_to_json(t)\n    FROM (\n      SELECT \n        COUNT(DISTINCT v.professor_id) as total_professores,\n        SUM(v.carteira_alunos) as total_alunos,\n        ROUND(AVG(v.media_presenca), 1) as media_presenca,\n        SUM(v.evasoes) as total_evasoes,\n        SUM(v.matriculas) as total_matriculas,\n        ROUND(AVG(v.carteira_alunos), 1) as media_alunos_professor\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano - 1 AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n    ) t\n  ));\n\n  RETURN v_result;\nEND;\n$$;"}	fix_top_media_turma_query	producoes.emla@gmail.com	\N	\N
20260118113943	{"-- =====================================================\n-- TABELA: evasoes_v2\n-- Evasões com estrutura correta (FKs em vez de texto)\n-- =====================================================\n\nCREATE TABLE evasoes_v2 (\n  id SERIAL PRIMARY KEY,\n  aluno_id INTEGER REFERENCES alunos(id),\n  unidade_id UUID REFERENCES unidades(id) NOT NULL,\n  data_evasao DATE NOT NULL DEFAULT CURRENT_DATE,\n  tipo_saida_id INTEGER REFERENCES tipos_saida(id) NOT NULL,\n  motivo_saida_id INTEGER REFERENCES motivos_saida(id),\n  professor_id INTEGER REFERENCES professores(id),\n  valor_parcela NUMERIC(10,2),\n  situacao_pagamento VARCHAR(20) DEFAULT 'em_dia' CHECK (situacao_pagamento IN ('em_dia', 'inadimplente')),\n  data_prevista_saida DATE, -- Para avisos prévios (mês que vai sair)\n  observacoes TEXT,\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  updated_at TIMESTAMPTZ DEFAULT NOW(),\n  created_by INTEGER REFERENCES usuarios(id)\n);\n\n-- Comentário da tabela\nCOMMENT ON TABLE evasoes_v2 IS 'Evasões 2026+ com estrutura correta (FKs). Tabela evasoes original mantida como histórico 2025.';\n\n-- Índices para performance\nCREATE INDEX idx_evasoes_v2_unidade ON evasoes_v2(unidade_id);\nCREATE INDEX idx_evasoes_v2_data ON evasoes_v2(data_evasao);\nCREATE INDEX idx_evasoes_v2_tipo ON evasoes_v2(tipo_saida_id);\nCREATE INDEX idx_evasoes_v2_aluno ON evasoes_v2(aluno_id);\nCREATE INDEX idx_evasoes_v2_professor ON evasoes_v2(professor_id);\n\n-- RLS (Row Level Security)\nALTER TABLE evasoes_v2 ENABLE ROW LEVEL SECURITY;\n\n-- Política corrigida usando auth_user_id\nCREATE POLICY \\"evasoes_v2_select\\" ON evasoes_v2\nFOR SELECT USING (\n  EXISTS (SELECT 1 FROM usuarios WHERE auth_user_id = auth.uid() AND perfil = 'admin')\n  OR\n  unidade_id IN (SELECT unidade_id FROM usuarios WHERE auth_user_id = auth.uid())\n);\n\nCREATE POLICY \\"evasoes_v2_insert\\" ON evasoes_v2\nFOR INSERT WITH CHECK (\n  EXISTS (SELECT 1 FROM usuarios WHERE auth_user_id = auth.uid() AND perfil = 'admin')\n  OR\n  unidade_id IN (SELECT unidade_id FROM usuarios WHERE auth_user_id = auth.uid())\n);\n\nCREATE POLICY \\"evasoes_v2_update\\" ON evasoes_v2\nFOR UPDATE USING (\n  EXISTS (SELECT 1 FROM usuarios WHERE auth_user_id = auth.uid() AND perfil = 'admin')\n  OR\n  unidade_id IN (SELECT unidade_id FROM usuarios WHERE auth_user_id = auth.uid())\n);\n\nCREATE POLICY \\"evasoes_v2_delete\\" ON evasoes_v2\nFOR DELETE USING (\n  EXISTS (SELECT 1 FROM usuarios WHERE auth_user_id = auth.uid() AND perfil = 'admin')\n);\n\n-- Trigger para updated_at\nCREATE TRIGGER set_updated_at_evasoes_v2\n  BEFORE UPDATE ON evasoes_v2\n  FOR EACH ROW\n  EXECUTE FUNCTION update_updated_at_column();"}	fase6_1a_criar_tabela_evasoes_v2	producoes.emla@gmail.com	\N	\N
20260118114000	{"-- =====================================================\n-- TABELA: leads_diarios\n-- Dados agregados do comercial (Hunters)\n-- Ex: \\"Hoje vieram 5 leads do Instagram interessados em Violão\\"\n-- =====================================================\n\nCREATE TABLE leads_diarios (\n  id SERIAL PRIMARY KEY,\n  unidade_id UUID REFERENCES unidades(id) NOT NULL,\n  data DATE NOT NULL DEFAULT CURRENT_DATE,\n  tipo VARCHAR(50) NOT NULL CHECK (tipo IN (\n    'lead',\n    'experimental_agendada',\n    'experimental_realizada',\n    'experimental_faltou',\n    'visita_escola',\n    'matricula'\n  )),\n  canal_origem_id INTEGER REFERENCES canais_origem(id),\n  curso_id INTEGER REFERENCES cursos(id),\n  quantidade INTEGER NOT NULL DEFAULT 1 CHECK (quantidade > 0),\n  observacoes TEXT,\n  -- Campos extras para matrícula (preenchidos quando tipo = 'matricula')\n  aluno_nome VARCHAR(255),\n  aluno_idade INTEGER,\n  professor_experimental_id INTEGER REFERENCES professores(id),\n  professor_fixo_id INTEGER REFERENCES professores(id),\n  agente_comercial VARCHAR(255),\n  valor_passaporte NUMERIC(10,2),\n  valor_parcela NUMERIC(10,2),\n  forma_pagamento_id INTEGER REFERENCES formas_pagamento(id),\n  tipo_matricula VARCHAR(20) CHECK (tipo_matricula IN ('EMLA', 'LAMK')),\n  aluno_novo_retorno VARCHAR(20) CHECK (aluno_novo_retorno IN ('novo', 'retorno')),\n  -- Metadados\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  updated_at TIMESTAMPTZ DEFAULT NOW(),\n  created_by INTEGER REFERENCES usuarios(id)\n);\n\n-- Comentário da tabela\nCOMMENT ON TABLE leads_diarios IS 'Dados agregados do comercial (Hunters). Leads, experimentais e visitas são agregados por quantidade. Matrículas são individuais com dados completos.';\n\n-- Índices para performance\nCREATE INDEX idx_leads_diarios_unidade ON leads_diarios(unidade_id);\nCREATE INDEX idx_leads_diarios_data ON leads_diarios(data);\nCREATE INDEX idx_leads_diarios_tipo ON leads_diarios(tipo);\nCREATE INDEX idx_leads_diarios_canal ON leads_diarios(canal_origem_id);\nCREATE INDEX idx_leads_diarios_curso ON leads_diarios(curso_id);\nCREATE INDEX idx_leads_diarios_unidade_data ON leads_diarios(unidade_id, data);\n\n-- RLS (Row Level Security)\nALTER TABLE leads_diarios ENABLE ROW LEVEL SECURITY;\n\n-- Políticas usando auth_user_id\nCREATE POLICY \\"leads_diarios_select\\" ON leads_diarios\nFOR SELECT USING (\n  EXISTS (SELECT 1 FROM usuarios WHERE auth_user_id = auth.uid() AND perfil = 'admin')\n  OR\n  unidade_id IN (SELECT unidade_id FROM usuarios WHERE auth_user_id = auth.uid())\n);\n\nCREATE POLICY \\"leads_diarios_insert\\" ON leads_diarios\nFOR INSERT WITH CHECK (\n  EXISTS (SELECT 1 FROM usuarios WHERE auth_user_id = auth.uid() AND perfil = 'admin')\n  OR\n  unidade_id IN (SELECT unidade_id FROM usuarios WHERE auth_user_id = auth.uid())\n);\n\nCREATE POLICY \\"leads_diarios_update\\" ON leads_diarios\nFOR UPDATE USING (\n  EXISTS (SELECT 1 FROM usuarios WHERE auth_user_id = auth.uid() AND perfil = 'admin')\n  OR\n  unidade_id IN (SELECT unidade_id FROM usuarios WHERE auth_user_id = auth.uid())\n);\n\nCREATE POLICY \\"leads_diarios_delete\\" ON leads_diarios\nFOR DELETE USING (\n  EXISTS (SELECT 1 FROM usuarios WHERE auth_user_id = auth.uid() AND perfil = 'admin')\n);\n\n-- Trigger para updated_at\nCREATE TRIGGER set_updated_at_leads_diarios\n  BEFORE UPDATE ON leads_diarios\n  FOR EACH ROW\n  EXECUTE FUNCTION update_updated_at_column();"}	fase6_1b_criar_tabela_leads_diarios	producoes.emla@gmail.com	\N	\N
20260118125636	{"-- Criar tabela motivos_arquivamento para leads arquivados\nCREATE TABLE IF NOT EXISTS motivos_arquivamento (\n  id SERIAL PRIMARY KEY,\n  nome VARCHAR(100) NOT NULL,\n  descricao TEXT,\n  ativo BOOLEAN DEFAULT true,\n  created_at TIMESTAMPTZ DEFAULT NOW()\n);\n\n-- Inserir motivos padrão\nINSERT INTO motivos_arquivamento (nome, descricao) VALUES\n  ('Não respondeu', 'Lead não respondeu após múltiplas tentativas de contato'),\n  ('Desistiu', 'Lead informou que desistiu de fazer aulas'),\n  ('Fora do perfil', 'Lead não se encaixa no perfil de aluno da escola'),\n  ('Preço', 'Lead achou o valor muito alto'),\n  ('Horário incompatível', 'Não há horário disponível que atenda o lead'),\n  ('Distância', 'Lead mora muito longe da unidade'),\n  ('Outro', 'Outro motivo não listado');\n\n-- Habilitar RLS\nALTER TABLE motivos_arquivamento ENABLE ROW LEVEL SECURITY;\n\n-- Política de leitura para usuários autenticados\nCREATE POLICY \\"Usuários autenticados podem ler motivos_arquivamento\\"\n  ON motivos_arquivamento FOR SELECT\n  TO authenticated\n  USING (true);\n\n-- Comentário na tabela\nCOMMENT ON TABLE motivos_arquivamento IS 'Motivos para arquivamento de leads que não converteram';"}	create_motivos_arquivamento	producoes.emla@gmail.com	\N	\N
