20260126170837	{"-- Corrigir função para usar taxa_cancelamento em vez de taxa_renovacao no cálculo do Health Score\nCREATE OR REPLACE FUNCTION get_dados_relatorio_coordenacao(\n  p_unidade_id UUID,\n  p_ano INTEGER,\n  p_mes INTEGER\n)\nRETURNS JSONB\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  v_result JSONB := '{}'::jsonb;\n  v_unidade_nome TEXT;\n  v_mes_anterior INTEGER;\n  v_ano_mes_anterior INTEGER;\nBEGIN\n  IF p_mes = 1 THEN\n    v_mes_anterior := 12;\n    v_ano_mes_anterior := p_ano - 1;\n  ELSE\n    v_mes_anterior := p_mes - 1;\n    v_ano_mes_anterior := p_ano;\n  END IF;\n\n  IF p_unidade_id IS NOT NULL THEN\n    SELECT nome INTO v_unidade_nome FROM unidades WHERE id = p_unidade_id;\n  ELSE\n    v_unidade_nome := 'Consolidado';\n  END IF;\n\n  v_result := v_result || jsonb_build_object('periodo', jsonb_build_object(\n    'unidade_id', p_unidade_id,\n    'unidade_nome', v_unidade_nome,\n    'ano', p_ano,\n    'mes', p_mes,\n    'coordenadores', ARRAY['Quintela', 'Juliana']\n  ));\n\n  -- KPIs professores COM TAXA_CANCELAMENTO para cálculo correto do Health Score\n  v_result := v_result || jsonb_build_object('kpis_professores', (\n    SELECT COALESCE(jsonb_agg(row_to_json(k)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_id,\n        v.professor_nome,\n        v.carteira_alunos,\n        COALESCE((\n          SELECT ROUND(AVG(ti.total_alunos), 2)\n          FROM vw_turmas_implicitas ti\n          WHERE ti.professor_id = v.professor_id\n            AND (p_unidade_id IS NULL OR ti.unidade_id = p_unidade_id)\n        ), 0) as media_alunos_turma,\n        v.media_presenca,\n        v.nps_medio,\n        v.taxa_conversao,\n        -- USAR 100 - taxa_cancelamento como retenção (igual ao frontend)\n        (100 - v.taxa_cancelamento) as taxa_retencao,\n        v.taxa_cancelamento,\n        v.evasoes,\n        v.matriculas,\n        v.mrr_carteira,\n        (SELECT COALESCE(array_agg(DISTINCT c.nome), ARRAY[]::text[])\n         FROM professores_cursos pc\n         JOIN cursos c ON c.id = pc.curso_id\n         WHERE pc.professor_id = v.professor_id) as cursos\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n      ORDER BY v.carteira_alunos DESC\n    ) k\n  ));\n\n  v_result := v_result || jsonb_build_object('totais', (\n    SELECT row_to_json(t)\n    FROM (\n      SELECT \n        COUNT(DISTINCT v.professor_id) as total_professores,\n        SUM(v.carteira_alunos) as total_alunos,\n        ROUND(AVG(v.carteira_alunos), 1) as media_alunos_professor,\n        COALESCE((\n          SELECT ROUND(AVG(ti.total_alunos), 2)\n          FROM vw_turmas_implicitas ti\n          WHERE (p_unidade_id IS NULL OR ti.unidade_id = p_unidade_id)\n        ), 0) as media_alunos_turma,\n        (SELECT COUNT(*) FROM vw_turmas_implicitas ti \n         WHERE (p_unidade_id IS NULL OR ti.unidade_id = p_unidade_id)) as total_turmas,\n        ROUND(AVG(v.media_presenca), 1) as media_presenca,\n        ROUND(AVG(NULLIF(v.nps_medio, 0)), 1) as nps_medio,\n        ROUND(AVG(v.taxa_conversao), 1) as taxa_conversao_media,\n        ROUND(AVG(100 - v.taxa_cancelamento), 1) as taxa_retencao_media,\n        SUM(v.evasoes) as total_evasoes,\n        SUM(v.matriculas) as total_matriculas,\n        SUM(v.mrr_carteira) as mrr_total,\n        SUM(v.renovacoes) as renovacoes_realizadas,\n        ROUND(AVG(NULLIF(v.taxa_cancelamento, 0)), 1) as churn_medio_professor\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('top_carteira', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT v.professor_nome as professor, v.carteira_alunos as alunos\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n      ORDER BY v.carteira_alunos DESC LIMIT 5\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('top_health_score', '[]'::jsonb);\n\n  v_result := v_result || jsonb_build_object('top_media_turma', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT p.nome as professor, ROUND(AVG(ti.total_alunos), 2) as media, COUNT(*) as turmas\n      FROM vw_turmas_implicitas ti\n      JOIN professores p ON p.id = ti.professor_id\n      WHERE (p_unidade_id IS NULL OR ti.unidade_id = p_unidade_id)\n      GROUP BY p.id, p.nome HAVING COUNT(*) > 0\n      ORDER BY AVG(ti.total_alunos) DESC LIMIT 5\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('top_presenca', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT v.professor_nome as professor, v.media_presenca as presenca\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n      ORDER BY v.media_presenca DESC LIMIT 5\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('top_matriculadores', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT v.professor_nome as professor, v.matriculas\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n        AND v.matriculas > 0\n      ORDER BY v.matriculas DESC LIMIT 5\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('top_retencao', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT p.nome as professor, ROUND(AVG(a.tempo_permanencia_meses), 1) as tempo_medio\n      FROM alunos a\n      JOIN professores p ON a.professor_atual_id = p.id\n      WHERE a.status = 'ativo'\n        AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n      GROUP BY p.id, p.nome HAVING COUNT(*) >= 3\n      ORDER BY AVG(a.tempo_permanencia_meses) DESC LIMIT 5\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('professores_alerta', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_id, v.professor_nome as professor, v.media_presenca as presenca,\n        v.evasoes, v.carteira_alunos as alunos,\n        CASE \n          WHEN v.media_presenca < 70 OR v.evasoes > 2 THEN 'critico'\n          WHEN v.media_presenca < 80 OR v.evasoes > 0 THEN 'atencao'\n          ELSE 'ok'\n        END as status\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n        AND (v.media_presenca < 80 OR v.evasoes > 0)\n      ORDER BY v.media_presenca ASC\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('agenda', (\n    SELECT row_to_json(a) FROM (\n      SELECT \n        (SELECT COUNT(*) FROM professor_acoes pa WHERE pa.tipo = 'treinamento' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as treinamentos_agendados,\n        (SELECT COUNT(*) FROM professor_acoes pa WHERE pa.tipo = 'reuniao' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as reunioes_agendadas,\n        (SELECT COUNT(*) FROM professor_acoes pa WHERE pa.tipo = 'checkpoint' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as checkpoints_agendados,\n        (SELECT COUNT(*) FROM professor_acoes pa WHERE pa.status = 'concluido' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as concluidos,\n        (SELECT COUNT(*) FROM professor_acoes pa WHERE pa.status = 'pendente' \n           AND pa.data_agendada < CURRENT_DATE\n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)) as atrasados\n    ) a\n  ));\n\n  v_result := v_result || jsonb_build_object('professores_em_treinamento', (\n    SELECT COALESCE(jsonb_agg(row_to_json(pt)), '[]'::jsonb)\n    FROM (\n      SELECT p.nome as professor, pa.titulo as treinamento, pa.data_agendada, pa.status\n      FROM professor_acoes pa\n      JOIN professores p ON pa.professor_id = p.id\n      WHERE pa.tipo = 'treinamento' AND pa.status IN ('pendente', 'em_andamento')\n        AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n      ORDER BY pa.data_agendada LIMIT 10\n    ) pt\n  ));\n\n  v_result := v_result || jsonb_build_object('catalogo_treinamentos', (\n    SELECT COALESCE(jsonb_agg(row_to_json(ct)), '[]'::jsonb)\n    FROM (SELECT id, nome, descricao FROM catalogo_treinamentos WHERE ativo = true ORDER BY nome) ct\n  ));\n\n  v_result := v_result || jsonb_build_object('metas_professores', COALESCE(\n    (SELECT jsonb_object_agg(mk.tipo, mk.valor)\n     FROM metas_kpi mk\n     WHERE mk.ano = p_ano AND mk.mes = p_mes\n       AND (p_unidade_id IS NULL OR mk.unidade_id = p_unidade_id)\n       AND mk.tipo IN ('media_alunos_turma', 'media_alunos_professor', 'taxa_renovacao_prof', \n                       'nps_medio', 'presenca_media', 'taxa_conversao_exp', 'melhor_retencao')),\n    '{}'::jsonb\n  ));\n\n  v_result := v_result || jsonb_build_object('mes_anterior', (\n    SELECT row_to_json(t) FROM (\n      SELECT \n        COUNT(DISTINCT v.professor_id) as total_professores,\n        SUM(v.carteira_alunos) as total_alunos,\n        ROUND(AVG(v.media_presenca), 1) as media_presenca,\n        SUM(v.evasoes) as total_evasoes,\n        SUM(v.matriculas) as total_matriculas,\n        0 as health_score_medio, 0 as professores_criticos\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = v_ano_mes_anterior AND v.mes = v_mes_anterior\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n    ) t\n  ));\n\n  v_result := v_result || jsonb_build_object('ano_anterior', (\n    SELECT row_to_json(t) FROM (\n      SELECT \n        COUNT(DISTINCT v.professor_id) as total_professores,\n        SUM(v.carteira_alunos) as total_alunos,\n        ROUND(AVG(v.media_presenca), 1) as media_presenca,\n        SUM(v.evasoes) as total_evasoes,\n        SUM(v.matriculas) as total_matriculas,\n        ROUND(AVG(v.carteira_alunos), 1) as media_alunos_professor\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano - 1 AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n    ) t\n  ));\n\n  RETURN v_result;\nEND;\n$$;"}	fix_health_score_usar_taxa_cancelamento	producoes.emla@gmail.com	\N	\N
20260118125755	{"-- View de renovações pendentes por mês\nCREATE OR REPLACE VIEW vw_renovacoes_pendentes AS\nSELECT \n  a.unidade_id,\n  u.nome as unidade_nome,\n  DATE_TRUNC('month', a.data_fim_contrato)::date as mes_vencimento,\n  COUNT(*) as total_vencendo,\n  COUNT(*) FILTER (WHERE r.status = 'realizada') as renovadas,\n  COUNT(*) FILTER (WHERE r.status = 'nao_renovada') as nao_renovadas,\n  COUNT(*) FILTER (WHERE r.id IS NULL AND a.data_fim_contrato >= CURRENT_DATE) as pendentes,\n  COUNT(*) FILTER (WHERE r.id IS NULL AND a.data_fim_contrato < CURRENT_DATE) as atrasadas\nFROM alunos a\nLEFT JOIN unidades u ON a.unidade_id = u.id\nLEFT JOIN renovacoes r ON a.id = r.aluno_id\nWHERE a.status = 'ativo'\nAND a.data_fim_contrato >= CURRENT_DATE - INTERVAL '60 days'\nAND a.data_fim_contrato <= CURRENT_DATE + INTERVAL '60 days'\nGROUP BY a.unidade_id, u.nome, DATE_TRUNC('month', a.data_fim_contrato)\nORDER BY mes_vencimento;\n\nCOMMENT ON VIEW vw_renovacoes_pendentes IS 'Renovações pendentes, atrasadas e realizadas por mês e unidade';"}	create_view_renovacoes_pendentes	producoes.emla@gmail.com	\N	\N
20260118125824	{"-- View completa de KPIs por Professor com Rankings\nCREATE OR REPLACE VIEW vw_kpis_professor_completo AS\nWITH carteira AS (\n  SELECT \n    professor_atual_id, \n    unidade_id,\n    COUNT(*) as qtd_alunos, \n    AVG(valor_parcela) as ticket_medio,\n    AVG(percentual_presenca) as media_presenca\n  FROM alunos \n  WHERE status = 'ativo' AND professor_atual_id IS NOT NULL\n  GROUP BY professor_atual_id, unidade_id\n),\nexperimentais AS (\n  SELECT \n    professor_experimental_id, \n    SUM(quantidade) as total\n  FROM leads_diarios \n  WHERE tipo = 'experimental_realizada' AND professor_experimental_id IS NOT NULL\n  GROUP BY professor_experimental_id\n),\nmatriculas AS (\n  SELECT \n    professor_experimental_id, \n    COUNT(*) as total\n  FROM leads_diarios \n  WHERE tipo = 'matricula' AND professor_experimental_id IS NOT NULL\n  GROUP BY professor_experimental_id\n),\nevasoes AS (\n  SELECT \n    professor_id, \n    COUNT(*) as total, \n    COALESCE(SUM(valor_parcela), 0) as mrr_perdido\n  FROM evasoes_v2 \n  WHERE professor_id IS NOT NULL\n  GROUP BY professor_id\n),\nrenovacoes_data AS (\n  SELECT \n    professor_id, \n    COUNT(*) FILTER (WHERE status = 'realizada') as realizadas,\n    COUNT(*) FILTER (WHERE status = 'nao_renovada') as nao_renovadas\n  FROM renovacoes \n  WHERE professor_id IS NOT NULL\n  GROUP BY professor_id\n)\nSELECT \n  p.id,\n  p.nome,\n  c.unidade_id,\n  u.nome as unidade_nome,\n  COALESCE(c.qtd_alunos, 0) as carteira_alunos,\n  COALESCE(ROUND(c.ticket_medio::numeric, 2), 0) as ticket_medio,\n  COALESCE(ROUND(c.media_presenca::numeric, 1), 0) as media_presenca,\n  COALESCE(100 - ROUND(c.media_presenca::numeric, 1), 100) as taxa_faltas,\n  COALESCE(e.total, 0) as experimentais,\n  COALESCE(m.total, 0) as matriculas,\n  CASE \n    WHEN COALESCE(e.total, 0) > 0 \n    THEN ROUND((COALESCE(m.total, 0)::decimal / e.total) * 100, 2) \n    ELSE 0 \n  END as taxa_conversao,\n  COALESCE(ev.total, 0) as evasoes,\n  COALESCE(ROUND(ev.mrr_perdido::numeric, 2), 0) as mrr_perdido,\n  COALESCE(rd.realizadas, 0) as renovacoes,\n  COALESCE(rd.nao_renovadas, 0) as nao_renovacoes,\n  CASE \n    WHEN COALESCE(rd.realizadas, 0) + COALESCE(rd.nao_renovadas, 0) > 0 \n    THEN ROUND((COALESCE(rd.realizadas, 0)::decimal / (COALESCE(rd.realizadas, 0) + COALESCE(rd.nao_renovadas, 0))) * 100, 2) \n    ELSE 0 \n  END as taxa_renovacao,\n  CASE \n    WHEN COALESCE(rd.realizadas, 0) + COALESCE(rd.nao_renovadas, 0) > 0 \n    THEN ROUND((COALESCE(rd.nao_renovadas, 0)::decimal / (COALESCE(rd.realizadas, 0) + COALESCE(rd.nao_renovadas, 0))) * 100, 2) \n    ELSE 0 \n  END as taxa_nao_renovacao,\n  CASE \n    WHEN COALESCE(c.qtd_alunos, 0) > 0 \n    THEN ROUND((COALESCE(ev.total, 0)::decimal / c.qtd_alunos) * 100, 2) \n    ELSE 0 \n  END as taxa_cancelamento,\n  RANK() OVER (\n    ORDER BY CASE WHEN COALESCE(e.total, 0) > 0 THEN (COALESCE(m.total, 0)::decimal / e.total) ELSE 0 END DESC\n  ) as ranking_matriculador,\n  RANK() OVER (\n    ORDER BY CASE WHEN COALESCE(rd.realizadas, 0) + COALESCE(rd.nao_renovadas, 0) > 0 \n      THEN (COALESCE(rd.realizadas, 0)::decimal / (COALESCE(rd.realizadas, 0) + COALESCE(rd.nao_renovadas, 0))) \n      ELSE 0 END DESC\n  ) as ranking_renovador,\n  RANK() OVER (\n    ORDER BY COALESCE(ev.total, 0) ASC\n  ) as ranking_churn,\n  p.nps_medio,\n  p.media_alunos_turma\nFROM professores p\nLEFT JOIN carteira c ON p.id = c.professor_atual_id\nLEFT JOIN unidades u ON c.unidade_id = u.id\nLEFT JOIN experimentais e ON p.id = e.professor_experimental_id\nLEFT JOIN matriculas m ON p.id = m.professor_experimental_id\nLEFT JOIN evasoes ev ON p.id = ev.professor_id\nLEFT JOIN renovacoes_data rd ON p.id = rd.professor_id\nWHERE p.ativo = true;\n\nCOMMENT ON VIEW vw_kpis_professor_completo IS 'KPIs completos por professor com rankings de matriculador, renovador e churn';"}	create_view_kpis_professor_completo_v2	producoes.emla@gmail.com	\N	\N
20260118170602	{"-- Atualizar view para incluir unidade_id\nDROP VIEW IF EXISTS vw_kpis_professor_mensal;\n\nCREATE OR REPLACE VIEW vw_kpis_professor_mensal AS\nWITH carteira AS (\n    SELECT \n        a.professor_atual_id AS professor_id,\n        a.unidade_id,\n        COUNT(*) AS carteira_alunos,\n        AVG(a.valor_parcela) AS ticket_medio,\n        AVG(a.percentual_presenca) AS media_presenca,\n        SUM(a.valor_parcela) AS mrr_carteira\n    FROM alunos a\n    WHERE a.status = 'ativo' AND a.professor_atual_id IS NOT NULL\n    GROUP BY a.professor_atual_id, a.unidade_id\n),\nexperimentais AS (\n    SELECT \n        ld.professor_experimental_id AS professor_id,\n        ld.unidade_id,\n        EXTRACT(YEAR FROM ld.data)::INTEGER AS ano,\n        EXTRACT(MONTH FROM ld.data)::INTEGER AS mes,\n        SUM(CASE WHEN ld.tipo = 'experimental_realizada' THEN ld.quantidade ELSE 0 END) AS experimentais,\n        SUM(CASE WHEN ld.tipo = 'matricula' THEN ld.quantidade ELSE 0 END) AS matriculas_leads\n    FROM leads_diarios ld\n    WHERE ld.professor_experimental_id IS NOT NULL\n    GROUP BY ld.professor_experimental_id, ld.unidade_id, EXTRACT(YEAR FROM ld.data), EXTRACT(MONTH FROM ld.data)\n),\nmatriculas_mes AS (\n    SELECT \n        a.professor_experimental_id AS professor_id,\n        a.unidade_id,\n        EXTRACT(YEAR FROM a.data_matricula)::INTEGER AS ano,\n        EXTRACT(MONTH FROM a.data_matricula)::INTEGER AS mes,\n        COUNT(*) AS matriculas\n    FROM alunos a\n    WHERE a.professor_experimental_id IS NOT NULL AND a.data_matricula IS NOT NULL\n    GROUP BY a.professor_experimental_id, a.unidade_id, EXTRACT(YEAR FROM a.data_matricula), EXTRACT(MONTH FROM a.data_matricula)\n),\nrenovacoes_mes AS (\n    SELECT \n        r.professor_id,\n        r.unidade_id,\n        EXTRACT(YEAR FROM r.data_renovacao)::INTEGER AS ano,\n        EXTRACT(MONTH FROM r.data_renovacao)::INTEGER AS mes,\n        COUNT(*) FILTER (WHERE r.status = 'realizada') AS renovacoes,\n        COUNT(*) FILTER (WHERE r.status = 'nao_renovada') AS nao_renovacoes,\n        COUNT(*) AS total_renovacoes\n    FROM renovacoes r\n    WHERE r.professor_id IS NOT NULL\n    GROUP BY r.professor_id, r.unidade_id, EXTRACT(YEAR FROM r.data_renovacao), EXTRACT(MONTH FROM r.data_renovacao)\n),\nevasoes_mes AS (\n    SELECT \n        ev.professor_id,\n        ev.unidade_id,\n        EXTRACT(YEAR FROM ev.data_evasao)::INTEGER AS ano,\n        EXTRACT(MONTH FROM ev.data_evasao)::INTEGER AS mes,\n        COUNT(*) AS evasoes,\n        SUM(ev.valor_parcela) AS mrr_perdido\n    FROM evasoes_v2 ev\n    WHERE ev.professor_id IS NOT NULL\n    GROUP BY ev.professor_id, ev.unidade_id, EXTRACT(YEAR FROM ev.data_evasao), EXTRACT(MONTH FROM ev.data_evasao)\n)\nSELECT \n    p.id AS professor_id,\n    p.nome AS professor_nome,\n    COALESCE(c.unidade_id, e.unidade_id, m.unidade_id) AS unidade_id,\n    COALESCE(e.ano, EXTRACT(YEAR FROM CURRENT_DATE)::INTEGER) AS ano,\n    COALESCE(e.mes, EXTRACT(MONTH FROM CURRENT_DATE)::INTEGER) AS mes,\n    COALESCE(c.carteira_alunos, 0)::INTEGER AS carteira_alunos,\n    COALESCE(c.ticket_medio, 0)::NUMERIC(10,2) AS ticket_medio,\n    COALESCE(c.media_presenca, 0)::NUMERIC(5,2) AS media_presenca,\n    COALESCE(100 - c.media_presenca, 0)::NUMERIC(5,2) AS taxa_faltas,\n    COALESCE(c.mrr_carteira, 0)::NUMERIC(12,2) AS mrr_carteira,\n    COALESCE(p.nps_medio, 0)::NUMERIC(5,2) AS nps_medio,\n    COALESCE(p.media_alunos_turma, 0)::NUMERIC(5,2) AS media_alunos_turma,\n    COALESCE(e.experimentais, 0)::INTEGER AS experimentais,\n    COALESCE(m.matriculas, e.matriculas_leads, 0)::INTEGER AS matriculas,\n    CASE \n        WHEN COALESCE(e.experimentais, 0) > 0 \n        THEN ROUND(COALESCE(m.matriculas, e.matriculas_leads, 0)::NUMERIC / e.experimentais * 100, 2)\n        ELSE 0 \n    END AS taxa_conversao,\n    COALESCE(r.renovacoes, 0)::INTEGER AS renovacoes,\n    COALESCE(r.nao_renovacoes, 0)::INTEGER AS nao_renovacoes,\n    CASE \n        WHEN COALESCE(r.total_renovacoes, 0) > 0 \n        THEN ROUND(r.renovacoes::NUMERIC / r.total_renovacoes * 100, 2)\n        ELSE 0 \n    END AS taxa_renovacao,\n    COALESCE(ev.evasoes, 0)::INTEGER AS evasoes,\n    COALESCE(ev.mrr_perdido, 0)::NUMERIC(12,2) AS mrr_perdido,\n    CASE \n        WHEN COALESCE(c.carteira_alunos, 0) > 0 \n        THEN ROUND(COALESCE(ev.evasoes, 0)::NUMERIC / c.carteira_alunos * 100, 2)\n        ELSE 0 \n    END AS taxa_cancelamento,\n    RANK() OVER (ORDER BY CASE WHEN COALESCE(e.experimentais, 0) > 0 THEN COALESCE(m.matriculas, 0)::NUMERIC / e.experimentais ELSE 0 END DESC) AS ranking_matriculador,\n    RANK() OVER (ORDER BY CASE WHEN COALESCE(r.total_renovacoes, 0) > 0 THEN r.renovacoes::NUMERIC / r.total_renovacoes ELSE 0 END DESC) AS ranking_renovador,\n    RANK() OVER (ORDER BY CASE WHEN COALESCE(c.carteira_alunos, 0) > 0 THEN COALESCE(ev.evasoes, 0)::NUMERIC / c.carteira_alunos ELSE 1 END) AS ranking_churn\nFROM professores p\nLEFT JOIN carteira c ON c.professor_id = p.id\nLEFT JOIN experimentais e ON e.professor_id = p.id\nLEFT JOIN matriculas_mes m ON m.professor_id = p.id AND m.ano = e.ano AND m.mes = e.mes AND m.unidade_id = e.unidade_id\nLEFT JOIN renovacoes_mes r ON r.professor_id = p.id AND r.ano = COALESCE(e.ano, EXTRACT(YEAR FROM CURRENT_DATE)::INTEGER) AND r.mes = COALESCE(e.mes, EXTRACT(MONTH FROM CURRENT_DATE)::INTEGER)\nLEFT JOIN evasoes_mes ev ON ev.professor_id = p.id AND ev.ano = COALESCE(e.ano, EXTRACT(YEAR FROM CURRENT_DATE)::INTEGER) AND ev.mes = COALESCE(e.mes, EXTRACT(MONTH FROM CURRENT_DATE)::INTEGER)\nWHERE p.ativo = true;"}	add_unidade_id_to_vw_kpis_professor_mensal	producoes.emla@gmail.com	\N	\N
