20260118235137	{"-- View para KPIs comerciais históricos (dados de 2025)\nCREATE OR REPLACE VIEW vw_kpis_comercial_historico AS\nSELECT \n  dc.id,\n  dc.competencia,\n  dc.unidade as unidade_nome,\n  u.id as unidade_id,\n  EXTRACT(year FROM dc.competencia)::integer as ano,\n  EXTRACT(month FROM dc.competencia)::integer as mes,\n  dc.total_leads,\n  dc.aulas_experimentais as experimentais_realizadas,\n  dc.novas_matriculas_total,\n  dc.novas_matriculas_lamk,\n  dc.novas_matriculas_emla,\n  dc.ticket_medio_parcelas,\n  dc.ticket_medio_passaporte,\n  dc.faturamento_passaporte,\n  -- Taxas calculadas\n  CASE WHEN dc.total_leads > 0 \n    THEN ROUND((dc.aulas_experimentais::numeric / dc.total_leads) * 100, 1) \n    ELSE 0 END as taxa_lead_exp,\n  CASE WHEN dc.aulas_experimentais > 0 \n    THEN ROUND((dc.novas_matriculas_total::numeric / dc.aulas_experimentais) * 100, 1) \n    ELSE 0 END as taxa_exp_mat,\n  CASE WHEN dc.total_leads > 0 \n    THEN ROUND((dc.novas_matriculas_total::numeric / dc.total_leads) * 100, 1) \n    ELSE 0 END as taxa_lead_mat\nFROM dados_comerciais dc\nLEFT JOIN unidades u ON UPPER(u.nome) = UPPER(dc.unidade)\nORDER BY dc.competencia DESC, dc.unidade;"}	create_vw_kpis_comercial_historico	producoes.emla@gmail.com	\N	\N
20260120121400	{"\n-- Criar tabela metas_kpi com estrutura flexível (tipo + valor)\nCREATE TABLE IF NOT EXISTS metas_kpi (\n  id SERIAL PRIMARY KEY,\n  ano INTEGER NOT NULL,\n  mes INTEGER NOT NULL,\n  unidade_id UUID REFERENCES unidades(id),\n  tipo VARCHAR(50) NOT NULL,\n  valor DECIMAL(15,2) NOT NULL,\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  updated_at TIMESTAMPTZ DEFAULT NOW(),\n  UNIQUE(ano, mes, unidade_id, tipo)\n);\n\n-- Índices para performance\nCREATE INDEX IF NOT EXISTS idx_metas_kpi_ano ON metas_kpi(ano);\nCREATE INDEX IF NOT EXISTS idx_metas_kpi_unidade ON metas_kpi(unidade_id);\nCREATE INDEX IF NOT EXISTS idx_metas_kpi_tipo ON metas_kpi(tipo);\nCREATE INDEX IF NOT EXISTS idx_metas_kpi_ano_mes ON metas_kpi(ano, mes);\n\n-- Habilitar RLS\nALTER TABLE metas_kpi ENABLE ROW LEVEL SECURITY;\n\n-- Política para leitura (todos autenticados podem ler)\nCREATE POLICY \\"Authenticated users can read metas_kpi\\" ON metas_kpi\n  FOR SELECT TO authenticated USING (true);\n\n-- Política para inserção (todos autenticados podem inserir)\nCREATE POLICY \\"Authenticated users can insert metas_kpi\\" ON metas_kpi\n  FOR INSERT TO authenticated WITH CHECK (true);\n\n-- Política para atualização (todos autenticados podem atualizar)\nCREATE POLICY \\"Authenticated users can update metas_kpi\\" ON metas_kpi\n  FOR UPDATE TO authenticated USING (true) WITH CHECK (true);\n\n-- Política para deleção (todos autenticados podem deletar)\nCREATE POLICY \\"Authenticated users can delete metas_kpi\\" ON metas_kpi\n  FOR DELETE TO authenticated USING (true);\n"}	create_metas_kpi_table	producoes.emla@gmail.com	\N	\N
