20260122180619	{"-- Tabela de Salas (cada unidade tem suas salas com capacidades diferentes)\nCREATE TABLE IF NOT EXISTS salas (\n  id SERIAL PRIMARY KEY,\n  unidade_id UUID NOT NULL REFERENCES unidades(id),\n  nome VARCHAR(100) NOT NULL,\n  codigo VARCHAR(20),\n  capacidade_maxima INTEGER NOT NULL DEFAULT 4,\n  cursos_permitidos TEXT[], -- Array de cursos que podem usar esta sala\n  descricao TEXT,\n  ativo BOOLEAN DEFAULT true,\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  updated_at TIMESTAMPTZ DEFAULT NOW(),\n  UNIQUE(unidade_id, nome)\n);\n\n-- Índices\nCREATE INDEX IF NOT EXISTS idx_salas_unidade ON salas(unidade_id);\nCREATE INDEX IF NOT EXISTS idx_salas_ativo ON salas(ativo);\n\n-- Comentários\nCOMMENT ON TABLE salas IS 'Salas de aula de cada unidade com capacidade máxima';\nCOMMENT ON COLUMN salas.capacidade_maxima IS 'Número máximo de alunos por turma nesta sala';\nCOMMENT ON COLUMN salas.cursos_permitidos IS 'Lista de cursos que podem usar esta sala (ex: Guitarra, Violão)';"}	create_salas_table	producoes.emla@gmail.com	\N	\N
20260122180631	{"-- Tabela de Turmas (combinação de professor + dia + horário + sala)\nCREATE TABLE IF NOT EXISTS turmas (\n  id SERIAL PRIMARY KEY,\n  unidade_id UUID NOT NULL REFERENCES unidades(id),\n  professor_id INTEGER NOT NULL REFERENCES professores(id),\n  sala_id INTEGER REFERENCES salas(id),\n  curso_id INTEGER REFERENCES cursos(id),\n  dia_semana VARCHAR(20) NOT NULL, -- Segunda, Terça, etc\n  horario_inicio TIME NOT NULL,\n  horario_fim TIME,\n  capacidade_maxima INTEGER DEFAULT 4,\n  nome VARCHAR(100), -- Nome opcional da turma\n  observacoes TEXT,\n  ativo BOOLEAN DEFAULT true,\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  updated_at TIMESTAMPTZ DEFAULT NOW(),\n  UNIQUE(unidade_id, professor_id, dia_semana, horario_inicio)\n);\n\n-- Índices\nCREATE INDEX IF NOT EXISTS idx_turmas_unidade ON turmas(unidade_id);\nCREATE INDEX IF NOT EXISTS idx_turmas_professor ON turmas(professor_id);\nCREATE INDEX IF NOT EXISTS idx_turmas_sala ON turmas(sala_id);\nCREATE INDEX IF NOT EXISTS idx_turmas_dia ON turmas(dia_semana);\nCREATE INDEX IF NOT EXISTS idx_turmas_ativo ON turmas(ativo);\n\n-- Comentários\nCOMMENT ON TABLE turmas IS 'Turmas de aula - combinação de professor, dia, horário e sala';\nCOMMENT ON COLUMN turmas.capacidade_maxima IS 'Capacidade máxima de alunos nesta turma (herda da sala se não definido)';"}	create_turmas_table	producoes.emla@gmail.com	\N	\N
20260126230924	{"-- Adicionar coluna fator_demanda na tabela cursos\nALTER TABLE cursos \nADD COLUMN IF NOT EXISTS fator_demanda DECIMAL(2,1) DEFAULT 1.0;\n\n-- Adicionar constraint para valores permitidos\nALTER TABLE cursos\nADD CONSTRAINT cursos_fator_demanda_check \nCHECK (fator_demanda IN (1.0, 1.5, 2.0, 2.5, 3.0));\n\n-- Comentário explicativo\nCOMMENT ON COLUMN cursos.fator_demanda IS 'Fator de demanda para Health Score (1.0=curso grande, 3.0=curso pequeno). Valores: 1.0, 1.5, 2.0, 2.5, 3.0';"}	add_fator_demanda_to_cursos	producoes.emla@gmail.com	\N	\N
20260122180640	{"-- Tabela de relacionamento Alunos x Turmas\nCREATE TABLE IF NOT EXISTS alunos_turmas (\n  id SERIAL PRIMARY KEY,\n  aluno_id INTEGER NOT NULL REFERENCES alunos(id) ON DELETE CASCADE,\n  turma_id INTEGER NOT NULL REFERENCES turmas(id) ON DELETE CASCADE,\n  data_entrada DATE DEFAULT CURRENT_DATE,\n  data_saida DATE,\n  ativo BOOLEAN DEFAULT true,\n  observacoes TEXT,\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  updated_at TIMESTAMPTZ DEFAULT NOW(),\n  UNIQUE(aluno_id, turma_id)\n);\n\n-- Índices\nCREATE INDEX IF NOT EXISTS idx_alunos_turmas_aluno ON alunos_turmas(aluno_id);\nCREATE INDEX IF NOT EXISTS idx_alunos_turmas_turma ON alunos_turmas(turma_id);\nCREATE INDEX IF NOT EXISTS idx_alunos_turmas_ativo ON alunos_turmas(ativo);\n\n-- Comentários\nCOMMENT ON TABLE alunos_turmas IS 'Relacionamento entre alunos e turmas - permite histórico de turmas';\nCOMMENT ON COLUMN alunos_turmas.data_entrada IS 'Data em que o aluno entrou na turma';\nCOMMENT ON COLUMN alunos_turmas.data_saida IS 'Data em que o aluno saiu da turma (se aplicável)';"}	create_alunos_turmas_table	producoes.emla@gmail.com	\N	\N
20260122180653	{"-- View para turmas com contagem de alunos e informações completas\nCREATE OR REPLACE VIEW vw_turmas_completa AS\nSELECT \n  t.id,\n  t.unidade_id,\n  u.nome as unidade_nome,\n  t.professor_id,\n  p.nome as professor_nome,\n  t.sala_id,\n  s.nome as sala_nome,\n  t.curso_id,\n  c.nome as curso_nome,\n  t.dia_semana,\n  t.horario_inicio,\n  t.horario_fim,\n  COALESCE(t.capacidade_maxima, s.capacidade_maxima, 4) as capacidade_maxima,\n  t.nome as turma_nome,\n  t.ativo,\n  COUNT(DISTINCT at.aluno_id) FILTER (WHERE at.ativo = true) as total_alunos,\n  ARRAY_AGG(DISTINCT a.nome) FILTER (WHERE at.ativo = true) as nomes_alunos,\n  ARRAY_AGG(DISTINCT a.id) FILTER (WHERE at.ativo = true) as ids_alunos\nFROM turmas t\nLEFT JOIN unidades u ON t.unidade_id = u.id\nLEFT JOIN professores p ON t.professor_id = p.id\nLEFT JOIN salas s ON t.sala_id = s.id\nLEFT JOIN cursos c ON t.curso_id = c.id\nLEFT JOIN alunos_turmas at ON t.id = at.turma_id\nLEFT JOIN alunos a ON at.aluno_id = a.id AND a.status = 'ativo'\nWHERE t.ativo = true\nGROUP BY t.id, t.unidade_id, u.nome, t.professor_id, p.nome, t.sala_id, s.nome, \n         t.curso_id, c.nome, t.dia_semana, t.horario_inicio, t.horario_fim, \n         t.capacidade_maxima, s.capacidade_maxima, t.nome, t.ativo;\n\n-- Comentário\nCOMMENT ON VIEW vw_turmas_completa IS 'View com informações completas das turmas incluindo contagem de alunos';"}	create_view_turmas_completa	producoes.emla@gmail.com	\N	\N
20260122180705	{"-- View para turmas implícitas (baseadas nos dados existentes de alunos)\n-- Agrupa alunos por professor + dia + horário\nCREATE OR REPLACE VIEW vw_turmas_implicitas AS\nSELECT \n  a.unidade_id,\n  u.nome as unidade_nome,\n  a.professor_atual_id as professor_id,\n  p.nome as professor_nome,\n  a.curso_id,\n  c.nome as curso_nome,\n  a.dia_aula as dia_semana,\n  a.horario_aula as horario_inicio,\n  COUNT(*) as total_alunos,\n  ARRAY_AGG(a.nome ORDER BY a.nome) as nomes_alunos,\n  ARRAY_AGG(a.id ORDER BY a.nome) as ids_alunos,\n  ROUND(AVG(a.valor_parcela)::numeric, 2) as ticket_medio_turma,\n  ROUND(AVG(a.tempo_permanencia_meses)::numeric, 1) as tempo_medio_turma\nFROM alunos a\nLEFT JOIN unidades u ON a.unidade_id = u.id\nLEFT JOIN professores p ON a.professor_atual_id = p.id\nLEFT JOIN cursos c ON a.curso_id = c.id\nWHERE a.status = 'ativo'\n  AND a.professor_atual_id IS NOT NULL\n  AND a.dia_aula IS NOT NULL\n  AND a.horario_aula IS NOT NULL\nGROUP BY a.unidade_id, u.nome, a.professor_atual_id, p.nome, \n         a.curso_id, c.nome, a.dia_aula, a.horario_aula\nORDER BY p.nome, a.dia_aula, a.horario_aula;\n\n-- Comentário\nCOMMENT ON VIEW vw_turmas_implicitas IS 'View de turmas baseada nos dados existentes de alunos (professor + dia + horário)';"}	create_view_turmas_implicitas	producoes.emla@gmail.com	\N	\N
20260122180715	{"-- Inserir salas iniciais para cada unidade\nINSERT INTO salas (unidade_id, nome, codigo, capacidade_maxima, cursos_permitidos, descricao)\nSELECT \n  u.id,\n  sala.nome,\n  sala.codigo,\n  sala.capacidade,\n  sala.cursos,\n  sala.descricao\nFROM unidades u\nCROSS JOIN (\n  VALUES \n    ('Sala 1', 'S1', 4, ARRAY['Guitarra', 'Violão', 'Baixo'], 'Sala de cordas'),\n    ('Sala 2', 'S2', 3, ARRAY['Piano', 'Teclado'], 'Sala de teclas'),\n    ('Sala 3', 'S3', 2, ARRAY['Bateria'], 'Sala de bateria'),\n    ('Sala 4', 'S4', 4, ARRAY['Canto', 'Violino'], 'Sala de canto e cordas acústicas')\n) AS sala(nome, codigo, capacidade, cursos, descricao)\nON CONFLICT (unidade_id, nome) DO NOTHING;"}	insert_salas_iniciais	producoes.emla@gmail.com	\N	\N
20260122235704	{"-- Criar tabela de turmas explícitas (turmas regulares e bandas)\nCREATE TABLE IF NOT EXISTS turmas_explicitas (\n  id SERIAL PRIMARY KEY,\n  tipo VARCHAR(10) NOT NULL CHECK (tipo IN ('turma', 'banda')),\n  nome VARCHAR(255),\n  professor_id INTEGER NOT NULL REFERENCES professores(id) ON DELETE CASCADE,\n  curso_id INTEGER REFERENCES cursos(id) ON DELETE SET NULL,\n  dia_semana VARCHAR(20) NOT NULL,\n  horario_inicio TIME NOT NULL,\n  sala_id INTEGER REFERENCES salas(id) ON DELETE SET NULL,\n  unidade_id UUID NOT NULL REFERENCES unidades(id) ON DELETE CASCADE,\n  capacidade_maxima INTEGER,\n  ativo BOOLEAN DEFAULT true,\n  created_at TIMESTAMP DEFAULT NOW(),\n  updated_at TIMESTAMP DEFAULT NOW()\n);\n\n-- Criar tabela de relacionamento entre turmas e alunos\nCREATE TABLE IF NOT EXISTS turmas_alunos (\n  id SERIAL PRIMARY KEY,\n  turma_id INTEGER NOT NULL REFERENCES turmas_explicitas(id) ON DELETE CASCADE,\n  aluno_id INTEGER NOT NULL REFERENCES alunos(id) ON DELETE CASCADE,\n  created_at TIMESTAMP DEFAULT NOW(),\n  UNIQUE(turma_id, aluno_id)\n);\n\n-- Criar índices para melhorar performance\nCREATE INDEX IF NOT EXISTS idx_turmas_explicitas_professor ON turmas_explicitas(professor_id);\nCREATE INDEX IF NOT EXISTS idx_turmas_explicitas_unidade ON turmas_explicitas(unidade_id);\nCREATE INDEX IF NOT EXISTS idx_turmas_explicitas_dia_horario ON turmas_explicitas(dia_semana, horario_inicio);\nCREATE INDEX IF NOT EXISTS idx_turmas_alunos_turma ON turmas_alunos(turma_id);\nCREATE INDEX IF NOT EXISTS idx_turmas_alunos_aluno ON turmas_alunos(aluno_id);\n\n-- Comentários nas tabelas\nCOMMENT ON TABLE turmas_explicitas IS 'Turmas explícitas criadas manualmente (turmas regulares e bandas)';\nCOMMENT ON TABLE turmas_alunos IS 'Relacionamento entre turmas explícitas e alunos';\nCOMMENT ON COLUMN turmas_explicitas.tipo IS 'Tipo da turma: turma (regular) ou banda';\nCOMMENT ON COLUMN turmas_explicitas.nome IS 'Nome da turma/banda (obrigatório apenas para bandas)';\nCOMMENT ON COLUMN turmas_explicitas.capacidade_maxima IS 'Capacidade máxima (apenas para turmas regulares)';"}	create_turmas_explicitas_and_turmas_alunos	producoes.emla@gmail.com	\N	\N
20260123095753	{"-- Adicionar novos campos à tabela salas\nALTER TABLE salas \nADD COLUMN IF NOT EXISTS tipo_sala VARCHAR(100),\nADD COLUMN IF NOT EXISTS buffer_operacional INTEGER DEFAULT 10,\nADD COLUMN IF NOT EXISTS recursos TEXT[] DEFAULT '{}',\nADD COLUMN IF NOT EXISTS sala_coringa BOOLEAN DEFAULT false;\n\n-- Comentários para documentação\nCOMMENT ON COLUMN salas.tipo_sala IS 'Tipo da sala: Piano/Teclado, Bateria/Percussão, Cordas, etc.';\nCOMMENT ON COLUMN salas.buffer_operacional IS 'Tempo mínimo em minutos entre aulas nesta sala';\nCOMMENT ON COLUMN salas.recursos IS 'Lista de recursos disponíveis na sala (Piano de Cauda, Ar Condicionado, etc.)';\nCOMMENT ON COLUMN salas.sala_coringa IS 'Se true, a sala pode ser usada para múltiplos tipos de aula';"}	add_salas_extended_fields	producoes.emla@gmail.com	\N	\N
20260123101236	{"-- Habilitar RLS na tabela salas\nALTER TABLE salas ENABLE ROW LEVEL SECURITY;\n\n-- Política de SELECT: usuários autenticados podem ver salas da sua unidade ou admins veem todas\nCREATE POLICY \\"salas_select\\" ON salas\nFOR SELECT\nTO authenticated\nUSING (\n  is_admin() OR unidade_id = get_user_unidade_id()\n);\n\n-- Política de INSERT: usuários autenticados podem criar salas na sua unidade ou admins em qualquer\nCREATE POLICY \\"salas_insert\\" ON salas\nFOR INSERT\nTO authenticated\nWITH CHECK (\n  is_admin() OR unidade_id = get_user_unidade_id()\n);\n\n-- Política de UPDATE: usuários autenticados podem atualizar salas da sua unidade ou admins qualquer\nCREATE POLICY \\"salas_update\\" ON salas\nFOR UPDATE\nTO authenticated\nUSING (\n  is_admin() OR unidade_id = get_user_unidade_id()\n)\nWITH CHECK (\n  is_admin() OR unidade_id = get_user_unidade_id()\n);\n\n-- Política de DELETE: usuários autenticados podem excluir salas da sua unidade ou admins qualquer\nCREATE POLICY \\"salas_delete\\" ON salas\nFOR DELETE\nTO authenticated\nUSING (\n  is_admin() OR unidade_id = get_user_unidade_id()\n);"}	add_salas_rls_policies	producoes.emla@gmail.com	\N	\N
20260123105713	{"-- Tabela principal de inventário\nCREATE TABLE inventario (\n  id SERIAL PRIMARY KEY,\n  codigo_patrimonio VARCHAR(50) UNIQUE,  -- Código único de patrimônio (ex: LA-BAR-001)\n  sala_id INTEGER REFERENCES salas(id),\n  unidade_id UUID REFERENCES unidades(id),\n  \n  -- Identificação do Item\n  nome VARCHAR(255) NOT NULL,\n  categoria VARCHAR(100),\n  marca VARCHAR(100),\n  modelo VARCHAR(100),\n  numero_serie VARCHAR(100),\n  \n  -- Financeiro\n  valor_compra DECIMAL(10,2),\n  data_compra DATE,\n  nota_fiscal VARCHAR(100),\n  fornecedor VARCHAR(255),\n  \n  -- Depreciação e Vida Útil\n  vida_util_meses INTEGER DEFAULT 60,\n  valor_residual DECIMAL(10,2),\n  \n  -- Status e Condição\n  status VARCHAR(50) DEFAULT 'ativo',\n  condicao VARCHAR(50) DEFAULT 'bom',\n  \n  -- Controle\n  quantidade INTEGER DEFAULT 1,\n  observacoes TEXT,\n  foto_url VARCHAR(500),\n  \n  -- Alertas\n  proxima_revisao DATE,\n  alerta_revisao_dias INTEGER DEFAULT 30,\n  \n  -- Auditoria\n  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n  updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n  created_by UUID REFERENCES auth.users(id),\n  ativo BOOLEAN DEFAULT true\n);\n\n-- Tabela de manutenções\nCREATE TABLE inventario_manutencoes (\n  id SERIAL PRIMARY KEY,\n  item_id INTEGER REFERENCES inventario(id) ON DELETE CASCADE,\n  tipo VARCHAR(50) NOT NULL,  -- preventiva, corretiva, limpeza\n  descricao TEXT,\n  custo DECIMAL(10,2),\n  data_manutencao DATE NOT NULL,\n  data_proxima_revisao DATE,\n  responsavel VARCHAR(255),\n  fornecedor_servico VARCHAR(255),\n  observacoes TEXT,\n  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n  created_by UUID REFERENCES auth.users(id)\n);\n\n-- Tabela de movimentações (histórico)\nCREATE TABLE inventario_movimentacoes (\n  id SERIAL PRIMARY KEY,\n  item_id INTEGER REFERENCES inventario(id) ON DELETE CASCADE,\n  tipo VARCHAR(50) NOT NULL,  -- entrada, saida, transferencia, baixa\n  sala_origem_id INTEGER REFERENCES salas(id),\n  sala_destino_id INTEGER REFERENCES salas(id),\n  motivo TEXT,\n  data_movimentacao TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n  usuario_id UUID REFERENCES auth.users(id)\n);\n\n-- Índices para performance\nCREATE INDEX idx_inventario_sala ON inventario(sala_id);\nCREATE INDEX idx_inventario_unidade ON inventario(unidade_id);\nCREATE INDEX idx_inventario_categoria ON inventario(categoria);\nCREATE INDEX idx_inventario_status ON inventario(status);\nCREATE INDEX idx_inventario_proxima_revisao ON inventario(proxima_revisao);\nCREATE INDEX idx_manutencoes_item ON inventario_manutencoes(item_id);\nCREATE INDEX idx_movimentacoes_item ON inventario_movimentacoes(item_id);\n\n-- Trigger para atualizar updated_at\nCREATE OR REPLACE FUNCTION update_inventario_updated_at()\nRETURNS TRIGGER AS $$\nBEGIN\n  NEW.updated_at = NOW();\n  RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql;\n\nCREATE TRIGGER trigger_inventario_updated_at\n  BEFORE UPDATE ON inventario\n  FOR EACH ROW\n  EXECUTE FUNCTION update_inventario_updated_at();"}	create_inventario_tables	producoes.emla@gmail.com	\N	\N
20260123105723	{"-- Habilitar RLS nas tabelas\nALTER TABLE inventario ENABLE ROW LEVEL SECURITY;\nALTER TABLE inventario_manutencoes ENABLE ROW LEVEL SECURITY;\nALTER TABLE inventario_movimentacoes ENABLE ROW LEVEL SECURITY;\n\n-- Políticas para inventario\nCREATE POLICY \\"inventario_select_policy\\" ON inventario\n  FOR SELECT TO authenticated\n  USING (true);\n\nCREATE POLICY \\"inventario_insert_policy\\" ON inventario\n  FOR INSERT TO authenticated\n  WITH CHECK (true);\n\nCREATE POLICY \\"inventario_update_policy\\" ON inventario\n  FOR UPDATE TO authenticated\n  USING (true)\n  WITH CHECK (true);\n\nCREATE POLICY \\"inventario_delete_policy\\" ON inventario\n  FOR DELETE TO authenticated\n  USING (true);\n\n-- Políticas para inventario_manutencoes\nCREATE POLICY \\"manutencoes_select_policy\\" ON inventario_manutencoes\n  FOR SELECT TO authenticated\n  USING (true);\n\nCREATE POLICY \\"manutencoes_insert_policy\\" ON inventario_manutencoes\n  FOR INSERT TO authenticated\n  WITH CHECK (true);\n\nCREATE POLICY \\"manutencoes_update_policy\\" ON inventario_manutencoes\n  FOR UPDATE TO authenticated\n  USING (true)\n  WITH CHECK (true);\n\nCREATE POLICY \\"manutencoes_delete_policy\\" ON inventario_manutencoes\n  FOR DELETE TO authenticated\n  USING (true);\n\n-- Políticas para inventario_movimentacoes\nCREATE POLICY \\"movimentacoes_select_policy\\" ON inventario_movimentacoes\n  FOR SELECT TO authenticated\n  USING (true);\n\nCREATE POLICY \\"movimentacoes_insert_policy\\" ON inventario_movimentacoes\n  FOR INSERT TO authenticated\n  WITH CHECK (true);\n\nCREATE POLICY \\"movimentacoes_update_policy\\" ON inventario_movimentacoes\n  FOR UPDATE TO authenticated\n  USING (true)\n  WITH CHECK (true);\n\nCREATE POLICY \\"movimentacoes_delete_policy\\" ON inventario_movimentacoes\n  FOR DELETE TO authenticated\n  USING (true);"}	create_inventario_rls_policies	producoes.emla@gmail.com	\N	\N
