20260123141120	{"-- Adicionar campo duracao_minutos na tabela turmas\nALTER TABLE turmas \nADD COLUMN IF NOT EXISTS duracao_minutos INTEGER DEFAULT 60;\n\n-- Atualizar turmas existentes: calcular duração baseada em horario_inicio e horario_fim\nUPDATE turmas \nSET duracao_minutos = EXTRACT(EPOCH FROM (horario_fim - horario_inicio)) / 60\nWHERE horario_fim IS NOT NULL AND duracao_minutos IS NULL;\n\n-- Atualizar horario_fim para turmas que não têm (baseado em duração padrão de 1h)\nUPDATE turmas \nSET horario_fim = horario_inicio + INTERVAL '1 hour'\nWHERE horario_fim IS NULL;\n\n-- Adicionar comentário na coluna\nCOMMENT ON COLUMN turmas.duracao_minutos IS 'Duração da aula em minutos (padrão: 60)';"}	add_duracao_minutos_turmas	producoes.emla@gmail.com	\N	\N
20260123221228	{"-- Tabela para histórico de mudanças em turmas (audit log)\nCREATE TABLE IF NOT EXISTS turmas_historico (\n  id SERIAL PRIMARY KEY,\n  turma_id INTEGER NOT NULL REFERENCES turmas_explicitas(id) ON DELETE CASCADE,\n  aluno_id INTEGER NOT NULL REFERENCES alunos(id) ON DELETE CASCADE,\n  acao VARCHAR(50) NOT NULL CHECK (acao IN ('adicionar', 'remover', 'mover')),\n  turma_origem_id INTEGER REFERENCES turmas_explicitas(id) ON DELETE SET NULL,\n  turma_destino_id INTEGER REFERENCES turmas_explicitas(id) ON DELETE SET NULL,\n  usuario_id UUID REFERENCES auth.users(id) ON DELETE SET NULL,\n  motivo TEXT,\n  metadata JSONB DEFAULT '{}',\n  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()\n);\n\n-- Índices para melhor performance\nCREATE INDEX IF NOT EXISTS idx_turmas_historico_turma_id ON turmas_historico(turma_id);\nCREATE INDEX IF NOT EXISTS idx_turmas_historico_aluno_id ON turmas_historico(aluno_id);\nCREATE INDEX IF NOT EXISTS idx_turmas_historico_created_at ON turmas_historico(created_at DESC);\n\n-- Comentário na tabela\nCOMMENT ON TABLE turmas_historico IS 'Histórico de mudanças em turmas: adições, remoções e movimentações de alunos';\nCOMMENT ON COLUMN turmas_historico.acao IS 'Tipo de ação: adicionar, remover ou mover';\nCOMMENT ON COLUMN turmas_historico.turma_origem_id IS 'Turma de origem (usado apenas em ação mover)';\nCOMMENT ON COLUMN turmas_historico.turma_destino_id IS 'Turma de destino (usado apenas em ação mover)';\nCOMMENT ON COLUMN turmas_historico.metadata IS 'Dados adicionais como conflitos detectados, horários, etc.';"}	create_turmas_historico_table	producoes.emla@gmail.com	\N	\N
20260123231706	{"\n-- Adicionar colunas faltantes na tabela professores (sem email/telefone)\nALTER TABLE professores ADD COLUMN IF NOT EXISTS data_admissao DATE;\nALTER TABLE professores ADD COLUMN IF NOT EXISTS comissao_percentual NUMERIC(5,2) DEFAULT 0;\nALTER TABLE professores ADD COLUMN IF NOT EXISTS observacoes TEXT;\nALTER TABLE professores ADD COLUMN IF NOT EXISTS foto_url VARCHAR(500);\n\n-- Tabela de relacionamento professor-unidade (muitos para muitos)\nCREATE TABLE IF NOT EXISTS professores_unidades (\n  id SERIAL PRIMARY KEY,\n  professor_id INTEGER NOT NULL REFERENCES professores(id) ON DELETE CASCADE,\n  unidade_id UUID NOT NULL REFERENCES unidades(id) ON DELETE CASCADE,\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  UNIQUE(professor_id, unidade_id)\n);\n\n-- Tabela de relacionamento professor-curso (especialidades)\nCREATE TABLE IF NOT EXISTS professores_cursos (\n  id SERIAL PRIMARY KEY,\n  professor_id INTEGER NOT NULL REFERENCES professores(id) ON DELETE CASCADE,\n  curso_id INTEGER NOT NULL REFERENCES cursos(id) ON DELETE CASCADE,\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  UNIQUE(professor_id, curso_id)\n);\n\n-- Índices para performance\nCREATE INDEX IF NOT EXISTS idx_professores_unidades_professor ON professores_unidades(professor_id);\nCREATE INDEX IF NOT EXISTS idx_professores_unidades_unidade ON professores_unidades(unidade_id);\nCREATE INDEX IF NOT EXISTS idx_professores_cursos_professor ON professores_cursos(professor_id);\nCREATE INDEX IF NOT EXISTS idx_professores_cursos_curso ON professores_cursos(curso_id);\nCREATE INDEX IF NOT EXISTS idx_professores_ativo ON professores(ativo);\nCREATE INDEX IF NOT EXISTS idx_professores_data_admissao ON professores(data_admissao);\n\n-- Comentários para documentação\nCOMMENT ON TABLE professores_unidades IS 'Relacionamento N:N entre professores e unidades onde atuam';\nCOMMENT ON TABLE professores_cursos IS 'Relacionamento N:N entre professores e cursos que lecionam (especialidades)';\nCOMMENT ON COLUMN professores.data_admissao IS 'Data de admissão do professor na escola';\nCOMMENT ON COLUMN professores.comissao_percentual IS 'Percentual de comissão do professor';\nCOMMENT ON COLUMN professores.foto_url IS 'URL da foto do professor no storage';\n"}	add_professor_fields_and_relations	producoes.emla@gmail.com	\N	\N
20260202103030	{"-- Função para atualizar configurações Fideliza+\nCREATE OR REPLACE FUNCTION atualizar_config_fideliza(\n  p_ano INTEGER,\n  p_campo VARCHAR(100),\n  p_valor TEXT\n)\nRETURNS JSONB\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nBEGIN\n  EXECUTE format(\n    'UPDATE programa_fideliza_config SET %I = $1, updated_at = NOW() WHERE ano = $2',\n    p_campo\n  ) USING p_valor, p_ano;\n  \n  RETURN jsonb_build_object('success', true);\nEND;\n$$;"}	programa_fideliza_function_atualizar_config	producoes.emla@gmail.com	\N	\N
