ket_medio', dm.ticket_medio,\n      'taxa_renovacao', dm.taxa_renovacao,\n      'inadimplencia', dm.inadimplencia,\n      'tempo_permanencia', dm.tempo_permanencia,\n      'reajuste_parcelas', dm.reajuste_parcelas,\n      'novas_matriculas', dm.novas_matriculas,\n      'evasoes', dm.evasoes,\n      'faturamento_estimado', dm.faturamento_estimado\n    )), '[]'::jsonb)\n    FROM dados_mensais dm\n    WHERE dm.ano = v_ano_mes_anterior AND dm.mes = v_mes_anterior\n      AND (p_unidade_id IS NULL OR dm.unidade_id = p_unidade_id)\n  ));\n\n  -- Mesmo mês ano passado\n  v_result := v_result || jsonb_build_object('mesmo_mes_ano_passado', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'alunos_pagantes', dm.alunos_pagantes,\n      'churn_rate', dm.churn_rate,\n      'ticket_medio', dm.ticket_medio,\n      'taxa_renovacao', dm.taxa_renovacao,\n      'inadimplencia', dm.inadimplencia,\n      'tempo_permanencia', dm.tempo_permanencia,\n      'reajuste_parcelas', dm.reajuste_parcelas,\n      'novas_matriculas', dm.novas_matriculas,\n      'evasoes', dm.evasoes,\n      'faturamento_estimado', dm.faturamento_estimado,\n      'saldo_liquido', dm.saldo_liquido\n    )), '[]'::jsonb)\n    FROM dados_mensais dm\n    WHERE dm.ano = p_ano - 1 AND dm.mes = p_mes\n      AND (p_unidade_id IS NULL OR dm.unidade_id = p_unidade_id)\n  ));\n\n  -- Sazonalidade histórica\n  v_result := v_result || jsonb_build_object('sazonalidade', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'ano', sub.ano,\n      'novas_matriculas', sub.novas_matriculas,\n      'evasoes', sub.evasoes,\n      'churn_rate', sub.churn_rate,\n      'saldo_liquido', sub.saldo_liquido\n    )), '[]'::jsonb)\n    FROM (\n      SELECT s.ano, s.novas_matriculas, s.evasoes, s.churn_rate, s.saldo_liquido\n      FROM vw_sazonalidade s\n      WHERE s.mes = p_mes \n        AND s.ano >= p_ano - 3 \n        AND s.ano < p_ano\n        AND (p_unidade_id IS NULL OR s.unidade = v_unidade_nome)\n      ORDER BY s.ano DESC\n    ) sub\n  ));\n\n  -- Motivos de evasão (top 5)\n  v_result := v_result || jsonb_build_object('motivos_evasao', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'motivo', sub.motivo_categoria,\n      'quantidade', sub.quantidade,\n      'percentual', sub.percentual\n    )), '[]'::jsonb)\n    FROM (\n      SELECT em.motivo_categoria, em.quantidade, em.percentual\n      FROM vw_evasoes_motivos em\n      WHERE (p_unidade_id IS NULL OR em.unidade = v_unidade_nome)\n      ORDER BY em.quantidade DESC\n      LIMIT 5\n    ) sub\n  ));\n\n  -- Top 3 professores retenção\n  v_result := v_result || jsonb_build_object('top_professores_retencao', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'professor', sub.professor,\n      'tempo_medio_permanencia', sub.tempo_medio_permanencia,\n      'presenca_media', sub.presenca_media,\n      'total_alunos', sub.total_alunos\n    )), '[]'::jsonb)\n    FROM (\n      SELECT pr.professor, pr.tempo_medio_permanencia, pr.presenca_media, pr.total_alunos\n      FROM vw_ranking_professores_retencao pr\n      WHERE (p_unidade_id IS NULL OR pr.unidade = v_unidade_nome)\n      ORDER BY pr.tempo_medio_permanencia::numeric DESC\n      LIMIT 3\n    ) sub\n  ));\n\n  -- Top 3 professores matriculadores\n  v_result := v_result || jsonb_build_object('top_professores_matriculadores', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'professor_nome', sub.professor_nome,\n      'matriculas', sub.matriculas,\n      'experimentais', sub.experimentais,\n      'taxa_conversao', sub.taxa_conversao\n    )), '[]'::jsonb)\n    FROM (\n      SELECT pm.professor_nome, pm.matriculas, pm.experimentais, pm.taxa_conversao\n      FROM vw_kpis_professor_mensal pm\n      WHERE pm.ano = p_ano AND pm.mes = p_mes\n        AND (p_unidade_id IS NULL OR pm.unidade_id = p_unidade_id)\n        AND pm.matriculas > 0\n      ORDER BY pm.matriculas DESC NULLS LAST, pm.taxa_conversao DESC NULLS LAST\n      LIMIT 3\n    ) sub\n  ));\n\n  -- Top 3 professores presença\n  v_result := v_result || jsonb_build_object('top_professores_presenca', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'professor', sub.professor,\n      'presenca_media', sub.presenca_media,\n      'total_alunos', sub.total_alunos\n    )), '[]'::jsonb)\n    FROM (\n      SELECT pr.professor, pr.presenca_media, pr.total_alunos\n      FROM vw_ranking_professores_retencao pr\n      WHERE (p_unidade_id IS NULL OR pr.unidade = v_unidade_nome)\n        AND pr.total_alunos >= 5\n      ORDER BY pr.presenca_media::numeric DESC\n      LIMIT 3\n    ) sub\n  ));\n\n  -- Top 3 professores média de alunos por turma\n  v_result := v_result || jsonb_build_object('top_professores_media_turma', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'professor', sub.professor,\n      'media_alunos_turma', sub.media_alunos_turma,\n      'total_turmas', sub.total_turmas,\n      'total_alunos', sub.total_alunos\n    )), '[]'::jsonb)\n    FROM (\n      WITH turmas_professor AS (\n        SELECT \n          p.id as professor_id,\n          p.nome as professor,\n          a.dia_aula,\n          a.horario_aula,\n          COUNT(*) as alunos_na_turma\n        FROM professores p\n        INNER JOIN alunos a ON a.professor_atual_id = p.id\n        WHERE a.status = 'ativo'\n          AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n          AND a.dia_aula IS NOT NULL\n          AND a.horario_aula IS NOT NULL\n        GROUP BY p.id, p.nome, a.dia_aula, a.horario_aula\n      ),\n      media_por_professor AS (\n        SELECT \n          professor_id,\n          professor,\n          COUNT(*) as total_turmas,\n          SUM(alunos_na_turma) as total_alunos,\n          ROUND(AVG(alunos_na_turma), 1) as media_alunos_turma\n        FROM turmas_professor\n        GROUP BY professor_id, professor\n      )\n      SELECT \n        professor,\n        media_alunos_turma,\n        total_turmas,\n        total_alunos\n      FROM media_por_professor\n      WHERE total_turmas > 0\n      ORDER BY media_alunos_turma DESC\n      LIMIT 3\n    ) sub\n  ));\n\n  -- Cursos mais procurados (top 5)\n  v_result := v_result || jsonb_build_object('cursos_mais_procurados', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'curso', sub.curso,\n      'total_alunos', sub.total_alunos\n    )), '[]'::jsonb)\n    FROM (\n      SELECT c.nome as curso, COUNT(*) as total_alunos\n      FROM alunos a\n      JOIN cursos c ON a.curso_id = c.id\n      WHERE a.status = 'ativo'\n        AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n      GROUP BY c.nome\n      ORDER BY COUNT(*) DESC\n      LIMIT 5\n    ) sub\n  ));\n\n  -- Canais com maior conversão (top 3)\n  v_result := v_result || jsonb_build_object('canais_maior_conversao', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'canal', sub.canal,\n      'total_leads', sub.total_leads,\n      'matriculas', sub.matriculas,\n      'taxa_conversao', sub.taxa_conversao\n    )), '[]'::jsonb)\n    FROM (\n      SELECT \n        co.nome as canal,\n        COUNT(*) as total_leads,\n        SUM(CASE WHEN l.converteu = true THEN 1 ELSE 0 END) as matriculas,\n        ROUND(SUM(CASE WHEN l.converteu = true THEN 1 ELSE 0 END)::numeric / NULLIF(COUNT(*), 0) * 100, 1) as taxa_conversao\n      FROM leads l\n      JOIN canais_origem co ON l.canal_origem_id = co.id\n      WHERE l.data_contato >= (CURRENT_DATE - INTERVAL '12 months')\n        AND (p_unidade_id IS NULL OR l.unidade_id = p_unidade_id)\n      GROUP BY co.nome\n      HAVING COUNT(*) >= 5\n      ORDER BY ROUND(SUM(CASE WHEN l.converteu = true THEN 1 ELSE 0 END)::numeric / NULLIF(COUNT(*), 0) * 100, 1) DESC NULLS LAST\n      LIMIT 3\n    ) sub\n  ));\n\n  -- Total indicações no mês\n  v_result := v_result || jsonb_build_object('total_indicacoes', (\n    SELECT COUNT(*)\n    FROM leads l\n    JOIN canais_origem co ON l.canal_origem_id = co.id\n    WHERE co.nome = 'Indicação'\n      AND l.converteu = true\n      AND EXTRACT(YEAR FROM l.data_conversao) = p_ano\n      AND EXTRACT(MONTH FROM l.data_conversao) = p_mes\n      AND (p_unidade_id IS NULL OR l.unidade_id = p_unidade_id)\n  ));\n\n  -- Total pacotes Family no mês\n  v_result := v_result || jsonb_build_object('total_family_pacotes', (\n    SELECT COUNT(*)\n    FROM alunos a\n    WHERE a.status = 'ativo'\n      AND a.is_segundo_curso = true\n      AND a.data_matricula >= DATE_TRUNC('month', MAKE_DATE(p_ano, p_mes, 1))\n      AND a.data_matricula < DATE_TRUNC('month', MAKE_DATE(p_ano, p_mes, 1)) + INTERVAL '1 month'\n      AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n  ));\n\n  -- Permanência por faixa\n  v_result := v_result || jsonb_build_object('permanencia_por_faixa', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'faixa', sub.faixa,\n      'quantidade', sub.quantidade,\n      'percentual', sub.percentual\n    ) ORDER BY sub.ordem), '[]'::jsonb)\n    FROM (\n      SELECT \n        CASE \n          WHEN a.tempo_permanencia_meses < 6 THEN '0-6 meses'\n          WHEN a.tempo_permanencia_meses < 12 THEN '6-12 meses'\n          WHEN a.tempo_permanencia_meses < 24 THEN '1-2 anos'\n          WHEN a.tempo_permanencia_meses < 36 THEN '2-3 anos'\n          ELSE '3+ anos'\n        END as faixa,\n        CASE \n          WHEN a.tempo_permanencia_meses < 6 THEN 1\n          WHEN a.tempo_permanencia_meses < 12 THEN 2\n          WHEN a.tempo_permanencia_meses < 24 THEN 3\n          WHEN a.tempo_permanencia_meses < 36 THEN 4\n          ELSE 5\n        END as ordem,\n        COUNT(*)::INTEGER as quantidade,\n        ROUND(COUNT(*) * 100.0 / NULLIF(SUM(COUNT(*)) OVER(), 0), 1)::NUMERIC as percentual\n      FROM alunos a\n      WHERE a.status = 'ativo' AND a.classificacao = 'pagante'\n        AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n      GROUP BY faixa, ordem\n    ) sub\n  ));\n\n  -- Dados do mês atual\n  v_result := v_result || jsonb_build_object('dados_mes_atual', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'alunos_pagantes', dm.alunos_pagantes,\n      'churn_rate', dm.churn_rate,\n      'ticket_medio', dm.ticket_medio,\n      'taxa_renovacao', dm.taxa_renovacao,\n      'inadimplencia', dm.inadimplencia,\n      'tempo_permanencia', dm.tempo_permanencia,\n      'reajuste_parcelas', dm.reajuste_parcelas,\n      'novas_matriculas', dm.novas_matriculas,\n      'evasoes', dm.evasoes,\n      'faturamento_estimado', dm.faturamento_estimado,\n      'saldo_liquido', dm.saldo_liquido\n    )), '[]'::jsonb)\n    FROM dados_mensais dm\n    WHERE dm.ano = p_ano AND dm.mes = p_mes\n      AND (p_unidade_id IS NULL OR dm.unidade_id = p_unidade_id)\n  ));\n\n  RETURN v_result;\nEND;\n$function$;"}	add_top_media_alunos_turma_to_relatorio	producoes.emla@gmail.com	\N	\N
20260126152055	{"-- Corrigir busca de metas no relatório gerencial - usar metas_kpi\nCREATE OR REPLACE FUNCTION public.get_dados_relatorio_gerencial(\n  p_unidade_id uuid DEFAULT NULL::uuid, \n  p_ano integer DEFAULT (EXTRACT(year FROM CURRENT_DATE))::integer, \n  p_mes integer DEFAULT (EXTRACT(month FROM CURRENT_DATE))::integer\n)\nRETURNS jsonb\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $function$\nDECLARE\n  v_result JSONB;\n  v_mes_anterior INTEGER;\n  v_ano_mes_anterior INTEGER;\n  v_unidade_nome TEXT;\n  v_gerente_nome TEXT;\n  v_hunter_nome TEXT;\n  v_farmers_nomes TEXT[];\nBEGIN\n  -- Calcular mês anterior\n  IF p_mes = 1 THEN\n    v_mes_anterior := 12;\n    v_ano_mes_anterior := p_ano - 1;\n  ELSE\n    v_mes_anterior := p_mes - 1;\n    v_ano_mes_anterior := p_ano;\n  END IF;\n\n  -- Buscar dados da unidade\n  IF p_unidade_id IS NOT NULL THEN\n    SELECT nome, gerente_nome, hunter_nome, farmers_nomes \n    INTO v_unidade_nome, v_gerente_nome, v_hunter_nome, v_farmers_nomes \n    FROM unidades WHERE id = p_unidade_id;\n  ELSE\n    v_unidade_nome := 'Consolidado';\n    v_gerente_nome := 'Diretoria';\n    v_hunter_nome := 'Equipe Comercial';\n    v_farmers_nomes := ARRAY['Equipe Administrativa'];\n  END IF;\n\n  -- Construir resultado base\n  v_result := jsonb_build_object(\n    'periodo', jsonb_build_object(\n      'ano', p_ano,\n      'mes', p_mes,\n      'mes_nome', TO_CHAR(TO_DATE(p_mes::TEXT, 'MM'), 'TMMonth'),\n      'unidade_id', p_unidade_id,\n      'unidade_nome', v_unidade_nome\n    ),\n    'gerente_nome', COALESCE(v_gerente_nome, 'N/D'),\n    'hunter_nome', COALESCE(v_hunter_nome, 'N/D'),\n    'farmers_nomes', COALESCE(v_farmers_nomes, ARRAY['N/D'])\n  );\n\n  -- KPIs de Gestão\n  v_result := v_result || jsonb_build_object('kpis_gestao', (\n    SELECT COALESCE(jsonb_agg(row_to_json(kg)::jsonb), '[]'::jsonb)\n    FROM vw_kpis_gestao_mensal kg\n    WHERE (p_unidade_id IS NULL OR kg.unidade_id = p_unidade_id)\n  ));\n\n  -- KPIs de Retenção\n  v_result := v_result || jsonb_build_object('kpis_retencao', (\n    SELECT COALESCE(jsonb_agg(row_to_json(kr)::jsonb), '[]'::jsonb)\n    FROM vw_kpis_retencao_mensal kr\n    WHERE (p_unidade_id IS NULL OR kr.unidade_id = p_unidade_id)\n  ));\n\n  -- KPIs Comerciais\n  v_result := v_result || jsonb_build_object('kpis_comercial', (\n    SELECT COALESCE(jsonb_agg(row_to_json(kc)::jsonb), '[]'::jsonb)\n    FROM vw_kpis_comercial_mensal kc\n    WHERE kc.ano = p_ano AND kc.mes = p_mes\n      AND (p_unidade_id IS NULL OR kc.unidade_id = p_unidade_id)\n  ));\n\n  -- Metas do mês (da tabela metas_kpi - CORRIGIDO!)\n  v_result := v_result || jsonb_build_object('metas_kpi', (\n    SELECT COALESCE(jsonb_object_agg(mk.tipo, mk.valor), '{}'::jsonb)\n    FROM metas_kpi mk\n    WHERE mk.ano = p_ano AND mk.mes = p_mes\n      AND (p_unidade_id IS NULL OR mk.unidade_id = p_unidade_id)\n  ));\n\n  -- Matrículas detalhadas\n  v_result := v_result || jsonb_build_object(\n    'matriculas_ativas', (\n      SELECT COUNT(*) FROM alunos a \n      WHERE a.status = 'ativo' \n        AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n    ),\n    'matriculas_banda', (\n      SELECT COUNT(*) FROM alunos a \n      JOIN cursos c ON a.curso_id = c.id \n      WHERE a.status = 'ativo' AND c.nome ILIKE '%banda%'\n        AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n    ),\n    'matriculas_2_curso', (\n      SELECT COUNT(*) FROM alunos a \n      WHERE a.status = 'ativo' AND a.is_segundo_curso = true\n        AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n    ),\n    'total_bolsistas', (\n      SELECT COUNT(*) FROM alunos a \n      WHERE a.status = 'ativo' AND a.classificacao IN ('bolsista_integral', 'bolsista_parcial')\n        AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n    )\n  );\n\n  -- Dados do mês anterior\n  v_result := v_result || jsonb_build_object('mes_anterior', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'alunos_pagantes', dm.alunos_pagantes,\n      'churn_rate', dm.churn_rate,\n      'ticket_medio', dm.ticket_medio,\n      'taxa_renovacao', dm.taxa_renovacao,\n      'inadimplencia', dm.inadimplencia,\n      'tempo_permanencia', dm.tempo_permanencia,\n      'reajuste_parcelas', dm.reajuste_parcelas,\n      'novas_matriculas', dm.novas_matriculas,\n      'evasoes', dm.evasoes,\n      'faturamento_estimado', dm.faturamento_estimado\n    )), '[]'::jsonb)\n    FROM dados_mensais dm\n    WHERE dm.ano = v_ano_mes_anterior AND dm.mes = v_mes_anterior\n      AND (p_unidade_id IS NULL OR dm.unidade_id = p_unidade_id)\n  ));\n\n  -- Mesmo mês ano passado\n  v_result := v_result || jsonb_build_object('mesmo_mes_ano_passado', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'alunos_pagantes', dm.alunos_pagantes,\n      'churn_rate', dm.churn_rate,\n      'ticket_medio', dm.ticket_medio,\n      'taxa_renovacao', dm.taxa_renovacao,\n      'inadimplencia', dm.inadimplencia,\n      'tempo_permanencia', dm.tempo_permanencia,\n      'reajuste_parcelas', dm.reajuste_parcelas,\n      'novas_matriculas', dm.novas_matriculas,\n      'evasoes', dm.evasoes,\n      'faturamento_estimado', dm.faturamento_estimado,\n      'saldo_liquido', dm.saldo_liquido\n    )), '[]'::jsonb)\n    FROM dados_mensais dm\n    WHERE dm.ano = p_ano - 1 AND dm.mes = p_mes\n      AND (p_unidade_id IS NULL OR dm.unidade_id = p_unidade_id)\n  ));\n\n  -- Sazonalidade histórica\n  v_result := v_result || jsonb_build_object('sazonalidade', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'ano', sub.ano,\n      'novas_matriculas', sub.novas_matriculas,\n      'evasoes', sub.evasoes,\n      'churn_rate', sub.churn_rate,\n      'saldo_liquido', sub.saldo_liquido\n    )), '[]'::jsonb)\n    FROM (\n      SELECT s.ano, s.novas_matriculas, s.evasoes, s.churn_rate, s.saldo_liquido\n      FROM vw_sazonalidade s\n      WHERE s.mes = p_mes \n        AND s.ano >= p_ano - 3 \n        AND s.ano < p_ano\n        AND (p_unidade_id IS NULL OR s.unidade = v_unidade_nome)\n      ORDER BY s.ano DESC\n    ) sub\n  ));\n\n  -- Motivos de evasão (top 5)\n  v_result := v_result || jsonb_build_object('motivos_evasao', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'motivo', sub.motivo_categoria,\n      'quantidade', sub.quantidade,\n      'percentual', sub.percentual\n    )), '[]'::jsonb)\n    FROM (\n      SELECT em.motivo_categoria, em.quantidade, em.percentual\n      FROM vw_evasoes_motivos em\n      WHERE (p_unidade_id IS NULL OR em.unidade = v_unidade_nome)\n      ORDER BY em.quantidade DESC\n      LIMIT 5\n    ) sub\n  ));\n\n  -- Top 3 professores retenção\n  v_result := v_result || jsonb_build_object('top_professores_retencao', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'professor', sub.professor,\n      'tempo_medio_permanencia', sub.tempo_medio_permanencia,\n      'presenca_media', sub.presenca_media,\n      'total_alunos', sub.total_alunos\n    )), '[]'::jsonb)\n    FROM (\n      SELECT pr.professor, pr.tempo_medio_permanencia, pr.presenca_media, pr.total_alunos\n      FROM vw_ranking_professores_retencao pr\n      WHERE (p_unidade_id IS NULL OR pr.unidade = v_unidade_nome)\n      ORDER BY pr.tempo_medio_permanencia::numeric DESC\n      LIMIT 3\n    ) sub\n  ));\n\n  -- Top 3 professores matriculadores\n  v_result := v_result || jsonb_build_object('top_professores_matriculadores', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'professor_nome', sub.professor_nome,\n      'matriculas', sub.matriculas,\n      'experimentais', sub.experimentais,\n      'taxa_conversao', sub.taxa_conversao\n    )), '[]'::jsonb)\n    FROM (\n      SELECT pm.professor_nome, pm.matriculas, pm.experimentais, pm.taxa_conversao\n      FROM vw_kpis_professor_mensal pm\n      WHERE pm.ano = p_ano AND pm.mes = p_mes\n        AND (p_unidade_id IS NULL OR pm.unidade_id = p_unidade_id)\n        AND pm.matriculas > 0\n      ORDER BY pm.matriculas DESC NULLS LAST, pm.taxa_conversao DESC NULLS LAST\n      LIMIT 3\n    ) sub\n  ));\n\n  -- Top 3 professores presença\n  v_result := v_result || jsonb_build_object('top_professores_presenca', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'professor', sub.professor,\n      'presenca_media', sub.presenca_media,\n      'total_alunos', sub.total_alunos\n    )), '[]'::jsonb)\n    FROM (\n      SELECT pr.professor, pr.presenca_media, pr.total_alunos\n      FROM vw_ranking_professores_retencao pr\n      WHERE (p_unidade_id IS NULL OR pr.unidade = v_unidade_nome)\n        AND pr.total_alunos >= 5\n      ORDER BY pr.presenca_media::numeric DESC\n      LIMIT 3\n    ) sub\n  ));\n\n  -- Top 3 professores média de alunos por turma\n  v_result := v_result || jsonb_build_object('top_professores_media_turma', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'professor', sub.professor,\n      'media_alunos_turma', sub.media_alunos_turma,\n      'total_turmas', sub.total_turmas,\n      'total_alunos', sub.total_alunos\n    )), '[]'::jsonb)\n    FROM (\n      WITH turmas_professor AS (\n        SELECT \n          p.id as professor_id,\n          p.nome as professor,\n          a.dia_aula,\n          a.horario_aula,\n          COUNT(*) as alunos_na_turma\n        FROM professores p\n        INNER JOIN alunos a ON a.professor_atual_id = p.id\n        WHERE a.status = 'ativo'\n          AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n          AND a.dia_aula IS NOT NULL\n          AND a.horario_aula IS NOT NULL\n        GROUP BY p.id, p.nome, a.dia_aula, a.horario_aula\n      ),\n      media_por_professor AS (\n        SELECT \n          professor_id,\n          professor,\n          COUNT(*) as total_turmas,\n          SUM(alunos_na_turma) as total_alunos,\n          ROUND(AVG(alunos_na_turma), 1) as media_alunos_turma\n        FROM turmas_professor\n        GROUP BY professor_id, professor\n      )\n      SELECT \n        professor,\n        media_alunos_turma,\n        total_turmas,\n        total_alunos\n      FROM media_por_professor\n      WHERE total_turmas > 0\n      ORDER BY media_alunos_turma DESC\n      LIMIT 3\n    ) sub\n  ));\n\n  -- Cursos mais procurados (top 5)\n  v_result := v_result || jsonb_build_object('cursos_mais_procurados', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'curso', sub.curso,\n      'total_alunos', sub.total_alunos\n    )), '[]'::jsonb)\n    FROM (\n      SELECT c.nome as curso, COUNT(*) as total_alunos\n      FROM alunos a\n      JOIN cursos c ON a.curso_id = c.id\n      WHERE a.status = 'ativo'\n        AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n      GROUP BY c.nome\n      ORDER BY COUNT(*) DESC\n      LIMIT 5\n    ) sub\n  ));\n\n  -- Canais com maior conversão (top 3)\n  v_result := v_result || jsonb_build_object('canais_maior_conversao', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'canal', sub.canal,\n      'total_leads', sub.total_leads,\n      'matriculas', sub.matriculas,\n      'taxa_conversao', sub.taxa_conversao\n    )), '[]'::jsonb)\n    FROM (\n      SELECT \n        co.nome as canal,\n        COUNT(*) as total_leads,\n        SUM(CASE WHEN l.converteu = true THEN 1 ELSE 0 END) as matriculas,\n        ROUND(SUM(CASE WHEN l.converteu = true THEN 1 ELSE 0 END)::numeric / NULLIF(COUNT(*), 0) * 100, 1) as taxa_conversao\n      FROM leads l\n      JOIN canais_origem co ON l.canal_origem_id = co.id\n      WHERE l.data_contato >= (CURRENT_DATE - INTERVAL '12 months')\n        AND (p_unidade_id IS NULL OR l.unidade_id = p_unidade_id)\n      GROUP BY co.nome\n      HAVING COUNT(*) >= 5\n      ORDER BY ROUND(SUM(CASE WHEN l.converteu = true THEN 1 ELSE 0 END)::numeric / NULLIF(COUNT(*), 0) * 100, 1) DESC NULLS LAST\n      LIMIT 3\n    ) sub\n  ));\n\n  -- Total indicações no mês\n  v_result := v_result || jsonb_build_object('total_indicacoes', (\n    SELECT COUNT(*)\n    FROM leads l\n    JOIN canais_origem co ON l.canal_origem_id = co.id\n    WHERE co.nome = 'Indicação'\n      AND l.converteu = true\n      AND EXTRACT(YEAR FROM l.data_conversao) = p_ano\n      AND EXTRACT(MONTH FROM l.data_conversao) = p_mes\n      AND (p_unidade_id IS NULL OR l.unidade_id = p_unidade_id)\n  ));\n\n  -- Total pacotes Family no mês\n  v_result := v_result || jsonb_build_object('total_family_pacotes', (\n    SELECT COUNT(*)\n    FROM alunos a\n    WHERE a.status = 'ativo'\n      AND a.is_segundo_curso = true\n      AND a.data_matricula >= DATE_TRUNC('month', MAKE_DATE(p_ano, p_mes, 1))\n      AND a.data_matricula < DATE_TRUNC('month', MAKE_DATE(p_ano, p_mes, 1)) + INTERVAL '1 month'\n      AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n  ));\n\n  -- Permanência por faixa\n  v_result := v_result || jsonb_build_object('permanencia_por_faixa', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'faixa', sub.faixa,\n      'quantidade', sub.quantidade,\n      'percentual', sub.percentual\n    ) ORDER BY sub.ordem), '[]'::jsonb)\n    FROM (\n      SELECT \n        CASE \n          WHEN a.tempo_permanencia_meses < 6 THEN '0-6 meses'\n          WHEN a.tempo_permanencia_meses < 12 THEN '6-12 meses'\n          WHEN a.tempo_permanencia_meses < 24 THEN '1-2 anos'\n          WHEN a.tempo_permanencia_meses < 36 THEN '2-3 anos'\n          ELSE '3+ anos'\n        END as faixa,\n        CASE \n          WHEN a.tempo_permanencia_meses < 6 THEN 1\n          WHEN a.tempo_permanencia_meses < 12 THEN 2\n          WHEN a.tempo_permanencia_meses < 24 THEN 3\n          WHEN a.tempo_permanencia_meses < 36 THEN 4\n          ELSE 5\n        END as ordem,\n        COUNT(*)::INTEGER as quantidade,\n        ROUND(COUNT(*) * 100.0 / NULLIF(SUM(COUNT(*)) OVER(), 0), 1)::NUMERIC as percentual\n      FROM alunos a\n      WHERE a.status = 'ativo' AND a.classificacao = 'pagante'\n        AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n      GROUP BY faixa, ordem\n    ) sub\n  ));\n\n  -- Dados do mês atual\n  v_result := v_result || jsonb_build_object('dados_mes_atual', (\n    SELECT COALESCE(jsonb_agg(jsonb_build_object(\n      'alunos_pagantes', dm.alunos_pagantes,\n      'churn_rate', dm.churn_rate,\n      'ticket_medio', dm.ticket_medio,\n      'taxa_renovacao', dm.taxa_renovacao,\n      'inadimplencia', dm.inadimplencia,\n      'tempo_permanencia', dm.tempo_permanencia,\n      'reajuste_parcelas', dm.reajuste_parcelas,\n      'novas_matriculas', dm.novas_matriculas,\n      'evasoes', dm.evasoes,\n      'faturamento_estimado', dm.faturamento_estimado,\n      'saldo_liquido', dm.saldo_liquido\n    )), '[]'::jsonb)\n    FROM dados_mensais dm\n    WHERE dm.ano = p_ano AND dm.mes = p_mes\n      AND (p_unidade_id IS NULL OR dm.unidade_id = p_unidade_id)\n  ));\n\n  RETURN v_result;\nEND;\n$function$;"}	fix_metas_relatorio_gerencial	producoes.emla@gmail.com	\N	\N
20260126160157	{"\n-- Função para buscar dados do relatório de coordenação pedagógica\nCREATE OR REPLACE FUNCTION get_dados_relatorio_coordenacao(\n  p_unidade_id UUID,\n  p_ano INTEGER,\n  p_mes INTEGER\n)\nRETURNS JSONB\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  v_result JSONB := '{}'::jsonb;\n  v_unidade_nome TEXT;\nBEGIN\n  -- Buscar nome da unidade\n  IF p_unidade_id IS NOT NULL THEN\n    SELECT nome INTO v_unidade_nome FROM unidades WHERE id = p_unidade_id;\n  ELSE\n    v_unidade_nome := 'Consolidado';\n  END IF;\n\n  -- Período\n  v_result := v_result || jsonb_build_object('periodo', jsonb_build_object(\n    'unidade_id', p_unidade_id,\n    'unidade_nome', v_unidade_nome,\n    'ano', p_ano,\n    'mes', p_mes,\n    'coordenadores', ARRAY['Quintela', 'Juliana']\n  ));\n\n  -- KPIs consolidados dos professores\n  v_result := v_result || jsonb_build_object('kpis_professores', (\n    SELECT COALESCE(jsonb_agg(row_to_json(k)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_id,\n        v.professor_nome,\n        v.carteira_alunos,\n        v.media_alunos_turma,\n        v.media_presenca,\n        v.nps_medio,\n        v.taxa_conversao,\n        v.taxa_renovacao,\n        v.evasoes,\n        v.matriculas,\n        v.mrr_carteira,\n        v.ranking_matriculador,\n        v.ranking_renovador,\n        v.ranking_churn,\n        -- Cursos do professor\n        (SELECT COALESCE(array_agg(DISTINCT c.nome), ARRAY[]::text[])\n         FROM professores_cursos pc\n         JOIN cursos c ON c.id = pc.curso_id\n         WHERE pc.professor_id = v.professor_id) as cursos\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n      ORDER BY v.carteira_alunos DESC\n    ) k\n  ));\n\n  -- Totais consolidados\n  v_result := v_result || jsonb_build_object('totais', (\n    SELECT row_to_json(t)\n    FROM (\n      SELECT \n        COUNT(DISTINCT v.professor_id) as total_professores,\n        SUM(v.carteira_alunos) as total_alunos,\n        ROUND(AVG(v.carteira_alunos), 1) as media_alunos_professor,\n        ROUND(AVG(NULLIF(v.media_alunos_turma, 0)), 2) as media_alunos_turma,\n        ROUND(AVG(v.media_presenca), 1) as media_presenca,\n        ROUND(AVG(NULLIF(v.nps_medio, 0)), 1) as nps_medio,\n        ROUND(AVG(v.taxa_conversao), 1) as taxa_conversao_media,\n        ROUND(AVG(v.taxa_renovacao), 1) as taxa_renovacao_media,\n        SUM(v.evasoes) as total_evasoes,\n        SUM(v.matriculas) as total_matriculas,\n        SUM(v.mrr_carteira) as mrr_total\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n    ) t\n  ));\n\n  -- Top 5 professores por carteira\n  v_result := v_result || jsonb_build_object('top_carteira', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_nome as professor,\n        v.carteira_alunos as alunos\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n      ORDER BY v.carteira_alunos DESC\n      LIMIT 5\n    ) t\n  ));\n\n  -- Top 5 professores por média de alunos/turma\n  v_result := v_result || jsonb_build_object('top_media_turma', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_nome as professor,\n        v.media_alunos_turma as media,\n        (SELECT COUNT(*) FROM turmas tu WHERE tu.professor_id = v.professor_id AND tu.ativo = true) as turmas\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n        AND v.media_alunos_turma > 0\n      ORDER BY v.media_alunos_turma DESC\n      LIMIT 5\n    ) t\n  ));\n\n  -- Top 5 professores por presença\n  v_result := v_result || jsonb_build_object('top_presenca', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_nome as professor,\n        v.media_presenca as presenca\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n      ORDER BY v.media_presenca DESC\n      LIMIT 5\n    ) t\n  ));\n\n  -- Top 5 professores matriculadores\n  v_result := v_result || jsonb_build_object('top_matriculadores', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_nome as professor,\n        v.matriculas\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n        AND v.matriculas > 0\n      ORDER BY v.matriculas DESC\n      LIMIT 5\n    ) t\n  ));\n\n  -- Top 5 professores por retenção (tempo médio de permanência dos alunos)\n  v_result := v_result || jsonb_build_object('top_retencao', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        p.nome as professor,\n        ROUND(AVG(a.tempo_permanencia_meses), 1) as tempo_medio\n      FROM alunos a\n      JOIN professores p ON a.professor_atual_id = p.id\n      WHERE a.status = 'ativo'\n        AND (p_unidade_id IS NULL OR a.unidade_id = p_unidade_id)\n      GROUP BY p.id, p.nome\n      HAVING COUNT(*) >= 3\n      ORDER BY AVG(a.tempo_permanencia_meses) DESC\n      LIMIT 5\n    ) t\n  ));\n\n  -- Professores em alerta (baixa presença ou evasões)\n  v_result := v_result || jsonb_build_object('professores_alerta', (\n    SELECT COALESCE(jsonb_agg(row_to_json(t)), '[]'::jsonb)\n    FROM (\n      SELECT \n        v.professor_id,\n        v.professor_nome as professor,\n        v.media_presenca as presenca,\n        v.evasoes,\n        v.carteira_alunos as alunos,\n        CASE \n          WHEN v.media_presenca < 70 OR v.evasoes > 2 THEN 'critico'\n          WHEN v.media_presenca < 80 OR v.evasoes > 0 THEN 'atencao'\n          ELSE 'ok'\n        END as status\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n        AND (v.media_presenca < 80 OR v.evasoes > 0)\n      ORDER BY v.media_presenca ASC\n    ) t\n  ));\n\n  -- Agendamentos/Treinamentos do mês\n  v_result := v_result || jsonb_build_object('agenda', (\n    SELECT row_to_json(a)\n    FROM (\n      SELECT \n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.tipo = 'treinamento' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as treinamentos_agendados,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.tipo = 'reuniao' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as reunioes_agendadas,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.tipo = 'checkpoint' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as checkpoints_agendados,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.status = 'concluido' \n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)\n           AND EXTRACT(YEAR FROM pa.data_agendada) = p_ano \n           AND EXTRACT(MONTH FROM pa.data_agendada) = p_mes) as concluidos,\n        (SELECT COUNT(*) FROM professor_acoes pa \n         WHERE pa.status = 'pendente' \n           AND pa.data_agendada < CURRENT_DATE\n           AND (p_unidade_id IS NULL OR pa.unidade_id = p_unidade_id)) as atrasados\n    ) a\n  ));\n\n  -- Catálogo de treinamentos disponíveis\n  v_result := v_result || jsonb_build_object('catalogo_treinamentos', (\n    SELECT COALESCE(jsonb_agg(row_to_json(ct)), '[]'::jsonb)\n    FROM (\n      SELECT id, nome, descricao\n      FROM catalogo_treinamentos\n      WHERE ativo = true\n      ORDER BY nome\n    ) ct\n  ));\n\n  -- Metas de professores (da tabela metas_kpi com tipo de professor)\n  v_result := v_result || jsonb_build_object('metas_professores', (\n    SELECT COALESCE(jsonb_object_agg(mk.tipo, mk.valor), '{}'::jsonb)\n    FROM metas_kpi mk\n    WHERE mk.ano = p_ano AND mk.mes = p_mes\n      AND (p_unidade_id IS NULL OR mk.unidade_id = p_unidade_id)\n      AND mk.tipo IN ('media_alunos_turma', 'media_alunos_professor', 'taxa_renovacao_prof', \n                      'nps_medio', 'presenca_media', 'taxa_conversao_exp', 'melhor_retencao')\n  ));\n\n  -- Comparativo com mês anterior\n  v_result := v_result || jsonb_build_object('mes_anterior', (\n    SELECT row_to_json(t)\n    FROM (\n      SELECT \n        COUNT(DISTINCT v.professor_id) as total_professores,\n        SUM(v.carteira_alunos) as total_alunos,\n        ROUND(AVG(v.media_presenca), 1) as media_presenca,\n        SUM(v.evasoes) as total_evasoes,\n        SUM(v.matriculas) as total_matriculas\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = CASE WHEN p_mes = 1 THEN p_ano - 1 ELSE p_ano END\n        AND v.mes = CASE WHEN p_mes = 1 THEN 12 ELSE p_mes - 1 END\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n    ) t\n  ));\n\n  -- Comparativo com mesmo mês ano passado\n  v_result := v_result || jsonb_build_object('ano_anterior', (\n    SELECT row_to_json(t)\n    FROM (\n      SELECT \n        COUNT(DISTINCT v.professor_id) as total_professores,\n        SUM(v.carteira_alunos) as total_alunos,\n        ROUND(AVG(v.media_presenca), 1) as media_presenca,\n        SUM(v.evasoes) as total_evasoes,\n        SUM(v.matriculas) as total_matriculas\n      FROM vw_kpis_professor_mensal v\n      WHERE v.ano = p_ano - 1 AND v.mes = p_mes\n        AND (p_unidade_id IS NULL OR v.unidade_id = p_unidade_id)\n    ) t\n  ));\n\n  RETURN v_result;\nEND;\n$$;\n\n-- Comentário\nCOMMENT ON FUNCTION get_dados_relatorio_coordenacao IS 'Função para buscar dados do relatório de coordenação pedagógica com IA';\n"}	create_get_dados_relatorio_coordenacao	producoes.emla@gmail.com	\N	\N
20260126231011	{"-- Criar tabela de configuração do Health Score\nCREATE TABLE IF NOT EXISTS config_health_score (\n    id SERIAL PRIMARY KEY,\n    unidade_id UUID REFERENCES unidades(id) ON DELETE CASCADE,\n    \n    -- Pesos dos fatores (devem somar 100)\n    peso_taxa_crescimento INTEGER DEFAULT 15,\n    peso_media_turma INTEGER DEFAULT 20,\n    peso_retencao INTEGER DEFAULT 25,\n    peso_conversao INTEGER DEFAULT 15,\n    peso_presenca INTEGER DEFAULT 15,\n    peso_evasoes INTEGER DEFAULT 10,\n    \n    -- Parâmetros\n    meta_media_turma DECIMAL(3,1) DEFAULT 3.0,\n    \n    -- Faixas de classificação\n    limite_saudavel INTEGER DEFAULT 70,\n    limite_atencao INTEGER DEFAULT 50,\n    \n    created_at TIMESTAMPTZ DEFAULT NOW(),\n    updated_at TIMESTAMPTZ DEFAULT NOW(),\n    \n    -- Constraint: pesos devem somar 100\n    CONSTRAINT config_health_score_pesos_somam_100 CHECK (\n        peso_taxa_crescimento + peso_media_turma + peso_retencao + \n        peso_conversao + peso_presenca + peso_evasoes = 100\n    ),\n    \n    -- Unique: uma config por unidade (NULL = global)\n    CONSTRAINT config_health_score_unidade_unique UNIQUE (unidade_id)\n);\n\n-- Comentário\nCOMMENT ON TABLE config_health_score IS 'Configuração dos pesos e limites do Health Score do Professor';\n\n-- Inserir configuração global padrão\nINSERT INTO config_health_score (unidade_id) VALUES (NULL)\nON CONFLICT (unidade_id) DO NOTHING;\n\n-- Habilitar RLS\nALTER TABLE config_health_score ENABLE ROW LEVEL SECURITY;\n\n-- Política de leitura para todos os usuários autenticados\nCREATE POLICY \\"config_health_score_select_policy\\" ON config_health_score\n    FOR SELECT TO authenticated USING (true);\n\n-- Política de update apenas para admins (perfil = 'admin' ou 'super_admin')\nCREATE POLICY \\"config_health_score_update_policy\\" ON config_health_score\n    FOR UPDATE TO authenticated \n    USING (\n        EXISTS (\n            SELECT 1 FROM usuarios u \n            WHERE u.auth_user_id = auth.uid() \n            AND u.perfil IN ('admin', 'super_admin')\n        )\n    );"}	create_config_health_score	producoes.emla@gmail.com	\N	\N
