20260130211646	{"ALTER TABLE professor_360_criterios ENABLE ROW LEVEL SECURITY;\nALTER TABLE professor_360_ocorrencias ENABLE ROW LEVEL SECURITY;\nALTER TABLE professor_360_ocorrencias_log ENABLE ROW LEVEL SECURITY;\nALTER TABLE professor_360_avaliacoes ENABLE ROW LEVEL SECURITY;\nALTER TABLE professor_360_config ENABLE ROW LEVEL SECURITY;\n\nCREATE POLICY \\"Leitura criterios\\" ON professor_360_criterios FOR SELECT TO authenticated USING (true);\nCREATE POLICY \\"Leitura ocorrencias\\" ON professor_360_ocorrencias FOR SELECT TO authenticated USING (true);\nCREATE POLICY \\"Leitura ocorrencias_log\\" ON professor_360_ocorrencias_log FOR SELECT TO authenticated USING (true);\nCREATE POLICY \\"Leitura avaliacoes\\" ON professor_360_avaliacoes FOR SELECT TO authenticated USING (true);\nCREATE POLICY \\"Leitura config\\" ON professor_360_config FOR SELECT TO authenticated USING (true);\n\nCREATE POLICY \\"Escrita criterios\\" ON professor_360_criterios FOR ALL TO authenticated USING (true) WITH CHECK (true);\nCREATE POLICY \\"Escrita ocorrencias\\" ON professor_360_ocorrencias FOR ALL TO authenticated USING (true) WITH CHECK (true);\nCREATE POLICY \\"Escrita avaliacoes\\" ON professor_360_avaliacoes FOR ALL TO authenticated USING (true) WITH CHECK (true);\nCREATE POLICY \\"Escrita config\\" ON professor_360_config FOR ALL TO authenticated USING (true) WITH CHECK (true);"}	professor_360_rls_policies	producoes.emla@gmail.com	\N	\N
20260201150757	{"-- Trigger para sincronizar evasões de movimentacoes_admin para evasoes_v2\nCREATE OR REPLACE FUNCTION sync_evasao_to_evasoes_v2()\nRETURNS TRIGGER AS $$\nBEGIN\n  -- Apenas para INSERT de evasões (tipo='evasao' ou tipo='nao_renovacao')\n  IF (TG_OP = 'INSERT' AND NEW.tipo IN ('evasao', 'nao_renovacao')) THEN\n    \n    INSERT INTO evasoes_v2 (\n      unidade_id,\n      data_evasao,\n      aluno_id,\n      professor_id,\n      valor_parcela,\n      tipo_saida_id,\n      motivo_saida_id,\n      observacoes,\n      created_at\n    ) VALUES (\n      NEW.unidade_id,\n      NEW.data,\n      NEW.aluno_id,\n      NEW.professor_id,\n      COALESCE(NEW.valor_parcela_evasao, NEW.valor_parcela_anterior, 0),\n      CASE \n        WHEN NEW.tipo = 'evasao' THEN 1  -- Interrompido\n        WHEN NEW.tipo = 'nao_renovacao' THEN 2  -- Não Renovação\n        ELSE 1\n      END,\n      NEW.motivo_saida_id,\n      NEW.observacoes,\n      NOW()\n    );\n    \n    RAISE NOTICE 'Evasão sincronizada para evasoes_v2: % - %', NEW.aluno_nome, NEW.data;\n  END IF;\n  \n  RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql;\n\n-- Criar trigger (se não existir)\nDROP TRIGGER IF EXISTS tr_sync_evasao_v2 ON movimentacoes_admin;\nCREATE TRIGGER tr_sync_evasao_v2\n  AFTER INSERT ON movimentacoes_admin\n  FOR EACH ROW\n  EXECUTE FUNCTION sync_evasao_to_evasoes_v2();"}	sync_evasao_to_evasoes_v2	producoes.emla@gmail.com	\N	\N
20260201150839	{"-- Trigger para sincronizar experimentais de leads_diarios para experimentais_professor_mensal\nCREATE OR REPLACE FUNCTION sync_experimentais_professor()\nRETURNS TRIGGER AS $$\nDECLARE\n  v_ano INTEGER;\n  v_mes INTEGER;\n  v_delta INTEGER;\nBEGIN\n  -- Determinar operação\n  IF TG_OP = 'DELETE' THEN\n    v_ano := EXTRACT(YEAR FROM OLD.data)::INTEGER;\n    v_mes := EXTRACT(MONTH FROM OLD.data)::INTEGER;\n    v_delta := -COALESCE(OLD.quantidade, 1);\n    \n    -- Só processa se for experimental e tiver professor\n    IF OLD.tipo LIKE 'experimental%' AND OLD.professor_id IS NOT NULL THEN\n      UPDATE experimentais_professor_mensal\n      SET experimentais = GREATEST(0, experimentais + v_delta)\n      WHERE professor_id = OLD.professor_id \n        AND unidade_id = OLD.unidade_id \n        AND ano = v_ano \n        AND mes = v_mes;\n    END IF;\n    \n    RETURN OLD;\n  ELSE\n    v_ano := EXTRACT(YEAR FROM NEW.data)::INTEGER;\n    v_mes := EXTRACT(MONTH FROM NEW.data)::INTEGER;\n    v_delta := COALESCE(NEW.quantidade, 1);\n    \n    -- Só processa se for experimental e tiver professor\n    IF NEW.tipo LIKE 'experimental%' AND NEW.professor_id IS NOT NULL THEN\n      INSERT INTO experimentais_professor_mensal (professor_id, unidade_id, ano, mes, experimentais)\n      VALUES (NEW.professor_id, NEW.unidade_id, v_ano, v_mes, v_delta)\n      ON CONFLICT (professor_id, unidade_id, ano, mes) \n      DO UPDATE SET experimentais = experimentais_professor_mensal.experimentais + v_delta;\n    END IF;\n    \n    RETURN NEW;\n  END IF;\nEND;\n$$ LANGUAGE plpgsql;\n\n-- Trigger para sincronizar experimentais por unidade\nCREATE OR REPLACE FUNCTION sync_experimentais_unidade()\nRETURNS TRIGGER AS $$\nDECLARE\n  v_ano INTEGER;\n  v_mes INTEGER;\n  v_delta_exp INTEGER := 0;\n  v_delta_mat INTEGER := 0;\nBEGIN\n  -- Determinar operação\n  IF TG_OP = 'DELETE' THEN\n    v_ano := EXTRACT(YEAR FROM OLD.data)::INTEGER;\n    v_mes := EXTRACT(MONTH FROM OLD.data)::INTEGER;\n    \n    IF OLD.tipo LIKE 'experimental%' THEN\n      v_delta_exp := -COALESCE(OLD.quantidade, 1);\n    ELSIF OLD.tipo = 'matricula' THEN\n      v_delta_mat := -COALESCE(OLD.quantidade, 1);\n    END IF;\n    \n    IF v_delta_exp != 0 OR v_delta_mat != 0 THEN\n      UPDATE experimentais_mensal_unidade\n      SET \n        total_experimentais = GREATEST(0, total_experimentais + v_delta_exp),\n        total_matriculas = GREATEST(0, total_matriculas + v_delta_mat)\n      WHERE unidade_id = OLD.unidade_id AND ano = v_ano AND mes = v_mes;\n    END IF;\n    \n    RETURN OLD;\n  ELSE\n    v_ano := EXTRACT(YEAR FROM NEW.data)::INTEGER;\n    v_mes := EXTRACT(MONTH FROM NEW.data)::INTEGER;\n    \n    IF NEW.tipo LIKE 'experimental%' THEN\n      v_delta_exp := COALESCE(NEW.quantidade, 1);\n    ELSIF NEW.tipo = 'matricula' THEN\n      v_delta_mat := COALESCE(NEW.quantidade, 1);\n    END IF;\n    \n    IF v_delta_exp != 0 OR v_delta_mat != 0 THEN\n      INSERT INTO experimentais_mensal_unidade (unidade_id, ano, mes, total_experimentais, total_matriculas)\n      VALUES (NEW.unidade_id, v_ano, v_mes, GREATEST(0, v_delta_exp), GREATEST(0, v_delta_mat))\n      ON CONFLICT (unidade_id, ano, mes) \n      DO UPDATE SET \n        total_experimentais = experimentais_mensal_unidade.total_experimentais + v_delta_exp,\n        total_matriculas = experimentais_mensal_unidade.total_matriculas + v_delta_mat;\n    END IF;\n    \n    RETURN NEW;\n  END IF;\nEND;\n$$ LANGUAGE plpgsql;\n\n-- Criar triggers\nDROP TRIGGER IF EXISTS tr_sync_experimentais_professor ON leads_diarios;\nCREATE TRIGGER tr_sync_experimentais_professor\n  AFTER INSERT OR DELETE ON leads_diarios\n  FOR EACH ROW\n  EXECUTE FUNCTION sync_experimentais_professor();\n\nDROP TRIGGER IF EXISTS tr_sync_experimentais_unidade ON leads_diarios;\nCREATE TRIGGER tr_sync_experimentais_unidade\n  AFTER INSERT OR DELETE ON leads_diarios\n  FOR EACH ROW\n  EXECUTE FUNCTION sync_experimentais_unidade();"}	sync_experimentais_triggers	producoes.emla@gmail.com	\N	\N
20260201150906	{"-- Função para criar snapshot mensal de dados agregados\n-- Deve ser executada no fechamento de cada mês\nCREATE OR REPLACE FUNCTION snapshot_dados_mensais(p_ano INTEGER, p_mes INTEGER)\nRETURNS TABLE (unidade_nome VARCHAR, registros_afetados INTEGER) AS $$\nDECLARE\n  v_unidade RECORD;\n  v_count INTEGER := 0;\nBEGIN\n  FOR v_unidade IN SELECT id, nome FROM unidades WHERE ativo = true LOOP\n    \n    INSERT INTO dados_mensais (\n      unidade_id, ano, mes,\n      alunos_pagantes, novas_matriculas, evasoes,\n      churn_rate, ticket_medio, taxa_renovacao,\n      tempo_permanencia, inadimplencia, faturamento_estimado, saldo_liquido\n    )\n    SELECT \n      v_unidade.id,\n      p_ano,\n      p_mes,\n      -- Alunos pagantes (status ativo e tipo que conta como pagante)\n      (SELECT COUNT(*) FROM alunos a \n       LEFT JOIN tipos_matricula tm ON tm.id = a.tipo_matricula_id\n       WHERE a.unidade_id = v_unidade.id AND a.status = 'ativo' \n       AND (tm.conta_como_pagante = true OR tm.id IS NULL)),\n      -- Novas matrículas do mês\n      (SELECT COALESCE(SUM(quantidade), 0) FROM leads_diarios \n       WHERE unidade_id = v_unidade.id AND tipo = 'matricula'\n       AND EXTRACT(YEAR FROM data) = p_ano AND EXTRACT(MONTH FROM data) = p_mes),\n      -- Evasões do mês\n      (SELECT COUNT(*) FROM evasoes \n       WHERE unidade = v_unidade.nome\n       AND EXTRACT(YEAR FROM competencia) = p_ano AND EXTRACT(MONTH FROM competencia) = p_mes),\n      -- Churn rate (evasões / alunos do mês anterior * 100)\n      COALESCE((\n        SELECT CASE WHEN dm_ant.alunos_pagantes > 0 \n          THEN ROUND(((SELECT COUNT(*) FROM evasoes WHERE unidade = v_unidade.nome \n                       AND EXTRACT(YEAR FROM competencia) = p_ano \n                       AND EXTRACT(MONTH FROM competencia) = p_mes)::NUMERIC \n                      / dm_ant.alunos_pagantes) * 100, 2)\n          ELSE 0 END\n        FROM dados_mensais dm_ant\n        WHERE dm_ant.unidade_id = v_unidade.id \n        AND (dm_ant.ano = p_ano AND dm_ant.mes = p_mes - 1)\n           OR (dm_ant.ano = p_ano - 1 AND dm_ant.mes = 12 AND p_mes = 1)\n        LIMIT 1\n      ), 0),\n      -- Ticket médio\n      (SELECT COALESCE(ROUND(AVG(a.valor_parcela), 2), 0) FROM alunos a\n       LEFT JOIN tipos_matricula tm ON tm.id = a.tipo_matricula_id\n       WHERE a.unidade_id = v_unidade.id AND a.status = 'ativo'\n       AND (tm.entra_ticket_medio = true OR tm.id IS NULL)),\n      -- Taxa renovação (placeholder - precisa de dados de renovações)\n      0,\n      -- Tempo permanência médio\n      (SELECT COALESCE(ROUND(AVG(tempo_permanencia_meses), 0), 0) FROM alunos \n       WHERE unidade_id = v_unidade.id AND status = 'ativo'),\n      -- Inadimplência (placeholder - precisa de integração com financeiro)\n      0,\n      -- Faturamento estimado (MRR)\n      (SELECT COALESCE(SUM(a.valor_parcela), 0) FROM alunos a\n       LEFT JOIN tipos_matricula tm ON tm.id = a.tipo_matricula_id\n       WHERE a.unidade_id = v_unidade.id AND a.status = 'ativo'\n       AND (tm.conta_como_pagante = true OR tm.id IS NULL)),\n      -- Saldo líquido (matrículas - evasões)\n      (SELECT COALESCE(SUM(quantidade), 0) FROM leads_diarios \n       WHERE unidade_id = v_unidade.id AND tipo = 'matricula'\n       AND EXTRACT(YEAR FROM data) = p_ano AND EXTRACT(MONTH FROM data) = p_mes)\n      - (SELECT COUNT(*) FROM evasoes \n         WHERE unidade = v_unidade.nome\n         AND EXTRACT(YEAR FROM competencia) = p_ano AND EXTRACT(MONTH FROM competencia) = p_mes)\n    ON CONFLICT (unidade_id, ano, mes) DO UPDATE SET\n      alunos_pagantes = EXCLUDED.alunos_pagantes,\n      novas_matriculas = EXCLUDED.novas_matriculas,\n      evasoes = EXCLUDED.evasoes,\n      churn_rate = EXCLUDED.churn_rate,\n      ticket_medio = EXCLUDED.ticket_medio,\n      tempo_permanencia = EXCLUDED.tempo_permanencia,\n      faturamento_estimado = EXCLUDED.faturamento_estimado,\n      saldo_liquido = EXCLUDED.saldo_liquido,\n      updated_at = NOW();\n    \n    v_count := v_count + 1;\n    unidade_nome := v_unidade.nome;\n    registros_afetados := 1;\n    RETURN NEXT;\n    \n  END LOOP;\n  \n  RETURN;\nEND;\n$$ LANGUAGE plpgsql;\n\n-- Comentário de uso\nCOMMENT ON FUNCTION snapshot_dados_mensais IS \n'Executa snapshot mensal de KPIs. Uso: SELECT * FROM snapshot_dados_mensais(2026, 2);';"}	snapshot_dados_mensais_function	producoes.emla@gmail.com	\N	\N
20260201150956	{"-- View que calcula performance de professores em tempo real\nCREATE OR REPLACE VIEW vw_professores_performance_atual AS\nWITH alunos_por_professor AS (\n  SELECT \n    p.id as professor_id,\n    p.nome as professor,\n    u.nome as unidade,\n    u.id as unidade_id,\n    COUNT(a.id) as total_alunos,\n    COALESCE(AVG(a.valor_parcela), 0) as ticket_medio,\n    COALESCE(SUM(a.valor_parcela), 0) as mrr,\n    COALESCE(AVG(a.tempo_permanencia_meses), 0) as tempo_medio,\n    COALESCE(AVG(a.percentual_presenca), 0) as presenca_media\n  FROM professores p\n  JOIN professores_unidades pu ON pu.professor_id = p.id\n  JOIN unidades u ON u.id = pu.unidade_id\n  LEFT JOIN alunos a ON a.professor_atual_id = p.id \n    AND a.unidade_id = u.id \n    AND a.status = 'ativo'\n  WHERE p.ativo = true\n  GROUP BY p.id, p.nome, u.nome, u.id\n),\nexperimentais_ano AS (\n  SELECT \n    professor_id,\n    unidade_id,\n    SUM(experimentais) as total_experimentais\n  FROM experimentais_professor_mensal\n  WHERE ano = EXTRACT(YEAR FROM CURRENT_DATE)\n  GROUP BY professor_id, unidade_id\n),\nmatriculas_ano AS (\n  SELECT \n    professor_fixo_id as professor_id,\n    unidade_id,\n    SUM(quantidade) as total_matriculas\n  FROM leads_diarios\n  WHERE tipo = 'matricula' \n    AND professor_fixo_id IS NOT NULL\n    AND EXTRACT(YEAR FROM data) = EXTRACT(YEAR FROM CURRENT_DATE)\n  GROUP BY professor_fixo_id, unidade_id\n),\nevasoes_ano AS (\n  SELECT \n    p.id as professor_id,\n    u.id as unidade_id,\n    COUNT(*) as total_evasoes\n  FROM evasoes e\n  JOIN professores p ON p.nome = e.professor\n  JOIN unidades u ON u.nome = e.unidade\n  WHERE EXTRACT(YEAR FROM e.competencia) = EXTRACT(YEAR FROM CURRENT_DATE)\n  GROUP BY p.id, u.id\n)\nSELECT \n  ap.professor_id,\n  ap.professor,\n  ap.unidade,\n  EXTRACT(YEAR FROM CURRENT_DATE)::INTEGER as ano,\n  ap.total_alunos,\n  ap.ticket_medio::NUMERIC(10,2),\n  ap.mrr::NUMERIC(12,2),\n  ap.tempo_medio::NUMERIC(5,1) as tempo_permanencia_medio,\n  ap.presenca_media::NUMERIC(5,1),\n  COALESCE(ea.total_experimentais, 0)::INTEGER as experimentais,\n  COALESCE(ma.total_matriculas, 0)::INTEGER as matriculas,\n  CASE WHEN COALESCE(ea.total_experimentais, 0) > 0 \n    THEN ROUND((COALESCE(ma.total_matriculas, 0)::NUMERIC / ea.total_experimentais) * 100, 1)\n    ELSE 0 \n  END as taxa_conversao,\n  COALESCE(ev.total_evasoes, 0)::INTEGER as evasoes\nFROM alunos_por_professor ap\nLEFT JOIN experimentais_ano ea ON ea.professor_id = ap.professor_id AND ea.unidade_id = ap.unidade_id\nLEFT JOIN matriculas_ano ma ON ma.professor_id = ap.professor_id AND ma.unidade_id = ap.unidade_id\nLEFT JOIN evasoes_ano ev ON ev.professor_id = ap.professor_id AND ev.unidade_id = ap.unidade_id\nORDER BY ap.total_alunos DESC;\n\nCOMMENT ON VIEW vw_professores_performance_atual IS \n'View que calcula performance de professores em tempo real. Substitui professores_performance para dados do ano atual.';"}	vw_professores_performance_atual	producoes.emla@gmail.com	\N	\N
20260201151036	{"-- Corrigir função para não inserir em colunas GENERATED\nCREATE OR REPLACE FUNCTION snapshot_dados_mensais(p_ano INTEGER, p_mes INTEGER)\nRETURNS TABLE (unidade_nome VARCHAR, registros_afetados INTEGER) AS $$\nDECLARE\n  v_unidade RECORD;\nBEGIN\n  FOR v_unidade IN SELECT id, nome FROM unidades WHERE ativo = true LOOP\n    \n    INSERT INTO dados_mensais (\n      unidade_id, ano, mes,\n      alunos_pagantes, novas_matriculas, evasoes,\n      churn_rate, ticket_medio, taxa_renovacao,\n      tempo_permanencia, inadimplencia\n      -- faturamento_estimado e saldo_liquido são GENERATED, não inserir\n    )\n    SELECT \n      v_unidade.id,\n      p_ano,\n      p_mes,\n      -- Alunos pagantes\n      (SELECT COUNT(*) FROM alunos a \n       LEFT JOIN tipos_matricula tm ON tm.id = a.tipo_matricula_id\n       WHERE a.unidade_id = v_unidade.id AND a.status = 'ativo' \n       AND (tm.conta_como_pagante = true OR tm.id IS NULL))::INTEGER,\n      -- Novas matrículas do mês\n      (SELECT COALESCE(SUM(quantidade), 0) FROM leads_diarios \n       WHERE unidade_id = v_unidade.id AND tipo = 'matricula'\n       AND EXTRACT(YEAR FROM data) = p_ano AND EXTRACT(MONTH FROM data) = p_mes)::INTEGER,\n      -- Evasões do mês\n      (SELECT COUNT(*) FROM evasoes \n       WHERE unidade = v_unidade.nome\n       AND EXTRACT(YEAR FROM competencia) = p_ano AND EXTRACT(MONTH FROM competencia) = p_mes)::INTEGER,\n      -- Churn rate\n      COALESCE((\n        SELECT CASE WHEN dm_ant.alunos_pagantes > 0 \n          THEN ROUND(((SELECT COUNT(*) FROM evasoes WHERE unidade = v_unidade.nome \n                       AND EXTRACT(YEAR FROM competencia) = p_ano \n                       AND EXTRACT(MONTH FROM competencia) = p_mes)::NUMERIC \n                      / dm_ant.alunos_pagantes) * 100, 2)\n          ELSE 0 END\n        FROM dados_mensais dm_ant\n        WHERE dm_ant.unidade_id = v_unidade.id \n        AND ((dm_ant.ano = p_ano AND dm_ant.mes = p_mes - 1)\n           OR (dm_ant.ano = p_ano - 1 AND dm_ant.mes = 12 AND p_mes = 1))\n        LIMIT 1\n      ), 0),\n      -- Ticket médio\n      (SELECT COALESCE(ROUND(AVG(a.valor_parcela), 2), 0) FROM alunos a\n       LEFT JOIN tipos_matricula tm ON tm.id = a.tipo_matricula_id\n       WHERE a.unidade_id = v_unidade.id AND a.status = 'ativo'\n       AND (tm.entra_ticket_medio = true OR tm.id IS NULL)),\n      -- Taxa renovação (placeholder)\n      0,\n      -- Tempo permanência médio\n      (SELECT COALESCE(ROUND(AVG(tempo_permanencia_meses), 0), 0) FROM alunos \n       WHERE unidade_id = v_unidade.id AND status = 'ativo')::INTEGER,\n      -- Inadimplência (placeholder)\n      0\n    ON CONFLICT (unidade_id, ano, mes) DO UPDATE SET\n      alunos_pagantes = EXCLUDED.alunos_pagantes,\n      novas_matriculas = EXCLUDED.novas_matriculas,\n      evasoes = EXCLUDED.evasoes,\n      churn_rate = EXCLUDED.churn_rate,\n      ticket_medio = EXCLUDED.ticket_medio,\n      tempo_permanencia = EXCLUDED.tempo_permanencia,\n      updated_at = NOW();\n    \n    unidade_nome := v_unidade.nome;\n    registros_afetados := 1;\n    RETURN NEXT;\n    \n  END LOOP;\n  \n  RETURN;\nEND;\n$$ LANGUAGE plpgsql;"}	fix_snapshot_dados_mensais	producoes.emla@gmail.com	\N	\N
