NUMERIC(12,2) AS faturamento_previsto,\n  -- Faturamento realizado = MRR (sem inadimplência por enquanto)\n  COALESCE(aa.mrr, 0)::NUMERIC(12,2) AS faturamento_realizado,\n  -- Churn em tempo real\n  CASE \n    WHEN COALESCE(ama.alunos_pagantes, 0) > 0 \n    THEN ROUND((COALESCE(ema.evasoes_realtime, 0)::NUMERIC / ama.alunos_pagantes) * 100, 2)\n    ELSE 0\n  END::NUMERIC(5,2) AS churn_rate,\n  -- Evasões em tempo real\n  COALESCE(ema.evasoes_realtime, 0)::INTEGER AS total_evasoes,\n  -- Matrículas em tempo real\n  COALESCE(mma.matriculas_realtime, 0)::INTEGER AS novas_matriculas,\n  -- Saldo líquido em tempo real\n  (COALESCE(mma.matriculas_realtime, 0) - COALESCE(ema.evasoes_realtime, 0))::INTEGER AS saldo_liquido,\n  -- Taxa renovação em tempo real\n  CASE \n    WHEN COALESCE(cvm.total_vencer, 0) > 0 \n    THEN ROUND((COALESCE(rma.renovacoes_realtime, 0)::NUMERIC / cvm.total_vencer) * 100, 2)\n    ELSE 0\n  END::NUMERIC(5,2) AS taxa_renovacao,\n  -- Renovações realizadas\n  COALESCE(rma.renovacoes_realtime, 0)::INTEGER AS renovacoes_realizadas,\n  -- Contratos a vencer\n  COALESCE(cvm.total_vencer, 0)::INTEGER AS contratos_vencer\nFROM unidades u\nLEFT JOIN alunos_ativos aa ON aa.unidade_id = u.id\nLEFT JOIN permanencia p ON p.unidade_id = u.id\nLEFT JOIN ltv_calc l ON l.unidade_id = u.id\nLEFT JOIN evasoes_mes_atual ema ON ema.unidade_id = u.id\nLEFT JOIN matriculas_mes_atual mma ON mma.unidade_id = u.id\nLEFT JOIN renovacoes_mes_atual rma ON rma.unidade_id = u.id\nLEFT JOIN contratos_vencer_mes cvm ON cvm.unidade_id = u.id\nLEFT JOIN alunos_mes_anterior ama ON ama.unidade_id = u.id\nWHERE u.ativo = true;"}	vw_kpis_gestao_mensal_full_realtime	producoes.emla@gmail.com	\N	\N
20260201152037	{"-- Atualizar vw_alertas_inteligentes para usar dados em tempo real\nCREATE OR REPLACE VIEW vw_alertas_inteligentes AS\nWITH \n-- Calcular churn em tempo real para alertas\nchurn_realtime AS (\n  SELECT \n    u.id as unidade_id,\n    u.nome as unidade_nome,\n    COALESCE(em.evasoes, 0) as evasoes,\n    COALESCE(ama.alunos_pagantes, 0) as alunos_anterior,\n    CASE \n      WHEN COALESCE(ama.alunos_pagantes, 0) > 0 \n      THEN ROUND((COALESCE(em.evasoes, 0)::NUMERIC / ama.alunos_pagantes) * 100, 2)\n      ELSE 0\n    END as churn_rate\n  FROM unidades u\n  LEFT JOIN (\n    SELECT un.id as unidade_id, COUNT(*) as evasoes\n    FROM evasoes e\n    JOIN unidades un ON un.nome = e.unidade\n    WHERE EXTRACT(YEAR FROM e.competencia) = EXTRACT(YEAR FROM CURRENT_DATE)\n      AND EXTRACT(MONTH FROM e.competencia) = EXTRACT(MONTH FROM CURRENT_DATE)\n    GROUP BY un.id\n  ) em ON em.unidade_id = u.id\n  LEFT JOIN (\n    SELECT unidade_id, alunos_pagantes\n    FROM dados_mensais\n    WHERE (ano = EXTRACT(YEAR FROM CURRENT_DATE) AND mes = EXTRACT(MONTH FROM CURRENT_DATE) - 1)\n       OR (ano = EXTRACT(YEAR FROM CURRENT_DATE) - 1 AND mes = 12 AND EXTRACT(MONTH FROM CURRENT_DATE) = 1)\n  ) ama ON ama.unidade_id = u.id\n  WHERE u.ativo = true\n),\n-- Ticket médio atual em tempo real\nticket_realtime AS (\n  SELECT \n    a.unidade_id,\n    AVG(a.valor_parcela) FILTER (WHERE tm.entra_ticket_medio = true) as ticket_atual\n  FROM alunos a\n  LEFT JOIN tipos_matricula tm ON tm.id = a.tipo_matricula_id\n  WHERE a.status = 'ativo'\n  GROUP BY a.unidade_id\n)\nSELECT tipo_alerta, severidade, unidade_id, unidade_nome, quantidade, descricao, detalhe, valor_atual, valor_meta, data_referencia\nFROM (\n  -- Contratos vencendo sem renovação\n  SELECT \n    'CONTRATO_VENCENDO'::text AS tipo_alerta,\n    'critico'::text AS severidade,\n    a.unidade_id,\n    u.nome AS unidade_nome,\n    count(*)::integer AS quantidade,\n    'Contratos vencendo em 30 dias sem renovação'::text AS descricao,\n    concat(count(*), ' alunos com contrato vencendo até ', to_char(CURRENT_DATE + INTERVAL '30 days', 'DD/MM')) AS detalhe,\n    NULL::numeric AS valor_atual,\n    NULL::numeric AS valor_meta,\n    CURRENT_DATE AS data_referencia\n  FROM alunos a\n  JOIN unidades u ON a.unidade_id = u.id\n  LEFT JOIN renovacoes r ON r.aluno_id = a.id AND r.data_fim_novo_contrato > a.data_fim_contrato AND r.status = 'concluida'\n  WHERE a.status = 'ativo'\n    AND a.data_fim_contrato >= CURRENT_DATE \n    AND a.data_fim_contrato <= CURRENT_DATE + INTERVAL '30 days'\n    AND r.id IS NULL\n  GROUP BY a.unidade_id, u.nome\n  HAVING count(*) > 0\n\n  UNION ALL\n\n  -- Renovações pendentes\n  SELECT \n    'RENOVACOES_PENDENTES'::text,\n    'atencao'::text,\n    a.unidade_id,\n    u.nome,\n    count(*)::integer,\n    'Renovações pendentes para este mês'::text,\n    concat(count(*), ' contratos vencem este mês'),\n    NULL::numeric,\n    NULL::numeric,\n    CURRENT_DATE\n  FROM alunos a\n  JOIN unidades u ON a.unidade_id = u.id\n  WHERE a.status = 'ativo'\n    AND EXTRACT(MONTH FROM a.data_fim_contrato) = EXTRACT(MONTH FROM CURRENT_DATE)\n    AND EXTRACT(YEAR FROM a.data_fim_contrato) = EXTRACT(YEAR FROM CURRENT_DATE)\n  GROUP BY a.unidade_id, u.nome\n  HAVING count(*) > 0\n\n  UNION ALL\n\n  -- Conversão baixa (dados comerciais - já é tempo real via trigger)\n  SELECT \n    'CONVERSAO_BAIXA'::text,\n    CASE WHEN (dc.novas_matriculas_total::numeric / NULLIF(dc.aulas_experimentais, 0)::numeric * 100) < 10 THEN 'critico' ELSE 'atencao' END,\n    u.id,\n    u.nome,\n    1,\n    'Taxa de conversão abaixo da meta'::text,\n    concat('Conversão: ', round((dc.novas_matriculas_total::numeric / NULLIF(dc.aulas_experimentais, 0)::numeric * 100), 1), '% (meta: 13.5%)'),\n    round((dc.novas_matriculas_total::numeric / NULLIF(dc.aulas_experimentais, 0)::numeric * 100), 1),\n    13.5,\n    dc.competencia\n  FROM dados_comerciais dc\n  JOIN unidades u ON lower(u.nome) = lower(dc.unidade)\n  WHERE dc.competencia = date_trunc('month', CURRENT_DATE)\n    AND dc.aulas_experimentais > 0\n    AND (dc.novas_matriculas_total::numeric / dc.aulas_experimentais::numeric * 100) < 13.5\n\n  UNION ALL\n\n  -- CHURN ALTO - AGORA EM TEMPO REAL\n  SELECT \n    'CHURN_ALTO'::text,\n    CASE WHEN cr.churn_rate > 6 THEN 'critico' ELSE 'atencao' END,\n    cr.unidade_id,\n    cr.unidade_nome,\n    1,\n    'Churn acima da meta mensal'::text,\n    concat('Churn: ', cr.churn_rate, '% (meta: 4%)'),\n    cr.churn_rate,\n    4.0,\n    CURRENT_DATE\n  FROM churn_realtime cr\n  WHERE cr.churn_rate > 4\n\n  UNION ALL\n\n  -- TICKET CAINDO - TEMPO REAL vs mês anterior\n  SELECT \n    'TICKET_CAINDO'::text,\n    'atencao'::text,\n    tr.unidade_id,\n    u.nome,\n    1,\n    'Ticket médio caindo vs mês anterior'::text,\n    concat('Ticket caiu ', round(((dm.ticket_medio - tr.ticket_atual) / NULLIF(dm.ticket_medio, 0) * 100), 1), '% (R$', round(dm.ticket_medio), ' → R$', round(tr.ticket_atual), ')'),\n    tr.ticket_atual,\n    dm.ticket_medio,\n    CURRENT_DATE\n  FROM ticket_realtime tr\n  JOIN unidades u ON u.id = tr.unidade_id\n  JOIN dados_mensais dm ON dm.unidade_id = tr.unidade_id\n    AND ((dm.ano = EXTRACT(YEAR FROM CURRENT_DATE) AND dm.mes = EXTRACT(MONTH FROM CURRENT_DATE) - 1)\n      OR (dm.ano = EXTRACT(YEAR FROM CURRENT_DATE) - 1 AND dm.mes = 12 AND EXTRACT(MONTH FROM CURRENT_DATE) = 1))\n  WHERE tr.ticket_atual < dm.ticket_medio\n\n  UNION ALL\n\n  -- Professor com turma baixa\n  SELECT \n    'PROFESSOR_TURMA_BAIXA'::text,\n    'informativo'::text,\n    pu.unidade_id,\n    u.nome,\n    count(DISTINCT t.professor_id)::integer,\n    'Professores com média alunos/turma baixa'::text,\n    concat(count(DISTINCT t.professor_id), ' professores com média < 1.5 alunos/turma'),\n    NULL::numeric,\n    1.5,\n    CURRENT_DATE\n  FROM (\n    SELECT professor_id, avg(total_alunos) as media\n    FROM vw_turmas_implicitas\n    GROUP BY professor_id\n    HAVING avg(total_alunos) < 1.5\n  ) t\n  JOIN professores_unidades pu ON t.professor_id = pu.professor_id\n  JOIN unidades u ON pu.unidade_id = u.id\n  GROUP BY pu.unidade_id, u.nome\n  HAVING count(DISTINCT t.professor_id) > 0\n\n  UNION ALL\n\n  -- Meta em risco\n  SELECT \n    'META_EM_RISCO'::text,\n    'critico'::text,\n    mk.unidade_id,\n    u.nome,\n    1,\n    concat('Meta de ', mk.tipo, ' em risco'),\n    concat(mk.tipo, ': realizado abaixo de 70% da meta'),\n    NULL::numeric,\n    mk.valor,\n    make_date(mk.ano, mk.mes, 1)\n  FROM metas_kpi mk\n  JOIN unidades u ON mk.unidade_id = u.id\n  WHERE mk.ano = EXTRACT(YEAR FROM CURRENT_DATE)\n    AND mk.mes = EXTRACT(MONTH FROM CURRENT_DATE)\n    AND mk.tipo IN ('matriculas', 'alunos_ativos')\n) alertas\nORDER BY \n  CASE severidade \n    WHEN 'critico' THEN 1 \n    WHEN 'atencao' THEN 2 \n    ELSE 3 \n  END, \n  quantidade DESC;"}	vw_alertas_inteligentes_realtime	producoes.emla@gmail.com	\N	\N
20260201152804	{"-- Adicionar campo status_pagamento na tabela alunos\nALTER TABLE alunos \nADD COLUMN IF NOT EXISTS status_pagamento VARCHAR(20) DEFAULT 'em_dia';\n\n-- Adicionar comentário explicativo\nCOMMENT ON COLUMN alunos.status_pagamento IS 'Status de pagamento: em_dia, inadimplente, parcial';"}	add_status_pagamento_alunos	producoes.emla@gmail.com	\N	\N
20260201152929	{"-- Recriar vw_kpis_gestao_mensal com reajuste médio e inadimplência\nCREATE VIEW vw_kpis_gestao_mensal AS\nWITH alunos_ativos AS (\n  SELECT a.unidade_id,\n    count(*) AS total_ativos,\n    count(*) FILTER (WHERE tm.conta_como_pagante = true) AS total_pagantes,\n    count(*) FILTER (WHERE tm.codigo = 'BOLSISTA_INT') AS bolsistas_integrais,\n    count(*) FILTER (WHERE tm.codigo = 'BOLSISTA_PARC') AS bolsistas_parciais,\n    count(*) FILTER (WHERE tm.codigo = 'BANDA') AS banda,\n    avg(a.valor_parcela) FILTER (WHERE tm.entra_ticket_medio = true) AS ticket_medio,\n    sum(a.valor_parcela) FILTER (WHERE tm.conta_como_pagante = true) AS mrr\n  FROM alunos a\n  LEFT JOIN tipos_matricula tm ON tm.id = a.tipo_matricula_id\n  WHERE a.status = 'ativo'\n  GROUP BY a.unidade_id\n),\npermanencia AS (\n  SELECT unidade_id, avg(tempo_permanencia_meses) AS tempo_permanencia_medio\n  FROM alunos WHERE status = 'ativo'\n  GROUP BY unidade_id\n),\nltv_calc AS (\n  SELECT a.unidade_id,\n    avg(a.valor_parcela * a.tempo_permanencia_meses) FILTER (WHERE a.tempo_permanencia_meses >= 4 AND tm.entra_ltv = true) AS ltv_medio\n  FROM alunos a\n  LEFT JOIN tipos_matricula tm ON tm.id = a.tipo_matricula_id\n  WHERE a.status = 'ativo'\n  GROUP BY a.unidade_id\n),\nevasoes_mes_atual AS (\n  SELECT u.id as unidade_id, COUNT(*) as evasoes_realtime\n  FROM evasoes e\n  JOIN unidades u ON u.nome = e.unidade\n  WHERE EXTRACT(YEAR FROM e.competencia) = EXTRACT(YEAR FROM CURRENT_DATE)\n    AND EXTRACT(MONTH FROM e.competencia) = EXTRACT(MONTH FROM CURRENT_DATE)\n  GROUP BY u.id\n),\nmatriculas_mes_atual AS (\n  SELECT unidade_id, COALESCE(SUM(quantidade), 0) as matriculas_realtime\n  FROM leads_diarios\n  WHERE tipo = 'matricula'\n    AND EXTRACT(YEAR FROM data) = EXTRACT(YEAR FROM CURRENT_DATE)\n    AND EXTRACT(MONTH FROM data) = EXTRACT(MONTH FROM CURRENT_DATE)\n  GROUP BY unidade_id\n),\nrenovacoes_mes_atual AS (\n  SELECT unidade_id, \n    COUNT(*) as renovacoes_realtime,\n    AVG(percentual_reajuste) as reajuste_medio\n  FROM renovacoes\n  WHERE EXTRACT(YEAR FROM data_renovacao) = EXTRACT(YEAR FROM CURRENT_DATE)\n    AND EXTRACT(MONTH FROM data_renovacao) = EXTRACT(MONTH FROM CURRENT_DATE)\n    AND status = 'renovado'\n  GROUP BY unidade_id\n),\ncontratos_vencer_mes AS (\n  SELECT unidade_id, COUNT(*) as total_vencer\n  FROM alunos\n  WHERE status = 'ativo'\n    AND EXTRACT(YEAR FROM data_fim_contrato) = EXTRACT(YEAR FROM CURRENT_DATE)\n    AND EXTRACT(MONTH FROM data_fim_contrato) = EXTRACT(MONTH FROM CURRENT_DATE)\n  GROUP BY unidade_id\n),\nalunos_mes_anterior AS (\n  SELECT unidade_id, alunos_pagantes\n  FROM dados_mensais\n  WHERE (ano = EXTRACT(YEAR FROM CURRENT_DATE) AND mes = EXTRACT(MONTH FROM CURRENT_DATE) - 1)\n     OR (ano = EXTRACT(YEAR FROM CURRENT_DATE) - 1 AND mes = 12 AND EXTRACT(MONTH FROM CURRENT_DATE) = 1)\n),\ninadimplencia_atual AS (\n  SELECT a.unidade_id,\n    COUNT(*) FILTER (WHERE a.status_pagamento = 'inadimplente') as qtd_inadimplentes,\n    COUNT(*) as total_pagantes_calc\n  FROM alunos a\n  LEFT JOIN tipos_matricula tm ON tm.id = a.tipo_matricula_id\n  WHERE a.status = 'ativo' AND (tm.conta_como_pagante = true OR tm.id IS NULL)\n  GROUP BY a.unidade_id\n)\nSELECT \n  u.id AS unidade_id,\n  u.nome AS unidade_nome,\n  EXTRACT(YEAR FROM CURRENT_DATE)::INTEGER AS ano,\n  EXTRACT(MONTH FROM CURRENT_DATE)::INTEGER AS mes,\n  COALESCE(aa.total_ativos, 0)::BIGINT AS total_alunos_ativos,\n  COALESCE(aa.total_pagantes, 0)::BIGINT AS total_alunos_pagantes,\n  COALESCE(aa.bolsistas_integrais, 0)::BIGINT AS total_bolsistas_integrais,\n  COALESCE(aa.bolsistas_parciais, 0)::BIGINT AS total_bolsistas_parciais,\n  COALESCE(aa.banda, 0)::BIGINT AS total_banda,\n  COALESCE(aa.ticket_medio, 0)::NUMERIC(10,2) AS ticket_medio,\n  COALESCE(aa.mrr, 0)::NUMERIC(12,2) AS mrr,\n  COALESCE(aa.mrr * 12, 0)::NUMERIC(12,2) AS arr,\n  COALESCE(p.tempo_permanencia_medio, 0)::NUMERIC(5,1) AS tempo_permanencia_medio,\n  COALESCE(l.ltv_medio, 0)::NUMERIC(10,2) AS ltv_medio,\n  -- Inadimplência em tempo real\n  CASE \n    WHEN COALESCE(ia.total_pagantes_calc, 0) > 0 \n    THEN ROUND((COALESCE(ia.qtd_inadimplentes, 0)::NUMERIC / ia.total_pagantes_calc) * 100, 2)\n    ELSE 0\n  END::NUMERIC(5,2) AS inadimplencia_pct,\n  -- Quantidade de inadimplentes\n  COALESCE(ia.qtd_inadimplentes, 0)::INTEGER AS qtd_inadimplentes,\n  -- Faturamento previsto = MRR\n  COALESCE(aa.mrr, 0)::NUMERIC(12,2) AS faturamento_previsto,\n  -- Faturamento realizado = MRR - (MRR * inadimplência%)\n  CASE \n    WHEN COALESCE(ia.total_pagantes_calc, 0) > 0 \n    THEN COALESCE(aa.mrr, 0) * (1 - (COALESCE(ia.qtd_inadimplentes, 0)::NUMERIC / ia.total_pagantes_calc))\n    ELSE COALESCE(aa.mrr, 0)\n  END::NUMERIC(12,2) AS faturamento_realizado,\n  -- Churn em tempo real\n  CASE \n    WHEN COALESCE(ama.alunos_pagantes, 0) > 0 \n    THEN ROUND((COALESCE(ema.evasoes_realtime, 0)::NUMERIC / ama.alunos_pagantes) * 100, 2)\n    ELSE 0\n  END::NUMERIC(5,2) AS churn_rate,\n  COALESCE(ema.evasoes_realtime, 0)::INTEGER AS total_evasoes,\n  COALESCE(mma.matriculas_realtime, 0)::INTEGER AS novas_matriculas,\n  (COALESCE(mma.matriculas_realtime, 0) - COALESCE(ema.evasoes_realtime, 0))::INTEGER AS saldo_liquido,\n  -- Taxa renovação em tempo real\n  CASE \n    WHEN COALESCE(cvm.total_vencer, 0) > 0 \n    THEN ROUND((COALESCE(rma.renovacoes_realtime, 0)::NUMERIC / cvm.total_vencer) * 100, 2)\n    ELSE 0\n  END::NUMERIC(5,2) AS taxa_renovacao,\n  COALESCE(rma.renovacoes_realtime, 0)::INTEGER AS renovacoes_realizadas,\n  COALESCE(cvm.total_vencer, 0)::INTEGER AS contratos_vencer,\n  -- Reajuste médio em tempo real\n  COALESCE(rma.reajuste_medio, 0)::NUMERIC(5,2) AS reajuste_medio\nFROM unidades u\nLEFT JOIN alunos_ativos aa ON aa.unidade_id = u.id\nLEFT JOIN permanencia p ON p.unidade_id = u.id\nLEFT JOIN ltv_calc l ON l.unidade_id = u.id\nLEFT JOIN evasoes_mes_atual ema ON ema.unidade_id = u.id\nLEFT JOIN matriculas_mes_atual mma ON mma.unidade_id = u.id\nLEFT JOIN renovacoes_mes_atual rma ON rma.unidade_id = u.id\nLEFT JOIN contratos_vencer_mes cvm ON cvm.unidade_id = u.id\nLEFT JOIN alunos_mes_anterior ama ON ama.unidade_id = u.id\nLEFT JOIN inadimplencia_atual ia ON ia.unidade_id = u.id\nWHERE u.ativo = true;"}	recreate_vw_kpis_gestao_mensal_complete	producoes.emla@gmail.com	\N	\N
20260201153004	{"-- Recriar vw_dashboard_unidade com inadimplência e reajuste\nCREATE VIEW vw_dashboard_unidade AS\nWITH alunos_ativos AS (\n  SELECT a.unidade_id,\n    count(*) AS total_ativos,\n    count(*) FILTER (WHERE tm.conta_como_pagante = true) AS total_pagantes,\n    avg(a.valor_parcela) FILTER (WHERE tm.entra_ticket_medio = true) AS ticket_medio,\n    sum(a.valor_parcela) FILTER (WHERE tm.conta_como_pagante = true) AS mrr,\n    avg(a.tempo_permanencia_meses) AS tempo_permanencia_medio\n  FROM alunos a\n  LEFT JOIN tipos_matricula tm ON tm.id = a.tipo_matricula_id\n  WHERE a.status = 'ativo'\n  GROUP BY a.unidade_id\n),\nevasoes_mes AS (\n  SELECT u.id as unidade_id, COUNT(*) as evasoes_realtime\n  FROM evasoes e\n  JOIN unidades u ON u.nome = e.unidade\n  WHERE EXTRACT(YEAR FROM e.competencia) = EXTRACT(YEAR FROM CURRENT_DATE)\n    AND EXTRACT(MONTH FROM e.competencia) = EXTRACT(MONTH FROM CURRENT_DATE)\n  GROUP BY u.id\n),\nmatriculas_mes AS (\n  SELECT unidade_id, COALESCE(SUM(quantidade), 0) as matriculas_realtime\n  FROM leads_diarios\n  WHERE tipo = 'matricula'\n    AND EXTRACT(YEAR FROM data) = EXTRACT(YEAR FROM CURRENT_DATE)\n    AND EXTRACT(MONTH FROM data) = EXTRACT(MONTH FROM CURRENT_DATE)\n  GROUP BY unidade_id\n),\nrenovacoes_mes AS (\n  SELECT unidade_id, \n    COUNT(*) as renovacoes_realtime,\n    AVG(percentual_reajuste) as reajuste_medio\n  FROM renovacoes\n  WHERE EXTRACT(YEAR FROM data_renovacao) = EXTRACT(YEAR FROM CURRENT_DATE)\n    AND EXTRACT(MONTH FROM data_renovacao) = EXTRACT(MONTH FROM CURRENT_DATE)\n    AND status = 'renovado'\n  GROUP BY unidade_id\n),\ncontratos_vencer AS (\n  SELECT unidade_id, COUNT(*) as total_vencer\n  FROM alunos\n  WHERE status = 'ativo'\n    AND EXTRACT(YEAR FROM data_fim_contrato) = EXTRACT(YEAR FROM CURRENT_DATE)\n    AND EXTRACT(MONTH FROM data_fim_contrato) = EXTRACT(MONTH FROM CURRENT_DATE)\n  GROUP BY unidade_id\n),\nalunos_mes_anterior AS (\n  SELECT unidade_id, alunos_pagantes\n  FROM dados_mensais\n  WHERE (ano = EXTRACT(YEAR FROM CURRENT_DATE) AND mes = EXTRACT(MONTH FROM CURRENT_DATE) - 1)\n     OR (ano = EXTRACT(YEAR FROM CURRENT_DATE) - 1 AND mes = 12 AND EXTRACT(MONTH FROM CURRENT_DATE) = 1)\n),\ninadimplencia_atual AS (\n  SELECT a.unidade_id,\n    COUNT(*) FILTER (WHERE a.status_pagamento = 'inadimplente') as qtd_inadimplentes,\n    COUNT(*) as total_pagantes_calc\n  FROM alunos a\n  LEFT JOIN tipos_matricula tm ON tm.id = a.tipo_matricula_id\n  WHERE a.status = 'ativo' AND (tm.conta_como_pagante = true OR tm.id IS NULL)\n  GROUP BY a.unidade_id\n)\nSELECT \n  u.id AS unidade_id,\n  u.nome AS unidade_nome,\n  u.codigo,\n  COALESCE(aa.total_ativos, 0)::INTEGER AS alunos_ativos,\n  COALESCE(aa.total_pagantes, 0)::INTEGER AS alunos_pagantes,\n  COALESCE(aa.ticket_medio, 0)::NUMERIC(10,2) AS ticket_medio,\n  COALESCE(aa.mrr, 0)::NUMERIC(12,2) AS mrr,\n  COALESCE(mm.matriculas_realtime, 0)::INTEGER AS matriculas_mes,\n  COALESCE(em.evasoes_realtime, 0)::INTEGER AS evasoes_mes,\n  -- Churn em tempo real\n  CASE \n    WHEN COALESCE(ama.alunos_pagantes, 0) > 0 \n    THEN ROUND((COALESCE(em.evasoes_realtime, 0)::NUMERIC / ama.alunos_pagantes) * 100, 2)\n    ELSE 0\n  END::NUMERIC(5,2) AS churn_rate,\n  -- Taxa renovação em tempo real\n  CASE \n    WHEN COALESCE(cv.total_vencer, 0) > 0 \n    THEN ROUND((COALESCE(rm.renovacoes_realtime, 0)::NUMERIC / cv.total_vencer) * 100, 2)\n    ELSE 0\n  END::NUMERIC(5,2) AS taxa_renovacao,\n  -- Inadimplência em tempo real\n  CASE \n    WHEN COALESCE(ia.total_pagantes_calc, 0) > 0 \n    THEN ROUND((COALESCE(ia.qtd_inadimplentes, 0)::NUMERIC / ia.total_pagantes_calc) * 100, 2)\n    ELSE 0\n  END::NUMERIC(5,2) AS inadimplencia_pct,\n  COALESCE(aa.tempo_permanencia_medio, 0)::NUMERIC(5,1) AS tempo_permanencia,\n  -- Reajuste médio em tempo real\n  COALESCE(rm.reajuste_medio, 0)::NUMERIC(5,2) AS reajuste_medio\nFROM unidades u\nLEFT JOIN alunos_ativos aa ON aa.unidade_id = u.id\nLEFT JOIN evasoes_mes em ON em.unidade_id = u.id\nLEFT JOIN matriculas_mes mm ON mm.unidade_id = u.id\nLEFT JOIN renovacoes_mes rm ON rm.unidade_id = u.id\nLEFT JOIN contratos_vencer cv ON cv.unidade_id = u.id\nLEFT JOIN alunos_mes_anterior ama ON ama.unidade_id = u.id\nLEFT JOIN inadimplencia_atual ia ON ia.unidade_id = u.id\nWHERE u.ativo = true;"}	recreate_vw_dashboard_unidade_complete	producoes.emla@gmail.com	\N	\N
