20260201161814	{"\n-- Habilitar RLS na tabela anotacoes_alunos\nALTER TABLE anotacoes_alunos ENABLE ROW LEVEL SECURITY;\n\n-- Política para SELECT: todos podem ver todas as anotações\nCREATE POLICY \\"Permitir leitura de anotações para todos\\"\nON anotacoes_alunos FOR SELECT\nTO authenticated\nUSING (true);\n\n-- Política para INSERT: todos autenticados podem criar anotações\nCREATE POLICY \\"Permitir criação de anotações para autenticados\\"\nON anotacoes_alunos FOR INSERT\nTO authenticated\nWITH CHECK (true);\n\n-- Política para UPDATE: todos autenticados podem atualizar anotações\nCREATE POLICY \\"Permitir atualização de anotações para autenticados\\"\nON anotacoes_alunos FOR UPDATE\nTO authenticated\nUSING (true)\nWITH CHECK (true);\n\n-- Política para DELETE: todos autenticados podem deletar anotações\nCREATE POLICY \\"Permitir exclusão de anotações para autenticados\\"\nON anotacoes_alunos FOR DELETE\nTO authenticated\nUSING (true);\n"}	add_rls_policies_anotacoes_alunos	producoes.emla@gmail.com	\N	\N
20260201163556	{"\n-- 1. Criar tabela de histórico de pagamentos (snapshot mensal)\nCREATE TABLE IF NOT EXISTS historico_pagamentos (\n  id SERIAL PRIMARY KEY,\n  aluno_id INTEGER NOT NULL REFERENCES alunos(id) ON DELETE CASCADE,\n  ano INTEGER NOT NULL,\n  mes INTEGER NOT NULL CHECK (mes >= 1 AND mes <= 12),\n  status_pagamento VARCHAR(50),\n  valor_parcela NUMERIC(10, 2),\n  dia_vencimento INTEGER,\n  unidade_id UUID REFERENCES unidades(id),\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  created_by VARCHAR(255),\n  \n  -- Constraint única: um registro por aluno por mês\n  UNIQUE(aluno_id, ano, mes)\n);\n\n-- Índices para performance\nCREATE INDEX IF NOT EXISTS idx_historico_pagamentos_aluno ON historico_pagamentos(aluno_id);\nCREATE INDEX IF NOT EXISTS idx_historico_pagamentos_ano_mes ON historico_pagamentos(ano, mes);\nCREATE INDEX IF NOT EXISTS idx_historico_pagamentos_unidade ON historico_pagamentos(unidade_id);\nCREATE INDEX IF NOT EXISTS idx_historico_pagamentos_status ON historico_pagamentos(status_pagamento);\n\n-- 2. Adicionar campo dia_vencimento na tabela alunos\nALTER TABLE alunos \nADD COLUMN IF NOT EXISTS dia_vencimento INTEGER DEFAULT 5 CHECK (dia_vencimento >= 1 AND dia_vencimento <= 31);\n\n-- Comentários para documentação\nCOMMENT ON TABLE historico_pagamentos IS 'Histórico mensal de status de pagamento dos alunos (snapshot antes do reset)';\nCOMMENT ON COLUMN alunos.dia_vencimento IS 'Dia do mês em que vence a mensalidade do aluno (1-31)';\n"}	add_historico_pagamentos_and_dia_vencimento	producoes.emla@gmail.com	\N	\N
20260201163607	{"\n-- Habilitar RLS na tabela historico_pagamentos\nALTER TABLE historico_pagamentos ENABLE ROW LEVEL SECURITY;\n\n-- Política para SELECT: todos autenticados podem ler\nCREATE POLICY \\"Permitir leitura de histórico para autenticados\\"\nON historico_pagamentos FOR SELECT\nTO authenticated\nUSING (true);\n\n-- Política para INSERT: todos autenticados podem criar\nCREATE POLICY \\"Permitir criação de histórico para autenticados\\"\nON historico_pagamentos FOR INSERT\nTO authenticated\nWITH CHECK (true);\n\n-- Política para UPDATE: todos autenticados podem atualizar\nCREATE POLICY \\"Permitir atualização de histórico para autenticados\\"\nON historico_pagamentos FOR UPDATE\nTO authenticated\nUSING (true)\nWITH CHECK (true);\n\n-- Política para DELETE: todos autenticados podem deletar\nCREATE POLICY \\"Permitir exclusão de histórico para autenticados\\"\nON historico_pagamentos FOR DELETE\nTO authenticated\nUSING (true);\n"}	add_rls_historico_pagamentos	producoes.emla@gmail.com	\N	\N
20260201232107	{"CREATE OR REPLACE FUNCTION get_historico_mensal_matriculador(\n  p_ano INTEGER DEFAULT 2026,\n  p_unidade_id UUID DEFAULT NULL\n)\nRETURNS JSONB\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  v_historico JSONB;\n  v_media_grupo JSONB;\nBEGIN\n  -- Buscar histórico mensal por unidade\n  SELECT jsonb_agg(dados ORDER BY mes) INTO v_historico\n  FROM (\n    SELECT \n      EXTRACT(MONTH FROM data)::int as mes,\n      u.id as unidade_id,\n      u.nome as unidade_nome,\n      SUM(CASE WHEN tipo = 'lead' THEN quantidade ELSE 0 END) as total_leads,\n      SUM(CASE WHEN tipo IN ('experimental_agendada', 'experimental_realizada', 'experimental') THEN quantidade ELSE 0 END) as total_experimentais,\n      SUM(CASE WHEN tipo = 'matricula' THEN quantidade ELSE 0 END) as total_matriculas,\n      ROUND(AVG(CASE WHEN tipo = 'matricula' AND valor_parcela > 0 THEN valor_parcela ELSE NULL END), 0) as ticket_medio,\n      -- Taxas calculadas\n      CASE \n        WHEN SUM(CASE WHEN tipo = 'lead' THEN quantidade ELSE 0 END) > 0 \n        THEN ROUND((SUM(CASE WHEN tipo IN ('experimental_agendada', 'experimental_realizada', 'experimental') THEN quantidade ELSE 0 END)::numeric / \n              SUM(CASE WHEN tipo = 'lead' THEN quantidade ELSE 0 END)) * 100, 1)\n        ELSE 0 \n      END as taxa_showup,\n      CASE \n        WHEN SUM(CASE WHEN tipo IN ('experimental_agendada', 'experimental_realizada', 'experimental') THEN quantidade ELSE 0 END) > 0 \n        THEN ROUND((SUM(CASE WHEN tipo = 'matricula' THEN quantidade ELSE 0 END)::numeric / \n              SUM(CASE WHEN tipo IN ('experimental_agendada', 'experimental_realizada', 'experimental') THEN quantidade ELSE 0 END)) * 100, 1)\n        ELSE 0 \n      END as taxa_exp_mat,\n      CASE \n        WHEN SUM(CASE WHEN tipo = 'lead' THEN quantidade ELSE 0 END) > 0 \n        THEN ROUND((SUM(CASE WHEN tipo = 'matricula' THEN quantidade ELSE 0 END)::numeric / \n              SUM(CASE WHEN tipo = 'lead' THEN quantidade ELSE 0 END)) * 100, 1)\n        ELSE 0 \n      END as taxa_geral\n    FROM leads_diarios ld\n    JOIN unidades u ON u.id = ld.unidade_id\n    WHERE EXTRACT(YEAR FROM data) = p_ano \n      AND EXTRACT(MONTH FROM data) BETWEEN 1 AND 11\n      AND (p_unidade_id IS NULL OR ld.unidade_id = p_unidade_id)\n    GROUP BY EXTRACT(MONTH FROM data), u.id, u.nome\n  ) dados;\n\n  -- Calcular média do grupo (todas as unidades) para comparativo\n  SELECT jsonb_build_object(\n    'taxa_geral', ROUND(AVG(taxa_geral), 1),\n    'volume_medio', ROUND(AVG(media_matriculas), 1),\n    'ticket_medio', ROUND(AVG(ticket_medio), 0)\n  ) INTO v_media_grupo\n  FROM (\n    SELECT \n      u.id,\n      CASE \n        WHEN SUM(CASE WHEN tipo = 'lead' THEN quantidade ELSE 0 END) > 0 \n        THEN (SUM(CASE WHEN tipo = 'matricula' THEN quantidade ELSE 0 END)::numeric / \n              SUM(CASE WHEN tipo = 'lead' THEN quantidade ELSE 0 END)) * 100\n        ELSE 0 \n      END as taxa_geral,\n      SUM(CASE WHEN tipo = 'matricula' THEN quantidade ELSE 0 END)::numeric / \n        GREATEST(COUNT(DISTINCT EXTRACT(MONTH FROM data)), 1) as media_matriculas,\n      AVG(CASE WHEN tipo = 'matricula' AND valor_parcela > 0 THEN valor_parcela ELSE NULL END) as ticket_medio\n    FROM leads_diarios ld\n    JOIN unidades u ON u.id = ld.unidade_id\n    WHERE EXTRACT(YEAR FROM data) = p_ano AND EXTRACT(MONTH FROM data) BETWEEN 1 AND 11\n    GROUP BY u.id\n  ) por_unidade;\n\n  RETURN jsonb_build_object(\n    'historico', COALESCE(v_historico, '[]'::jsonb),\n    'media_grupo', COALESCE(v_media_grupo, jsonb_build_object('taxa_geral', 0, 'volume_medio', 0, 'ticket_medio', 0))\n  );\nEND;\n$$;\n\nGRANT EXECUTE ON FUNCTION get_historico_mensal_matriculador(INTEGER, UUID) TO authenticated, anon, service_role;"}	get_historico_mensal_matriculador	producoes.emla@gmail.com	\N	\N
20260201195815	{"-- Corrigir função sync_experimentais_professor para usar professor_experimental_id em vez de professor_id\nCREATE OR REPLACE FUNCTION sync_experimentais_professor()\nRETURNS TRIGGER AS $$\nDECLARE\n  v_ano INTEGER;\n  v_mes INTEGER;\n  v_delta INTEGER;\nBEGIN\n  -- Determinar operação\n  IF TG_OP = 'DELETE' THEN\n    v_ano := EXTRACT(YEAR FROM OLD.data)::INTEGER;\n    v_mes := EXTRACT(MONTH FROM OLD.data)::INTEGER;\n    v_delta := -COALESCE(OLD.quantidade, 1);\n    \n    -- Só processa se for experimental e tiver professor\n    IF OLD.tipo LIKE 'experimental%' AND OLD.professor_experimental_id IS NOT NULL THEN\n      UPDATE experimentais_professor_mensal\n      SET experimentais = GREATEST(0, experimentais + v_delta)\n      WHERE professor_id = OLD.professor_experimental_id \n        AND unidade_id = OLD.unidade_id \n        AND ano = v_ano \n        AND mes = v_mes;\n    END IF;\n    \n    RETURN OLD;\n  ELSE\n    v_ano := EXTRACT(YEAR FROM NEW.data)::INTEGER;\n    v_mes := EXTRACT(MONTH FROM NEW.data)::INTEGER;\n    v_delta := COALESCE(NEW.quantidade, 1);\n    \n    -- Só processa se for experimental e tiver professor\n    IF NEW.tipo LIKE 'experimental%' AND NEW.professor_experimental_id IS NOT NULL THEN\n      INSERT INTO experimentais_professor_mensal (professor_id, unidade_id, ano, mes, experimentais)\n      VALUES (NEW.professor_experimental_id, NEW.unidade_id, v_ano, v_mes, v_delta)\n      ON CONFLICT (professor_id, unidade_id, ano, mes) \n      DO UPDATE SET experimentais = experimentais_professor_mensal.experimentais + v_delta;\n    END IF;\n    \n    RETURN NEW;\n  END IF;\nEND;\n$$ LANGUAGE plpgsql;"}	fix_sync_experimentais_professor_trigger	producoes.emla@gmail.com	\N	\N
