20260203015607	{"CREATE OR REPLACE FUNCTION calcular_comissao_venda()\nRETURNS TRIGGER AS $$\nDECLARE\n  v_comissao_percentual NUMERIC;\n  v_valor_comissao NUMERIC;\n  v_carteira_id INTEGER;\n  v_saldo_atual NUMERIC;\nBEGIN\n  -- Só processa se a venda tem vendedor (vendedor_id)\n  IF NEW.vendedor_id IS NULL THEN\n    RETURN NEW;\n  END IF;\n\n  -- Buscar percentual de comissão das configurações (global, sem filtro por unidade)\n  SELECT COALESCE(\n    (SELECT valor::NUMERIC FROM loja_configuracoes WHERE chave = 'comissao_farmer_venda' LIMIT 1),\n    5.0 -- Padrão 5%\n  ) INTO v_comissao_percentual;\n\n  -- Calcular valor da comissão\n  v_valor_comissao := (NEW.total * v_comissao_percentual) / 100;\n\n  -- Buscar ou criar carteira do colaborador\n  SELECT id, saldo INTO v_carteira_id, v_saldo_atual\n  FROM loja_carteira\n  WHERE colaborador_id = NEW.vendedor_id AND tipo_titular = 'farmer'\n  LIMIT 1;\n\n  IF v_carteira_id IS NULL THEN\n    -- Criar carteira\n    INSERT INTO loja_carteira (tipo_titular, colaborador_id, unidade_id, saldo, moedas_la)\n    VALUES ('farmer', NEW.vendedor_id, NEW.unidade_id, 0, 0)\n    RETURNING id, saldo INTO v_carteira_id, v_saldo_atual;\n  END IF;\n\n  -- Atualizar saldo da carteira\n  UPDATE loja_carteira\n  SET saldo = saldo + v_valor_comissao,\n      updated_at = NOW()\n  WHERE id = v_carteira_id;\n\n  -- Registrar movimentação com colunas corretas\n  INSERT INTO loja_carteira_movimentacoes (carteira_id, tipo, valor, saldo_apos, referencia_tipo, referencia_id, descricao, created_at)\n  VALUES (v_carteira_id, 'comissao_venda', v_valor_comissao, v_saldo_atual + v_valor_comissao, 'venda', NEW.id, 'Comissão de venda #' || NEW.id, NOW());\n\n  RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql;"}	fix_calcular_comissao_venda_trigger_v2	producoes.emla@gmail.com	\N	\N
20260203031913	{"-- Corrigir função get_dados_retencao_ia para usar colunas corretas da view vw_kpis_gestao_mensal\nCREATE OR REPLACE FUNCTION public.get_dados_retencao_ia(\n  p_unidade_id uuid DEFAULT NULL::uuid, \n  p_ano integer DEFAULT (EXTRACT(year FROM CURRENT_DATE))::integer, \n  p_mes integer DEFAULT (EXTRACT(month FROM CURRENT_DATE))::integer\n)\nRETURNS json\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $function$\nDECLARE\n  resultado JSON;\n  mes_anterior INT;\n  ano_anterior INT;\n  ano_passado INT;\nBEGIN\n  -- Calcular mês anterior\n  IF p_mes = 1 THEN\n    mes_anterior := 12;\n    ano_anterior := p_ano - 1;\n  ELSE\n    mes_anterior := p_mes - 1;\n    ano_anterior := p_ano;\n  END IF;\n  ano_passado := p_ano - 1;\n\n  SELECT json_build_object(\n    'periodo', json_build_object(\n      'ano', p_ano,\n      'mes', p_mes,\n      'mes_nome', TRIM(TO_CHAR(TO_DATE(p_mes::text, 'MM'), 'Month'))\n    ),\n    \n    -- CORRIGIDO: usar nomes corretos das colunas da view vw_kpis_gestao_mensal\n    'kpis_gestao', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'unidade_id', kg.unidade_id,\n        'unidade_nome', kg.unidade_nome,\n        'total_alunos_ativos', COALESCE(kg.total_alunos, 0),\n        'total_alunos_pagantes', COALESCE(kg.alunos_pagantes, 0),\n        'ticket_medio', COALESCE(kg.ticket_medio, 0),\n        'mrr', COALESCE(kg.mrr, 0),\n        'tempo_permanencia_medio', 0,\n        'ltv_medio', 0,\n        'inadimplencia_pct', 0,\n        'faturamento_previsto', COALESCE(kg.mrr, 0),\n        'faturamento_realizado', COALESCE(kg.mrr, 0),\n        'churn_rate', COALESCE(kg.churn_rate, 0),\n        'total_evasoes', COALESCE(kg.evasoes, 0)\n      )), '[]'::json)\n      FROM vw_kpis_gestao_mensal kg\n      WHERE (p_unidade_id IS NULL OR kg.unidade_id = p_unidade_id)\n        AND kg.ano = p_ano\n        AND kg.mes = p_mes\n    ),\n    \n    'kpis_retencao', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'unidade_id', kr.unidade_id,\n        'unidade_nome', kr.unidade_nome,\n        'total_evasoes', kr.total_evasoes,\n        'avisos_previos', kr.avisos_previos,\n        'renovacoes_previstas', kr.renovacoes_previstas,\n        'renovacoes_realizadas', kr.renovacoes_realizadas,\n        'nao_renovacoes', kr.nao_renovacoes,\n        'renovacoes_pendentes', kr.renovacoes_pendentes,\n        'taxa_renovacao', kr.taxa_renovacao,\n        'taxa_nao_renovacao', kr.taxa_nao_renovacao,\n        'mrr_perdido', kr.mrr_perdido\n      )), '[]'::json)\n      FROM vw_kpis_retencao_mensal kr\n      WHERE (p_unidade_id IS NULL OR kr.unidade_id = p_unidade_id)\n        AND kr.ano = p_ano\n        AND kr.mes = p_mes\n    ),\n    \n    'renovacoes_proximas', (\n      SELECT COALESCE(json_agg(row_to_json(rp)), '[]'::json)\n      FROM get_resumo_renovacoes_proximas(p_unidade_id) rp\n    ),\n    \n    'alunos_renovacao_urgente', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'aluno_nome', rp.aluno_nome,\n        'professor_nome', rp.professor_nome,\n        'curso_nome', rp.curso_nome,\n        'valor_parcela', rp.valor_parcela,\n        'dias_ate_vencimento', rp.dias_ate_vencimento,\n        'tempo_permanencia_meses', rp.tempo_permanencia_meses,\n        'telefone', rp.telefone\n      ) ORDER BY rp.dias_ate_vencimento), '[]'::json)\n      FROM vw_renovacoes_proximas rp\n      WHERE (p_unidade_id IS NULL OR rp.unidade_id = p_unidade_id)\n        AND rp.status_renovacao IN ('vencido', 'urgente_7_dias')\n      LIMIT 20\n    ),\n    \n    'mes_anterior', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'alunos_pagantes', dm.alunos_pagantes,\n        'churn_rate', dm.churn_rate,\n        'ticket_medio', dm.ticket_medio,\n        'taxa_renovacao', dm.taxa_renovacao,\n        'inadimplencia', dm.inadimplencia,\n        'tempo_permanencia', dm.tempo_permanencia,\n        'reajuste_parcelas', dm.reajuste_parcelas\n      )), '[]'::json)\n      FROM dados_mensais dm\n      WHERE (p_unidade_id IS NULL OR dm.unidade_id = p_unidade_id)\n        AND dm.ano = ano_anterior\n        AND dm.mes = mes_anterior\n    ),\n    \n    'mesmo_mes_ano_passado', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'alunos_pagantes', dm.alunos_pagantes,\n        'churn_rate', dm.churn_rate,\n        'ticket_medio', dm.ticket_medio,\n        'taxa_renovacao', dm.taxa_renovacao,\n        'inadimplencia', dm.inadimplencia,\n        'tempo_permanencia', dm.tempo_permanencia,\n        'reajuste_parcelas', dm.reajuste_parcelas\n      )), '[]'::json)\n      FROM dados_mensais dm\n      WHERE (p_unidade_id IS NULL OR dm.unidade_id = p_unidade_id)\n        AND dm.ano = ano_passado\n        AND dm.mes = p_mes\n    ),\n    \n    'metas', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'unidade_id', m.unidade_id,\n        'meta_leads', m.meta_leads,\n        'meta_experimentais', m.meta_experimentais,\n        'meta_matriculas', m.meta_matriculas,\n        'meta_taxa_conversao_experimental', m.meta_taxa_conversao_experimental,\n        'meta_taxa_conversao_lead', m.meta_taxa_conversao_lead,\n        'meta_faturamento_passaportes', m.meta_faturamento_passaportes,\n        'meta_alunos_pagantes', m.meta_alunos_pagantes,\n        'meta_alunos_ativos', m.meta_alunos_ativos,\n        'meta_ticket_medio', m.meta_ticket_medio,\n        'meta_churn_maximo', m.meta_churn_maximo,\n        'meta_evasoes_maximo', m.meta_evasoes_maximo,\n        'meta_renovacoes', m.meta_renovacoes,\n        'meta_taxa_renovacao', m.meta_taxa_renovacao,\n        'meta_inadimplencia_maxima', m.meta_inadimplencia_maxima,\n        'meta_ltv_meses', m.meta_ltv_meses,\n        'meta_faturamento_parcelas', m.meta_faturamento_parcelas\n      )), '[]'::json)\n      FROM metas m\n      WHERE (p_unidade_id IS NULL OR m.unidade_id = p_unidade_id OR m.unidade_id IS NULL)\n        AND m.ano = p_ano\n        AND (m.mes = p_mes OR m.mes IS NULL)\n        AND m.ativo = true\n    ),\n    \n    'evasoes_recentes', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'aluno_nome', a.nome,\n        'professor_nome', p.nome,\n        'motivo', ms.nome,\n        'tipo_saida', ts.nome,\n        'valor_parcela', e.valor_parcela,\n        'tempo_permanencia', a.tempo_permanencia_meses,\n        'data_saida', e.data_evasao\n      ) ORDER BY e.data_evasao DESC), '[]'::json)\n      FROM evasoes_v2 e\n      LEFT JOIN alunos a ON e.aluno_id = a.id\n      LEFT JOIN professores p ON e.professor_id = p.id\n      LEFT JOIN motivos_saida ms ON e.motivo_saida_id = ms.id\n      LEFT JOIN tipos_saida ts ON e.tipo_saida_id = ts.id\n      WHERE (p_unidade_id IS NULL OR e.unidade_id = p_unidade_id)\n        AND e.data_evasao >= (CURRENT_DATE - INTERVAL '30 days')\n      LIMIT 15\n    ),\n    \n    'permanencia_por_faixa', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'faixa', faixa,\n        'quantidade', quantidade,\n        'percentual', ROUND((quantidade::numeric / NULLIF(total, 0) * 100), 1)\n      ) ORDER BY ordem), '[]'::json)\n      FROM (\n        SELECT \n          CASE \n            WHEN tempo_permanencia_meses < 6 THEN '0-6 meses'\n            WHEN tempo_permanencia_meses < 12 THEN '6-12 meses'\n            WHEN tempo_permanencia_meses < 24 THEN '1-2 anos'\n            WHEN tempo_permanencia_meses < 36 THEN '2-3 anos'\n            ELSE '3+ anos'\n          END as faixa,\n          CASE \n            WHEN tempo_permanencia_meses < 6 THEN 1\n            WHEN tempo_permanencia_meses < 12 THEN 2\n            WHEN tempo_permanencia_meses < 24 THEN 3\n            WHEN tempo_permanencia_meses < 36 THEN 4\n            ELSE 5\n          END as ordem,\n          COUNT(*) as quantidade,\n          SUM(COUNT(*)) OVER () as total\n        FROM alunos\n        WHERE status = 'ativo'\n          AND (p_unidade_id IS NULL OR unidade_id = p_unidade_id)\n        GROUP BY faixa, ordem\n      ) sub\n    ),\n    \n    'dados_mes_atual', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'alunos_pagantes', dm.alunos_pagantes,\n        'novas_matriculas', dm.novas_matriculas,\n        'evasoes', dm.evasoes,\n        'churn_rate', dm.churn_rate,\n        'ticket_medio', dm.ticket_medio,\n        'taxa_renovacao', dm.taxa_renovacao,\n        'tempo_permanencia', dm.tempo_permanencia,\n        'inadimplencia', dm.inadimplencia,\n        'reajuste_parcelas', dm.reajuste_parcelas,\n        'faturamento_estimado', dm.faturamento_estimado,\n        'saldo_liquido', dm.saldo_liquido\n      )), '[]'::json)\n      FROM dados_mensais dm\n      WHERE (p_unidade_id IS NULL OR dm.unidade_id = p_unidade_id)\n        AND dm.ano = p_ano\n        AND dm.mes = p_mes\n    )\n    \n  ) INTO resultado;\n  \n  RETURN resultado;\nEND;\n$function$;"}	fix_get_dados_retencao_ia_columns	producoes.emla@gmail.com	\N	\N
20260203040631	{"-- Recriar a view com os nomes de colunas corretos\nDROP VIEW IF EXISTS vw_kpis_gestao_mensal;\n\nCREATE OR REPLACE VIEW vw_kpis_gestao_mensal AS\nWITH leads_mes AS (\n    SELECT \n        unidade_id,\n        EXTRACT(year FROM data)::integer AS ano,\n        EXTRACT(month FROM data)::integer AS mes,\n        SUM(CASE WHEN tipo = 'lead' THEN quantidade ELSE 0 END) AS total_leads,\n        SUM(CASE WHEN tipo = 'experimental_agendada' THEN quantidade ELSE 0 END) AS experimentais_agendadas,\n        SUM(CASE WHEN tipo = 'experimental_realizada' THEN quantidade ELSE 0 END) AS experimentais_realizadas,\n        SUM(CASE WHEN tipo = 'experimental_faltou' THEN quantidade ELSE 0 END) AS faltaram,\n        SUM(CASE WHEN tipo = 'matricula' THEN quantidade ELSE 0 END) AS novas_matriculas,\n        SUM(CASE WHEN arquivado = true THEN quantidade ELSE 0 END) AS leads_arquivados\n    FROM leads_diarios\n    GROUP BY unidade_id, EXTRACT(year FROM data), EXTRACT(month FROM data)\n),\nalunos_mes AS (\n    SELECT \n        a.unidade_id,\n        EXTRACT(year FROM CURRENT_DATE)::integer AS ano,\n        EXTRACT(month FROM CURRENT_DATE)::integer AS mes,\n        COUNT(*) AS total_alunos,\n        COUNT(*) FILTER (WHERE tm.conta_como_pagante = true) AS alunos_pagantes,\n        COUNT(*) FILTER (WHERE tm.codigo = 'BOLSISTA_INT') AS bolsistas_integrais,\n        COUNT(*) FILTER (WHERE tm.codigo = 'BOLSISTA_PARC') AS bolsistas_parciais,\n        COUNT(*) FILTER (WHERE tm.codigo = 'BANDA') AS total_banda,\n        COUNT(*) FILTER (WHERE a.is_segundo_curso = true) AS segundo_curso,\n        AVG(a.valor_parcela) FILTER (WHERE tm.entra_ticket_medio = true) AS ticket_medio,\n        SUM(a.valor_parcela) FILTER (WHERE tm.conta_como_pagante = true) AS mrr,\n        AVG(a.tempo_permanencia_meses) AS tempo_permanencia_medio\n    FROM alunos a\n    LEFT JOIN tipos_matricula tm ON tm.id = a.tipo_matricula_id\n    WHERE a.status = 'ativo'\n    GROUP BY a.unidade_id\n),\nevasoes_mes AS (\n    SELECT \n        u.id AS unidade_id,\n        EXTRACT(year FROM e.competencia)::integer AS ano,\n        EXTRACT(month FROM e.competencia)::integer AS mes,\n        COUNT(*) AS total_evasoes\n    FROM evasoes e\n    JOIN unidades u ON u.nome = e.unidade\n    GROUP BY u.id, EXTRACT(year FROM e.competencia), EXTRACT(month FROM e.competencia)\n),\nrenovacoes_mes AS (\n    SELECT \n        unidade_id,\n        EXTRACT(year FROM data_renovacao)::integer AS ano,\n        EXTRACT(month FROM data_renovacao)::integer AS mes,\n        COUNT(*) FILTER (WHERE status = 'renovado') AS renovacoes,\n        COUNT(*) AS total_contratos,\n        AVG(percentual_reajuste) FILTER (WHERE status = 'renovado') AS reajuste_medio\n    FROM renovacoes\n    GROUP BY unidade_id, EXTRACT(year FROM data_renovacao), EXTRACT(month FROM data_renovacao)\n),\ndados_anterior AS (\n    SELECT unidade_id, ano, mes, alunos_pagantes\n    FROM dados_mensais\n)\nSELECT \n    u.id AS unidade_id,\n    u.nome AS unidade_nome,\n    COALESCE(lm.ano, am.ano, EXTRACT(year FROM CURRENT_DATE)::integer) AS ano,\n    COALESCE(lm.mes, am.mes, EXTRACT(month FROM CURRENT_DATE)::integer) AS mes,\n    -- Nomes corrigidos para compatibilidade com o frontend\n    COALESCE(am.total_alunos, 0)::integer AS total_alunos_ativos,\n    COALESCE(am.alunos_pagantes, 0)::integer AS total_alunos_pagantes,\n    COALESCE(am.bolsistas_integrais, 0)::integer AS total_bolsistas_integrais,\n    COALESCE(am.bolsistas_parciais, 0)::integer AS total_bolsistas_parciais,\n    COALESCE(am.total_banda, 0)::integer AS total_banda,\n    COALESCE(am.segundo_curso, 0)::integer AS total_segundo_curso,\n    COALESCE(am.ticket_medio, 0)::numeric(10,2) AS ticket_medio,\n    COALESCE(am.mrr, 0)::numeric(12,2) AS mrr,\n    (COALESCE(am.mrr, 0) * 12)::numeric(14,2) AS arr,\n    COALESCE(am.tempo_permanencia_medio, 0)::numeric(5,1) AS tempo_permanencia_medio,\n    (COALESCE(am.ticket_medio, 0) * COALESCE(am.tempo_permanencia_medio, 0))::numeric(12,2) AS ltv_medio,\n    0::numeric(5,2) AS inadimplencia_pct,\n    COALESCE(am.mrr, 0)::numeric(12,2) AS faturamento_previsto,\n    COALESCE(am.mrr, 0)::numeric(12,2) AS faturamento_realizado,\n    COALESCE(lm.total_leads, 0)::integer AS total_leads,\n    COALESCE(lm.experimentais_agendadas, 0)::integer AS experimentais_agendadas,\n    COALESCE(lm.experimentais_realizadas, 0)::integer AS experimentais_realizadas,\n    COALESCE(lm.novas_matriculas, 0)::integer AS novas_matriculas,\n    COALESCE(em.total_evasoes, 0)::integer AS total_evasoes,\n    CASE \n        WHEN COALESCE(da.alunos_pagantes, 0) > 0 \n        THEN ROUND(COALESCE(em.total_evasoes, 0)::numeric / da.alunos_pagantes * 100, 2)\n        ELSE 0\n    END::numeric(5,2) AS churn_rate,\n    COALESCE(rm.renovacoes, 0)::integer AS renovacoes,\n    CASE \n        WHEN COALESCE(rm.total_contratos, 0) > 0 \n        THEN ROUND(rm.renovacoes::numeric / rm.total_contratos * 100, 2)\n        ELSE 0\n    END::numeric(5,2) AS taxa_renovacao,\n    COALESCE(rm.reajuste_medio, 0)::numeric(5,2) AS reajuste_medio\nFROM unidades u\nLEFT JOIN leads_mes lm ON lm.unidade_id = u.id\nLEFT JOIN alunos_mes am ON am.unidade_id = u.id\nLEFT JOIN evasoes_mes em ON em.unidade_id = u.id AND em.ano = COALESCE(lm.ano, am.ano) AND em.mes = COALESCE(lm.mes, am.mes)\nLEFT JOIN renovacoes_mes rm ON rm.unidade_id = u.id AND rm.ano = COALESCE(lm.ano, am.ano) AND rm.mes = COALESCE(lm.mes, am.mes)\nLEFT JOIN dados_anterior da ON da.unidade_id = u.id AND da.ano = COALESCE(lm.ano, am.ano) AND da.mes = (COALESCE(lm.mes, am.mes) - 1)\nWHERE u.ativo = true;"}	fix_vw_kpis_gestao_mensal_column_names_v2	producoes.emla@gmail.com	\N	\N
20260203102511	{"\n-- ============================================\n-- PAINEL FARMER - TABELAS PRINCIPAIS\n-- ============================================\n\n-- Tabela: farmer_rotinas (definição de rotinas customizáveis)\nCREATE TABLE IF NOT EXISTS farmer_rotinas (\n  id UUID PRIMARY KEY DEFAULT extensions.uuid_generate_v4(),\n  colaborador_id INTEGER NOT NULL REFERENCES colaboradores(id) ON DELETE CASCADE,\n  unidade_id UUID NOT NULL REFERENCES unidades(id),\n  descricao VARCHAR(255) NOT NULL,\n  frequencia VARCHAR(20) NOT NULL CHECK (frequencia IN ('diario', 'semanal', 'mensal')),\n  dias_semana INTEGER[] DEFAULT NULL, -- [1,2,3,4,5,6] para semanal (1=seg, 7=dom)\n  dia_mes INTEGER DEFAULT NULL CHECK (dia_mes IS NULL OR (dia_mes >= 1 AND dia_mes <= 31)),\n  prioridade VARCHAR(10) DEFAULT 'normal' CHECK (prioridade IN ('normal', 'alta')),\n  lembrete_whatsapp BOOLEAN DEFAULT false,\n  ativo BOOLEAN DEFAULT true,\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  updated_at TIMESTAMPTZ DEFAULT NOW()\n);\n\n-- Índices para farmer_rotinas\nCREATE INDEX IF NOT EXISTS idx_farmer_rotinas_colaborador ON farmer_rotinas(colaborador_id);\nCREATE INDEX IF NOT EXISTS idx_farmer_rotinas_unidade ON farmer_rotinas(unidade_id);\nCREATE INDEX IF NOT EXISTS idx_farmer_rotinas_ativo ON farmer_rotinas(ativo) WHERE ativo = true;\n\n-- Tabela: farmer_rotinas_execucao (execução diária das rotinas)\nCREATE TABLE IF NOT EXISTS farmer_rotinas_execucao (\n  id UUID PRIMARY KEY DEFAULT extensions.uuid_generate_v4(),\n  rotina_id UUID NOT NULL REFERENCES farmer_rotinas(id) ON DELETE CASCADE,\n  colaborador_id INTEGER NOT NULL REFERENCES colaboradores(id),\n  data_execucao DATE NOT NULL,\n  concluida BOOLEAN DEFAULT false,\n  concluida_em TIMESTAMPTZ DEFAULT NULL,\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  UNIQUE(rotina_id, data_execucao)\n);\n\n-- Índices para farmer_rotinas_execucao\nCREATE INDEX IF NOT EXISTS idx_farmer_rotinas_exec_data ON farmer_rotinas_execucao(data_execucao);\nCREATE INDEX IF NOT EXISTS idx_farmer_rotinas_exec_colaborador ON farmer_rotinas_execucao(colaborador_id);\nCREATE INDEX IF NOT EXISTS idx_farmer_rotinas_exec_rotina ON farmer_rotinas_execucao(rotina_id);\n\n-- Tabela: farmer_tarefas (to-do list manual)\nCREATE TABLE IF NOT EXISTS farmer_tarefas (\n  id UUID PRIMARY KEY DEFAULT extensions.uuid_generate_v4(),\n  colaborador_id INTEGER NOT NULL REFERENCES colaboradores(id) ON DELETE CASCADE,\n  unidade_id UUID NOT NULL REFERENCES unidades(id),\n  descricao VARCHAR(500) NOT NULL,\n  data_prazo DATE DEFAULT NULL,\n  prioridade VARCHAR(10) DEFAULT 'media' CHECK (prioridade IN ('alta', 'media', 'baixa')),\n  aluno_id INTEGER DEFAULT NULL REFERENCES alunos(id),\n  observacoes TEXT DEFAULT NULL,\n  concluida BOOLEAN DEFAULT false,\n  concluida_em TIMESTAMPTZ DEFAULT NULL,\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  updated_at TIMESTAMPTZ DEFAULT NOW()\n);\n\n-- Índices para farmer_tarefas\nCREATE INDEX IF NOT EXISTS idx_farmer_tarefas_colaborador ON farmer_tarefas(colaborador_id);\nCREATE INDEX IF NOT EXISTS idx_farmer_tarefas_unidade ON farmer_tarefas(unidade_id);\nCREATE INDEX IF NOT EXISTS idx_farmer_tarefas_prazo ON farmer_tarefas(data_prazo);\nCREATE INDEX IF NOT EXISTS idx_farmer_tarefas_pendentes ON farmer_tarefas(concluida) WHERE concluida = false;\n\n-- Tabela: farmer_recados (mensagens para professores)\nCREATE TABLE IF NOT EXISTS farmer_recados (\n  id UUID PRIMARY KEY DEFAULT extensions.uuid_generate_v4(),\n  colaborador_id INTEGER NOT NULL REFERENCES colaboradores(id),\n  unidade_id UUID NOT NULL REFERENCES unidades(id),\n  professor_id INTEGER NOT NULL REFERENCES professores(id),\n  aluno_id INTEGER DEFAULT NULL REFERENCES alunos(id),\n  assunto VARCHAR(100) DEFAULT NULL,\n  mensagem TEXT NOT NULL,\n  status VARCHAR(20) DEFAULT 'enviado' CHECK (status IN ('enviado', 'entregue', 'lido', 'erro')),\n  whatsapp_message_id VARCHAR(100) DEFAULT NULL,\n  enviado_em TIMESTAMPTZ DEFAULT NOW(),\n  entregue_em TIMESTAMPTZ DEFAULT NULL,\n  lido_em TIMESTAMPTZ DEFAULT NULL,\n  created_at TIMESTAMPTZ DEFAULT NOW()\n);\n\n-- Índices para farmer_recados\nCREATE INDEX IF NOT EXISTS idx_farmer_recados_colaborador ON farmer_recados(colaborador_id);\nCREATE INDEX IF NOT EXISTS idx_farmer_recados_professor ON farmer_recados(professor_id);\nCREATE INDEX IF NOT EXISTS idx_farmer_recados_status ON farmer_recados(status);\nCREATE INDEX IF NOT EXISTS idx_farmer_recados_data ON farmer_recados(enviado_em DESC);\n\n-- Tabela: farmer_templates (templates de mensagens)\nCREATE TABLE IF NOT EXISTS farmer_templates (\n  id UUID PRIMARY KEY DEFAULT extensions.uuid_generate_v4(),\n  unidade_id UUID DEFAULT NULL REFERENCES unidades(id), -- NULL = global\n  categoria VARCHAR(50) NOT NULL, -- 'aniversario', 'boas_vindas', 'renovacao', 'cobranca', 'experimental', 'faltoso'\n  nome VARCHAR(100) NOT NULL,\n  mensagem TEXT NOT NULL,\n  variaveis TEXT[] DEFAULT NULL, -- ['{nome}', '{instrumento}', '{data}', '{horario}']\n  ativo BOOLEAN DEFAULT true,\n  ordem INTEGER DEFAULT 0,\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  updated_at TIMESTAMPTZ DEFAULT NOW()\n);\n\n-- Índices para farmer_templates\nCREATE INDEX IF NOT EXISTS idx_farmer_templates_categoria ON farmer_templates(categoria);\nCREATE INDEX IF NOT EXISTS idx_farmer_templates_unidade ON farmer_templates(unidade_id);\n\n-- Comentários nas tabelas\nCOMMENT ON TABLE farmer_rotinas IS 'Rotinas customizáveis dos Farmers (diárias, semanais, mensais)';\nCOMMENT ON TABLE farmer_rotinas_execucao IS 'Registro de execução diária das rotinas';\nCOMMENT ON TABLE farmer_tarefas IS 'To-do list manual dos Farmers';\nCOMMENT ON TABLE farmer_recados IS 'Mensagens enviadas para professores via WhatsApp';\nCOMMENT ON TABLE farmer_templates IS 'Templates de mensagens para comunicação com alunos/responsáveis';\n"}	create_farmer_rotinas_tables	producoes.emla@gmail.com	\N	\N
20260203102538	{"\n-- ============================================\n-- PAINEL FARMER - RLS POLICIES\n-- ============================================\n\n-- Habilitar RLS em todas as tabelas\nALTER TABLE farmer_rotinas ENABLE ROW LEVEL SECURITY;\nALTER TABLE farmer_rotinas_execucao ENABLE ROW LEVEL SECURITY;\nALTER TABLE farmer_tarefas ENABLE ROW LEVEL SECURITY;\nALTER TABLE farmer_recados ENABLE ROW LEVEL SECURITY;\nALTER TABLE farmer_templates ENABLE ROW LEVEL SECURITY;\n\n-- ========== farmer_rotinas ==========\n-- Farmers veem suas próprias rotinas, gerentes/admin veem da unidade\nCREATE POLICY \\"farmer_rotinas_select\\" ON farmer_rotinas FOR SELECT\nUSING (\n  colaborador_id IN (SELECT id FROM colaboradores WHERE usuario_id = auth.uid())\n  OR\n  EXISTS (\n    SELECT 1 FROM colaboradores c \n    WHERE c.usuario_id = auth.uid() \n    AND c.tipo IN ('gerente', 'admin')\n    AND (c.unidade_id = farmer_rotinas.unidade_id OR c.tipo = 'admin')\n  )\n  OR\n  -- Permitir acesso se não há colaborador vinculado (desenvolvimento)\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n\nCREATE POLICY \\"farmer_rotinas_insert\\" ON farmer_rotinas FOR INSERT\nWITH CHECK (\n  colaborador_id IN (SELECT id FROM colaboradores WHERE usuario_id = auth.uid())\n  OR\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n\nCREATE POLICY \\"farmer_rotinas_update\\" ON farmer_rotinas FOR UPDATE\nUSING (\n  colaborador_id IN (SELECT id FROM colaboradores WHERE usuario_id = auth.uid())\n  OR\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n\nCREATE POLICY \\"farmer_rotinas_delete\\" ON farmer_rotinas FOR DELETE\nUSING (\n  colaborador_id IN (SELECT id FROM colaboradores WHERE usuario_id = auth.uid())\n  OR\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n\n-- ========== farmer_rotinas_execucao ==========\nCREATE POLICY \\"farmer_rotinas_exec_select\\" ON farmer_rotinas_execucao FOR SELECT\nUSING (\n  colaborador_id IN (SELECT id FROM colaboradores WHERE usuario_id = auth.uid())\n  OR\n  EXISTS (\n    SELECT 1 FROM colaboradores c \n    WHERE c.usuario_id = auth.uid() \n    AND c.tipo IN ('gerente', 'admin')\n  )\n  OR\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n\nCREATE POLICY \\"farmer_rotinas_exec_insert\\" ON farmer_rotinas_execucao FOR INSERT\nWITH CHECK (\n  colaborador_id IN (SELECT id FROM colaboradores WHERE usuario_id = auth.uid())\n  OR\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n\nCREATE POLICY \\"farmer_rotinas_exec_update\\" ON farmer_rotinas_execucao FOR UPDATE\nUSING (\n  colaborador_id IN (SELECT id FROM colaboradores WHERE usuario_id = auth.uid())\n  OR\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n\n-- ========== farmer_tarefas ==========\nCREATE POLICY \\"farmer_tarefas_select\\" ON farmer_tarefas FOR SELECT\nUSING (\n  colaborador_id IN (SELECT id FROM colaboradores WHERE usuario_id = auth.uid())\n  OR\n  EXISTS (\n    SELECT 1 FROM colaboradores c \n    WHERE c.usuario_id = auth.uid() \n    AND c.tipo IN ('gerente', 'admin')\n    AND (c.unidade_id = farmer_tarefas.unidade_id OR c.tipo = 'admin')\n  )\n  OR\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n\nCREATE POLICY \\"farmer_tarefas_insert\\" ON farmer_tarefas FOR INSERT\nWITH CHECK (\n  colaborador_id IN (SELECT id FROM colaboradores WHERE usuario_id = auth.uid())\n  OR\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n\nCREATE POLICY \\"farmer_tarefas_update\\" ON farmer_tarefas FOR UPDATE\nUSING (\n  colaborador_id IN (SELECT id FROM colaboradores WHERE usuario_id = auth.uid())\n  OR\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n\nCREATE POLICY \\"farmer_tarefas_delete\\" ON farmer_tarefas FOR DELETE\nUSING (\n  colaborador_id IN (SELECT id FROM colaboradores WHERE usuario_id = auth.uid())\n  OR\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n\n-- ========== farmer_recados ==========\nCREATE POLICY \\"farmer_recados_select\\" ON farmer_recados FOR SELECT\nUSING (\n  colaborador_id IN (SELECT id FROM colaboradores WHERE usuario_id = auth.uid())\n  OR\n  EXISTS (\n    SELECT 1 FROM colaboradores c \n    WHERE c.usuario_id = auth.uid() \n    AND c.tipo IN ('gerente', 'admin')\n  )\n  OR\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n\nCREATE POLICY \\"farmer_recados_insert\\" ON farmer_recados FOR INSERT\nWITH CHECK (\n  colaborador_id IN (SELECT id FROM colaboradores WHERE usuario_id = auth.uid())\n  OR\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n\n-- ========== farmer_templates ==========\n-- Templates são públicos para leitura (globais ou da unidade)\nCREATE POLICY \\"farmer_templates_select\\" ON farmer_templates FOR SELECT\nUSING (\n  unidade_id IS NULL -- globais\n  OR\n  unidade_id IN (SELECT unidade_id FROM colaboradores WHERE usuario_id = auth.uid())\n  OR\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n\n-- Apenas admin pode criar/editar templates\nCREATE POLICY \\"farmer_templates_insert\\" ON farmer_templates FOR INSERT\nWITH CHECK (\n  EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid() AND tipo = 'admin')\n  OR\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n\nCREATE POLICY \\"farmer_templates_update\\" ON farmer_templates FOR UPDATE\nUSING (\n  EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid() AND tipo = 'admin')\n  OR\n  NOT EXISTS (SELECT 1 FROM colaboradores WHERE usuario_id = auth.uid())\n);\n"}	create_farmer_rls_policies	producoes.emla@gmail.com	\N	\N
20260203102606	{"\n-- ============================================\n-- PAINEL FARMER - FUNCTIONS\n-- ============================================\n\n-- Função: get_rotinas_do_dia\n-- Retorna as rotinas que devem aparecer para um colaborador em uma data específica\nCREATE OR REPLACE FUNCTION get_rotinas_do_dia(\n  p_colaborador_id INTEGER, \n  p_data DATE DEFAULT CURRENT_DATE\n)\nRETURNS TABLE (\n  rotina_id UUID,\n  descricao VARCHAR,\n  frequencia VARCHAR,\n  prioridade VARCHAR,\n  concluida BOOLEAN,\n  execucao_id UUID\n) AS $$\nBEGIN\n  RETURN QUERY\n  SELECT \n    r.id as rotina_id,\n    r.descricao,\n    r.frequencia,\n    r.prioridade,\n    COALESCE(e.concluida, false) as concluida,\n    e.id as execucao_id\n  FROM farmer_rotinas r\n  LEFT JOIN farmer_rotinas_execucao e ON e.rotina_id = r.id AND e.data_execucao = p_data\n  WHERE r.colaborador_id = p_colaborador_id\n    AND r.ativo = true\n    AND (\n      -- Diário: sempre aparece\n      r.frequencia = 'diario'\n      OR\n      -- Semanal: aparece no dia da semana configurado (1=seg, 7=dom)\n      (r.frequencia = 'semanal' AND EXTRACT(ISODOW FROM p_data)::INTEGER = ANY(r.dias_semana))\n      OR\n      -- Mensal: aparece no dia do mês configurado\n      (r.frequencia = 'mensal' AND EXTRACT(DAY FROM p_data)::INTEGER = r.dia_mes)\n    )\n  ORDER BY \n    CASE r.prioridade WHEN 'alta' THEN 0 ELSE 1 END,\n    r.descricao;\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER;\n\n-- Função: marcar_rotina_concluida\n-- Marca uma rotina como concluída (ou desmarca) para uma data\nCREATE OR REPLACE FUNCTION marcar_rotina_concluida(\n  p_rotina_id UUID,\n  p_colaborador_id INTEGER,\n  p_concluida BOOLEAN DEFAULT true,\n  p_data DATE DEFAULT CURRENT_DATE\n)\nRETURNS UUID AS $$\nDECLARE\n  v_execucao_id UUID;\nBEGIN\n  INSERT INTO farmer_rotinas_execucao (rotina_id, colaborador_id, data_execucao, concluida, concluida_em)\n  VALUES (p_rotina_id, p_colaborador_id, p_data, p_concluida, CASE WHEN p_concluida THEN NOW() ELSE NULL END)\n  ON CONFLICT (rotina_id, data_execucao)\n  DO UPDATE SET \n    concluida = p_concluida, \n    concluida_em = CASE WHEN p_concluida THEN NOW() ELSE NULL END\n  RETURNING id INTO v_execucao_id;\n  \n  RETURN v_execucao_id;\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER;\n\n-- Função: get_progresso_rotinas_hoje\n-- Retorna o progresso das rotinas do dia para um colaborador\nCREATE OR REPLACE FUNCTION get_progresso_rotinas_hoje(p_colaborador_id INTEGER)\nRETURNS TABLE (\n  total INTEGER,\n  concluidas INTEGER,\n  percentual NUMERIC\n) AS $$\nBEGIN\n  RETURN QUERY\n  SELECT \n    COUNT(*)::INTEGER as total,\n    COUNT(*) FILTER (WHERE COALESCE(e.concluida, false) = true)::INTEGER as concluidas,\n    CASE \n      WHEN COUNT(*) > 0 \n      THEN ROUND((COUNT(*) FILTER (WHERE COALESCE(e.concluida, false) = true)::NUMERIC / COUNT(*)) * 100, 0)\n      ELSE 0 \n    END as percentual\n  FROM farmer_rotinas r\n  LEFT JOIN farmer_rotinas_execucao e ON e.rotina_id = r.id AND e.data_execucao = CURRENT_DATE\n  WHERE r.colaborador_id = p_colaborador_id\n    AND r.ativo = true\n    AND (\n      r.frequencia = 'diario'\n      OR (r.frequencia = 'semanal' AND EXTRACT(ISODOW FROM CURRENT_DATE)::INTEGER = ANY(r.dias_semana))\n      OR (r.frequencia = 'mensal' AND EXTRACT(DAY FROM CURRENT_DATE)::INTEGER = r.dia_mes)\n    );\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER;\n\n-- Função: get_historico_rotinas\n-- Retorna o histórico de completude das rotinas nos últimos N dias\nCREATE OR REPLACE FUNCTION get_historico_rotinas(\n  p_colaborador_id INTEGER,\n  p_dias INTEGER DEFAULT 7\n)\nRETURNS TABLE (\n  data DATE,\n  total_rotinas INTEGER,\n  rotinas_concluidas INTEGER,\n  percentual NUMERIC,\n  tarefas_concluidas INTEGER\n) AS $$\nBEGIN\n  RETURN QUERY\n  WITH datas AS (\n    SELECT generate_series(\n      CURRENT_DATE - (p_dias - 1),\n      CURRENT_DATE,\n      '1 day'::interval\n    )::date as data\n  ),\n  rotinas_por_dia AS (\n    SELECT \n      d.data,\n      COUNT(r.id) as total,\n      COUNT(e.id) FILTER (WHERE e.concluida = true) as concluidas\n    FROM datas d\n    CROSS JOIN farmer_rotinas r\n    LEFT JOIN farmer_rotinas_execucao e ON e.rotina_id = r.id AND e.data_execucao = d.data\n    WHERE r.colaborador_id = p_colaborador_id\n      AND r.ativo = true\n      AND (\n        r.frequencia = 'diario'\n        OR (r.frequencia = 'semanal' AND EXTRACT(ISODOW FROM d.data)::INTEGER = ANY(r.dias_semana))\n        OR (r.frequencia = 'mensal' AND EXTRACT(DAY FROM d.data)::INTEGER = r.dia_mes)\n      )\n    GROUP BY d.data\n  ),\n  tarefas_por_dia AS (\n    SELECT \n      concluida_em::date as data,\n      COUNT(*) as total\n    FROM farmer_tarefas\n    WHERE colaborador_id = p_colaborador_id\n      AND concluida = true\n      AND concluida_em >= CURRENT_DATE - (p_dias - 1)\n    GROUP BY concluida_em::date\n  )\n  SELECT \n    r.data,\n    r.total::INTEGER as total_rotinas,\n    r.concluidas::INTEGER as rotinas_concluidas,\n    CASE WHEN r.total > 0 THEN ROUND((r.concluidas::NUMERIC / r.total) * 100, 0) ELSE 0 END as percentual,\n    COALESCE(t.total, 0)::INTEGER as tarefas_concluidas\n  FROM rotinas_por_dia r\n  LEFT JOIN tarefas_por_dia t ON t.data = r.data\n  ORDER BY r.data DESC;\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER;\n\n-- Comentários nas functions\nCOMMENT ON FUNCTION get_rotinas_do_dia IS 'Retorna as rotinas que devem aparecer para um colaborador em uma data específica';\nCOMMENT ON FUNCTION marcar_rotina_concluida IS 'Marca uma rotina como concluída (ou desmarca) para uma data';\nCOMMENT ON FUNCTION get_progresso_rotinas_hoje IS 'Retorna o progresso das rotinas do dia para um colaborador';\nCOMMENT ON FUNCTION get_historico_rotinas IS 'Retorna o histórico de completude das rotinas nos últimos N dias';\n"}	create_farmer_functions	producoes.emla@gmail.com	\N	\N
20260203102633	{"\n-- ============================================\n-- PAINEL FARMER - VIEWS DE ALERTAS\n-- ============================================\n\n-- View: Aniversariantes de hoje\nCREATE OR REPLACE VIEW vw_farmer_aniversariantes_hoje AS\nSELECT \n  a.id as aluno_id,\n  a.nome as aluno_nome,\n  a.whatsapp,\n  a.data_nascimento,\n  a.unidade_id,\n  EXTRACT(YEAR FROM AGE(a.data_nascimento))::INTEGER as idade,\n  p.id as professor_id,\n  p.nome as professor_nome,\n  c.nome as instrumento\nFROM alunos a\nLEFT JOIN professores p ON a.professor_atual_id = p.id\nLEFT JOIN cursos c ON a.curso_id = c.id\nWHERE EXTRACT(MONTH FROM a.data_nascimento) = EXTRACT(MONTH FROM CURRENT_DATE)\n  AND EXTRACT(DAY FROM a.data_nascimento) = EXTRACT(DAY FROM CURRENT_DATE)\n  AND a.status = 'ativo';\n\n-- View: Inadimplentes (alunos com status_pagamento = 'inadimplente' ou histórico pendente)\nCREATE OR REPLACE VIEW vw_farmer_inadimplentes AS\nSELECT \n  a.id as aluno_id,\n  a.nome as aluno_nome,\n  a.whatsapp,\n  a.unidade_id,\n  a.valor_parcela,\n  a.status_pagamento,\n  a.dia_vencimento,\n  p.id as professor_id,\n  p.nome as professor_nome,\n  c.nome as instrumento,\n  -- Calcular dias de atraso baseado no dia de vencimento\n  CASE \n    WHEN a.dia_vencimento IS NOT NULL AND EXTRACT(DAY FROM CURRENT_DATE) > a.dia_vencimento \n    THEN (EXTRACT(DAY FROM CURRENT_DATE) - a.dia_vencimento)::INTEGER\n    ELSE 0\n  END as dias_atraso\nFROM alunos a\nLEFT JOIN professores p ON a.professor_atual_id = p.id\nLEFT JOIN cursos c ON a.curso_id = c.id\nWHERE a.status = 'ativo'\n  AND (\n    a.status_pagamento = 'inadimplente'\n    OR a.status_pagamento = 'pendente'\n  )\nORDER BY a.valor_parcela DESC;\n\n-- View: Novos matriculados (últimos 7 dias)\nCREATE OR REPLACE VIEW vw_farmer_novos_matriculados AS\nSELECT \n  a.id as aluno_id,\n  a.nome as aluno_nome,\n  a.whatsapp,\n  a.unidade_id,\n  a.data_matricula,\n  a.valor_parcela,\n  p.id as professor_id,\n  p.nome as professor_nome,\n  c.nome as instrumento,\n  a.dia_aula,\n  a.horario_aula\nFROM alunos a\nLEFT JOIN professores p ON a.professor_atual_id = p.id\nLEFT JOIN cursos c ON a.curso_id = c.id\nWHERE a.status = 'ativo'\n  AND a.data_matricula >= CURRENT_DATE - INTERVAL '7 days'\nORDER BY a.data_matricula DESC;\n\n-- View: Renovações próximas (vencendo em até 60 dias)\nCREATE OR REPLACE VIEW vw_farmer_renovacoes_proximas AS\nSELECT \n  a.id as aluno_id,\n  a.nome as aluno_nome,\n  a.whatsapp,\n  a.unidade_id,\n  a.data_fim_contrato as data_vencimento,\n  (a.data_fim_contrato - CURRENT_DATE)::INTEGER as dias_para_vencer,\n  a.valor_parcela,\n  p.id as professor_id,\n  p.nome as professor_nome,\n  c.nome as instrumento,\n  -- Classificar urgência\n  CASE \n    WHEN a.data_fim_contrato < CURRENT_DATE THEN 'vencido'\n    WHEN a.data_fim_contrato <= CURRENT_DATE + INTERVAL '7 days' THEN 'urgente'\n    WHEN a.data_fim_contrato <= CURRENT_DATE + INTERVAL '15 days' THEN 'atencao'\n    ELSE 'normal'\n  END as urgencia\nFROM alunos a\nLEFT JOIN professores p ON a.professor_atual_id = p.id\nLEFT JOIN cursos c ON a.curso_id = c.id\nLEFT JOIN renovacoes r ON r.aluno_id = a.id \n  AND r.data_fim_novo_contrato > a.data_fim_contrato \n  AND r.status = 'concluida'\nWHERE a.status = 'ativo'\n  AND a.data_fim_contrato IS NOT NULL\n  AND a.data_fim_contrato <= CURRENT_DATE + INTERVAL '60 days'\n  AND r.id IS NULL -- Não tem renovação pendente\nORDER BY a.data_fim_contrato ASC;\n\n-- View: Resumo de alertas por unidade (para dashboard)\nCREATE OR REPLACE VIEW vw_farmer_resumo_alertas AS\nSELECT \n  u.id as unidade_id,\n  u.nome as unidade_nome,\n  -- Aniversariantes\n  (SELECT COUNT(*) FROM vw_farmer_aniversariantes_hoje WHERE unidade_id = u.id) as aniversariantes_hoje,\n  -- Inadimplentes\n  (SELECT COUNT(*) FROM vw_farmer_inadimplentes WHERE unidade_id = u.id) as inadimplentes,\n  -- Novos matriculados\n  (SELECT COUNT(*) FROM vw_farmer_novos_matriculados WHERE unidade_id = u.id) as novos_matriculados,\n  -- Renovações\n  (SELECT COUNT(*) FROM vw_farmer_renovacoes_proximas WHERE unidade_id = u.id AND urgencia = 'vencido') as renovacoes_vencidas,\n  (SELECT COUNT(*) FROM vw_farmer_renovacoes_proximas WHERE unidade_id = u.id AND urgencia = 'urgente') as renovacoes_urgentes,\n  (SELECT COUNT(*) FROM vw_farmer_renovacoes_proximas WHERE unidade_id = u.id AND urgencia = 'atencao') as renovacoes_atencao\nFROM unidades u\nWHERE u.ativo = true;\n\n-- Comentários nas views\nCOMMENT ON VIEW vw_farmer_aniversariantes_hoje IS 'Alunos que fazem aniversário hoje';\nCOMMENT ON VIEW vw_farmer_inadimplentes IS 'Alunos com pagamento pendente ou inadimplente';\nCOMMENT ON VIEW vw_farmer_novos_matriculados IS 'Alunos matriculados nos últimos 7 dias';\nCOMMENT ON VIEW vw_farmer_renovacoes_proximas IS 'Alunos com contrato vencendo em até 60 dias';\nCOMMENT ON VIEW vw_farmer_resumo_alertas IS 'Resumo de alertas por unidade para dashboard';\n"}	create_farmer_alert_views	producoes.emla@gmail.com	\N	\N
20260203114147	{"\n-- =====================================================\n-- MIGRAÇÃO: Sistema de Recados e Health Score\n-- =====================================================\n\n-- 1. Adicionar campo health_score na tabela alunos\nALTER TABLE alunos \nADD COLUMN IF NOT EXISTS health_score VARCHAR(10) DEFAULT NULL,\nADD COLUMN IF NOT EXISTS health_score_updated_at TIMESTAMPTZ DEFAULT NULL,\nADD COLUMN IF NOT EXISTS health_score_updated_by INTEGER DEFAULT NULL;\n\nCOMMENT ON COLUMN alunos.health_score IS 'Percepção do professor: verde (saudável), amarelo (alerta), vermelho (emergente)';\n\n-- 2. Criar tabela de histórico de health score\nCREATE TABLE IF NOT EXISTS alunos_health_score_historico (\n  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n  aluno_id INTEGER NOT NULL REFERENCES alunos(id) ON DELETE CASCADE,\n  professor_id INTEGER NOT NULL REFERENCES professores(id),\n  health_score VARCHAR(10) NOT NULL CHECK (health_score IN ('verde', 'amarelo', 'vermelho')),\n  observacao TEXT,\n  created_at TIMESTAMPTZ DEFAULT NOW()\n);\n\nCREATE INDEX IF NOT EXISTS idx_health_score_historico_aluno ON alunos_health_score_historico(aluno_id);\nCREATE INDEX IF NOT EXISTS idx_health_score_historico_professor ON alunos_health_score_historico(professor_id);\n\n-- 3. Criar tabela de campanhas de recados\nCREATE TABLE IF NOT EXISTS farmer_recados_campanhas (\n  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n  unidade_id UUID NOT NULL REFERENCES unidades(id),\n  colaborador_id INTEGER NOT NULL REFERENCES colaboradores(id),\n  titulo VARCHAR(200) NOT NULL,\n  tipo VARCHAR(50) NOT NULL CHECK (tipo IN ('health_score', 'video', 'relatorio', 'aviso', 'outro')),\n  template_id UUID REFERENCES farmer_templates(id),\n  mensagem_base TEXT NOT NULL,\n  data_limite DATE,\n  status VARCHAR(20) DEFAULT 'rascunho' CHECK (status IN ('rascunho', 'agendada', 'enviando', 'concluida', 'cancelada')),\n  total_destinatarios INTEGER DEFAULT 0,\n  enviados INTEGER DEFAULT 0,\n  erros INTEGER DEFAULT 0,\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  updated_at TIMESTAMPTZ DEFAULT NOW(),\n  started_at TIMESTAMPTZ,\n  completed_at TIMESTAMPTZ\n);\n\n-- 4. Criar tabela de destinatários da campanha\nCREATE TABLE IF NOT EXISTS farmer_recados_destinatarios (\n  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n  campanha_id UUID NOT NULL REFERENCES farmer_recados_campanhas(id) ON DELETE CASCADE,\n  professor_id INTEGER NOT NULL REFERENCES professores(id),\n  whatsapp VARCHAR(20),\n  mensagem_personalizada TEXT,\n  status VARCHAR(20) DEFAULT 'pendente' CHECK (status IN ('pendente', 'enviado', 'erro', 'cancelado')),\n  enviado_at TIMESTAMPTZ,\n  erro_mensagem TEXT,\n  created_at TIMESTAMPTZ DEFAULT NOW()\n);\n\nCREATE INDEX IF NOT EXISTS idx_recados_campanhas_unidade ON farmer_recados_campanhas(unidade_id);\nCREATE INDEX IF NOT EXISTS idx_recados_campanhas_status ON farmer_recados_campanhas(status);\nCREATE INDEX IF NOT EXISTS idx_recados_destinatarios_campanha ON farmer_recados_destinatarios(campanha_id);\n\n-- 5. RLS Policies\nALTER TABLE alunos_health_score_historico ENABLE ROW LEVEL SECURITY;\nALTER TABLE farmer_recados_campanhas ENABLE ROW LEVEL SECURITY;\nALTER TABLE farmer_recados_destinatarios ENABLE ROW LEVEL SECURITY;\n\nCREATE POLICY \\"health_score_historico_all\\" ON alunos_health_score_historico FOR ALL USING (true) WITH CHECK (true);\nCREATE POLICY \\"recados_campanhas_all\\" ON farmer_recados_campanhas FOR ALL USING (true) WITH CHECK (true);\nCREATE POLICY \\"recados_destinatarios_all\\" ON farmer_recados_destinatarios FOR ALL USING (true) WITH CHECK (true);\n\n-- 6. Function para atualizar health score\nCREATE OR REPLACE FUNCTION atualizar_health_score(\n  p_aluno_id INTEGER,\n  p_professor_id INTEGER,\n  p_health_score VARCHAR(10),\n  p_observacao TEXT DEFAULT NULL\n) RETURNS BOOLEAN AS $$\nBEGIN\n  UPDATE alunos \n  SET health_score = p_health_score, health_score_updated_at = NOW(), health_score_updated_by = p_professor_id\n  WHERE id = p_aluno_id;\n  \n  INSERT INTO alunos_health_score_historico (aluno_id, professor_id, health_score, observacao)\n  VALUES (p_aluno_id, p_professor_id, p_health_score, p_observacao);\n  \n  RETURN TRUE;\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER;\n\n-- 7. View para professores com resumo de carteira\nCREATE OR REPLACE VIEW vw_professores_carteira_resumo AS\nSELECT \n  p.id as professor_id,\n  p.nome as professor_nome,\n  p.telefone_whatsapp,\n  a.unidade_id,\n  COUNT(a.id) as total_alunos,\n  COUNT(CASE WHEN a.health_score = 'verde' THEN 1 END) as alunos_verdes,\n  COUNT(CASE WHEN a.health_score = 'amarelo' THEN 1 END) as alunos_amarelos,\n  COUNT(CASE WHEN a.health_score = 'vermelho' THEN 1 END) as alunos_vermelhos,\n  COUNT(CASE WHEN a.health_score IS NULL THEN 1 END) as alunos_sem_avaliacao\nFROM professores p\nLEFT JOIN alunos a ON a.professor_atual_id = p.id AND a.status = 'ativo'\nWHERE p.ativo = true\nGROUP BY p.id, p.nome, p.telefone_whatsapp, a.unidade_id;\n\n-- 8. Inserir templates de recados (usando estrutura correta)\nINSERT INTO farmer_templates (categoria, nome, mensagem, variaveis, ativo, ordem) VALUES\n('recado_professor', 'Avaliação Health Score', \n'Olá, {nome_professor}! 👋\n\nEstamos realizando um acompanhamento dos alunos e gostaríamos de contar com sua avaliação pedagógica.\n\nPor gentileza, atribua um coração correspondente ao lado do nome de cada aluno:\n\n💚 Aluno saudável: acompanha bem as aulas, é participativo e demonstra entusiasmo.\n💛 Aluno em alerta: apresenta sinais de desmotivação, faltas frequentes ou queda de rendimento.\n❤️ Aluno em situação emergente: apresenta grandes chances de evasão ou desistência.\n\n⚠️ Sua devolutiva é muito importante! Prazo: {data_limite}\n\n*Lista de alunos:*\n{lista_alunos}\n\nObrigado! 🎵',\nARRAY['nome_professor', 'data_limite', 'lista_alunos'], true, 1),\n\n('recado_professor', 'Lembrete de Vídeos', \n'Olá, {nome_professor}! 🎬\n\nLembrando dos vídeos de evolução dos alunos para este mês.\n\n📹 Grave um vídeo curto (30s a 1min) de cada aluno tocando.\n\n*Alunos pendentes:*\n{lista_alunos}\n\nPrazo: {data_limite}\n\nObrigado! 🎵',\nARRAY['nome_professor', 'data_limite', 'lista_alunos'], true, 2),\n\n('recado_professor', 'Relatório Mensal', \n'Olá, {nome_professor}! 📝\n\nPrecisamos do seu relatório de evolução dos alunos.\n\nPor favor, envie um breve feedback sobre cada aluno:\n- Progresso técnico\n- Comportamento em aula\n- Sugestões de repertório\n\n*Seus alunos:*\n{lista_alunos}\n\nPrazo: {data_limite}\n\nObrigado! 🎵',\nARRAY['nome_professor', 'data_limite', 'lista_alunos'], true, 3),\n\n('recado_professor', 'Aviso Geral', \n'Olá, {nome_professor}! 📢\n\n{mensagem_personalizada}\n\nQualquer dúvida, estamos à disposição!\n\nEquipe LA Music 🎵',\nARRAY['nome_professor', 'mensagem_personalizada'], true, 4)\nON CONFLICT DO NOTHING;\n"}	add_health_score_and_recados_system	producoes.emla@gmail.com	\N	\N
20260203125215	{"-- Corrigir função get_dados_retencao_ia para usar nomes corretos das colunas\nCREATE OR REPLACE FUNCTION public.get_dados_retencao_ia(\n  p_unidade_id uuid DEFAULT NULL::uuid, \n  p_ano integer DEFAULT (EXTRACT(year FROM CURRENT_DATE))::integer, \n  p_mes integer DEFAULT (EXTRACT(month FROM CURRENT_DATE))::integer\n)\nRETURNS json\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $function$\nDECLARE\n  resultado JSON;\n  mes_anterior INT;\n  ano_anterior INT;\n  ano_passado INT;\nBEGIN\n  -- Calcular mês anterior\n  IF p_mes = 1 THEN\n    mes_anterior := 12;\n    ano_anterior := p_ano - 1;\n  ELSE\n    mes_anterior := p_mes - 1;\n    ano_anterior := p_ano;\n  END IF;\n  ano_passado := p_ano - 1;\n\n  SELECT json_build_object(\n    'periodo', json_build_object(\n      'ano', p_ano,\n      'mes', p_mes,\n      'mes_nome', TRIM(TO_CHAR(TO_DATE(p_mes::text, 'MM'), 'Month'))\n    ),\n    \n    -- CORRIGIDO: usar nomes corretos das colunas da view vw_kpis_gestao_mensal\n    'kpis_gestao', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'unidade_id', kg.unidade_id,\n        'unidade_nome', kg.unidade_nome,\n        'total_alunos_ativos', COALESCE(kg.total_alunos_ativos, 0),\n        'total_alunos_pagantes', COALESCE(kg.total_alunos_pagantes, 0),\n        'ticket_medio', COALESCE(kg.ticket_medio, 0),\n        'mrr', COALESCE(kg.mrr, 0),\n        'tempo_permanencia_medio', COALESCE(kg.tempo_permanencia_medio, 0),\n        'ltv_medio', COALESCE(kg.ltv_medio, 0),\n        'inadimplencia_pct', COALESCE(kg.inadimplencia_pct, 0),\n        'faturamento_previsto', COALESCE(kg.faturamento_previsto, 0),\n        'faturamento_realizado', COALESCE(kg.faturamento_realizado, 0),\n        'churn_rate', COALESCE(kg.churn_rate, 0),\n        'total_evasoes', COALESCE(kg.total_evasoes, 0)\n      )), '[]'::json)\n      FROM vw_kpis_gestao_mensal kg\n      WHERE (p_unidade_id IS NULL OR kg.unidade_id = p_unidade_id)\n        AND kg.ano = p_ano\n        AND kg.mes = p_mes\n    ),\n    \n    'kpis_retencao', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'unidade_id', kr.unidade_id,\n        'unidade_nome', kr.unidade_nome,\n        'total_evasoes', kr.total_evasoes,\n        'avisos_previos', kr.avisos_previos,\n        'renovacoes_previstas', kr.renovacoes_previstas,\n        'renovacoes_realizadas', kr.renovacoes_realizadas,\n        'nao_renovacoes', kr.nao_renovacoes,\n        'renovacoes_pendentes', kr.renovacoes_pendentes,\n        'taxa_renovacao', kr.taxa_renovacao,\n        'taxa_nao_renovacao', kr.taxa_nao_renovacao,\n        'mrr_perdido', kr.mrr_perdido\n      )), '[]'::json)\n      FROM vw_kpis_retencao_mensal kr\n      WHERE (p_unidade_id IS NULL OR kr.unidade_id = p_unidade_id)\n        AND kr.ano = p_ano\n        AND kr.mes = p_mes\n    ),\n    \n    'renovacoes_proximas', (\n      SELECT COALESCE(json_agg(row_to_json(rp)), '[]'::json)\n      FROM get_resumo_renovacoes_proximas(p_unidade_id) rp\n    ),\n    \n    'alunos_renovacao_urgente', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'aluno_nome', rp.aluno_nome,\n        'professor_nome', rp.professor_nome,\n        'curso_nome', rp.curso_nome,\n        'valor_parcela', rp.valor_parcela,\n        'dias_ate_vencimento', rp.dias_ate_vencimento,\n        'tempo_permanencia_meses', rp.tempo_permanencia_meses,\n        'telefone', rp.telefone\n      ) ORDER BY rp.dias_ate_vencimento), '[]'::json)\n      FROM vw_renovacoes_proximas rp\n      WHERE (p_unidade_id IS NULL OR rp.unidade_id = p_unidade_id)\n        AND rp.status_renovacao IN ('vencido', 'urgente_7_dias')\n      LIMIT 20\n    ),\n    \n    'mes_anterior', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'alunos_pagantes', dm.alunos_pagantes,\n        'churn_rate', dm.churn_rate,\n        'ticket_medio', dm.ticket_medio,\n        'taxa_renovacao', dm.taxa_renovacao,\n        'inadimplencia', dm.inadimplencia,\n        'tempo_permanencia', dm.tempo_permanencia,\n        'reajuste_parcelas', dm.reajuste_parcelas\n      )), '[]'::json)\n      FROM dados_mensais dm\n      WHERE (p_unidade_id IS NULL OR dm.unidade_id = p_unidade_id)\n        AND dm.ano = ano_anterior\n        AND dm.mes = mes_anterior\n    ),\n    \n    'mesmo_mes_ano_passado', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'alunos_pagantes', dm.alunos_pagantes,\n        'churn_rate', dm.churn_rate,\n        'ticket_medio', dm.ticket_medio,\n        'taxa_renovacao', dm.taxa_renovacao,\n        'inadimplencia', dm.inadimplencia,\n        'tempo_permanencia', dm.tempo_permanencia,\n        'reajuste_parcelas', dm.reajuste_parcelas\n      )), '[]'::json)\n      FROM dados_mensais dm\n      WHERE (p_unidade_id IS NULL OR dm.unidade_id = p_unidade_id)\n        AND dm.ano = ano_passado\n        AND dm.mes = p_mes\n    ),\n    \n    'metas', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'unidade_id', m.unidade_id,\n        'meta_leads', m.meta_leads,\n        'meta_experimentais', m.meta_experimentais,\n        'meta_matriculas', m.meta_matriculas,\n        'meta_taxa_conversao_experimental', m.meta_taxa_conversao_experimental,\n        'meta_taxa_conversao_lead', m.meta_taxa_conversao_lead,\n        'meta_faturamento_passaportes', m.meta_faturamento_passaportes,\n        'meta_alunos_pagantes', m.meta_alunos_pagantes,\n        'meta_alunos_ativos', m.meta_alunos_ativos,\n        'meta_ticket_medio', m.meta_ticket_medio,\n        'meta_churn_maximo', m.meta_churn_maximo,\n        'meta_evasoes_maximo', m.meta_evasoes_maximo,\n        'meta_renovacoes', m.meta_renovacoes,\n        'meta_taxa_renovacao', m.meta_taxa_renovacao,\n        'meta_inadimplencia_maxima', m.meta_inadimplencia_maxima,\n        'meta_ltv_meses', m.meta_ltv_meses,\n        'meta_faturamento_parcelas', m.meta_faturamento_parcelas\n      )), '[]'::json)\n      FROM metas m\n      WHERE (p_unidade_id IS NULL OR m.unidade_id = p_unidade_id OR m.unidade_id IS NULL)\n        AND m.ano = p_ano\n        AND (m.mes = p_mes OR m.mes IS NULL)\n        AND m.ativo = true\n    ),\n    \n    'evasoes_recentes', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'aluno_nome', a.nome,\n        'professor_nome', p.nome,\n        'motivo', ms.nome,\n        'tipo_saida', ts.nome,\n        'valor_parcela', e.valor_parcela,\n        'tempo_permanencia', a.tempo_permanencia_meses,\n        'data_saida', e.data_evasao\n      ) ORDER BY e.data_evasao DESC), '[]'::json)\n      FROM evasoes_v2 e\n      LEFT JOIN alunos a ON e.aluno_id = a.id\n      LEFT JOIN professores p ON e.professor_id = p.id\n      LEFT JOIN motivos_saida ms ON e.motivo_saida_id = ms.id\n      LEFT JOIN tipos_saida ts ON e.tipo_saida_id = ts.id\n      WHERE (p_unidade_id IS NULL OR e.unidade_id = p_unidade_id)\n        AND e.data_evasao >= (CURRENT_DATE - INTERVAL '30 days')\n      LIMIT 15\n    ),\n    \n    'permanencia_por_faixa', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'faixa', faixa,\n        'quantidade', quantidade,\n        'percentual', ROUND((quantidade::numeric / NULLIF(total, 0) * 100), 1)\n      ) ORDER BY ordem), '[]'::json)\n      FROM (\n        SELECT \n          CASE \n            WHEN tempo_permanencia_meses < 6 THEN '0-6 meses'\n            WHEN tempo_permanencia_meses < 12 THEN '6-12 meses'\n            WHEN tempo_permanencia_meses < 24 THEN '1-2 anos'\n            WHEN tempo_permanencia_meses < 36 THEN '2-3 anos'\n            ELSE '3+ anos'\n          END as faixa,\n          CASE \n            WHEN tempo_permanencia_meses < 6 THEN 1\n            WHEN tempo_permanencia_meses < 12 THEN 2\n            WHEN tempo_permanencia_meses < 24 THEN 3\n            WHEN tempo_permanencia_meses < 36 THEN 4\n            ELSE 5\n          END as ordem,\n          COUNT(*) as quantidade,\n          SUM(COUNT(*)) OVER () as total\n        FROM alunos\n        WHERE status = 'ativo'\n          AND (p_unidade_id IS NULL OR unidade_id = p_unidade_id)\n        GROUP BY faixa, ordem\n      ) sub\n    ),\n    \n    'dados_mes_atual', (\n      SELECT COALESCE(json_agg(json_build_object(\n        'alunos_pagantes', dm.alunos_pagantes,\n        'novas_matriculas', dm.novas_matriculas,\n        'evasoes', dm.evasoes,\n        'churn_rate', dm.churn_rate,\n        'ticket_medio', dm.ticket_medio,\n        'taxa_renovacao', dm.taxa_renovacao,\n        'tempo_permanencia', dm.tempo_permanencia,\n        'inadimplencia', dm.inadimplencia,\n        'reajuste_parcelas', dm.reajuste_parcelas,\n        'faturamento_estimado', dm.faturamento_estimado,\n        'saldo_liquido', dm.saldo_liquido\n      )), '[]'::json)\n      FROM dados_mensais dm\n      WHERE (p_unidade_id IS NULL OR dm.unidade_id = p_unidade_id)\n        AND dm.ano = p_ano\n        AND dm.mes = p_mes\n    )\n    \n  ) INTO resultado;\n  \n  RETURN resultado;\nEND;\n$function$;"}	fix_get_dados_retencao_ia_column_names	producoes.emla@gmail.com	\N	\N
20260203225120	{"-- Drop e recriar função get_rotinas_do_dia com nome do responsável\nDROP FUNCTION IF EXISTS public.get_rotinas_do_dia(integer, date);\n\nCREATE OR REPLACE FUNCTION public.get_rotinas_do_dia(\n  p_colaborador_id integer, \n  p_data date DEFAULT CURRENT_DATE\n)\nRETURNS TABLE(\n  rotina_id uuid, \n  descricao character varying, \n  frequencia character varying, \n  prioridade character varying, \n  concluida boolean, \n  execucao_id uuid,\n  responsavel_nome character varying,\n  responsavel_apelido character varying\n)\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $function$\nBEGIN\n  RETURN QUERY\n  SELECT \n    r.id as rotina_id,\n    r.descricao,\n    r.frequencia,\n    r.prioridade,\n    COALESCE(e.concluida, false) as concluida,\n    e.id as execucao_id,\n    c.nome as responsavel_nome,\n    c.apelido as responsavel_apelido\n  FROM farmer_rotinas r\n  LEFT JOIN farmer_rotinas_execucao e ON e.rotina_id = r.id AND e.data_execucao = p_data\n  LEFT JOIN colaboradores c ON c.id = r.colaborador_id\n  WHERE r.colaborador_id = p_colaborador_id\n    AND r.ativo = true\n    AND (\n      -- Diário: sempre aparece\n      r.frequencia = 'diario'\n      OR\n      -- Semanal: aparece no dia da semana configurado (1=seg, 7=dom)\n      (r.frequencia = 'semanal' AND EXTRACT(ISODOW FROM p_data)::INTEGER = ANY(r.dias_semana))\n      OR\n      -- Mensal: aparece no dia do mês configurado\n      (r.frequencia = 'mensal' AND EXTRACT(DAY FROM p_data)::INTEGER = r.dia_mes)\n    )\n  ORDER BY \n    CASE r.prioridade WHEN 'alta' THEN 0 ELSE 1 END,\n    r.descricao;\nEND;\n$function$;"}	drop_and_recreate_get_rotinas_do_dia	producoes.emla@gmail.com	\N	\N
20260204110718	{"\n-- =====================================================\n-- FASE 1.1: Tabela de Perfis\n-- Armazena os tipos de perfil (Admin, Gerente, Farmer, etc.)\n-- =====================================================\n\nCREATE TABLE IF NOT EXISTS perfis (\n  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n  nome VARCHAR(50) NOT NULL UNIQUE,\n  descricao TEXT,\n  nivel INTEGER NOT NULL DEFAULT 10,\n  icone VARCHAR(10) DEFAULT '👤',\n  cor VARCHAR(20) DEFAULT '#3b82f6',\n  sistema BOOLEAN DEFAULT FALSE,\n  ativo BOOLEAN DEFAULT TRUE,\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  updated_at TIMESTAMPTZ DEFAULT NOW()\n);\n\n-- Comentário da tabela\nCOMMENT ON TABLE perfis IS 'Perfis de acesso do sistema (Admin, Gerente, Farmer, Hunter, etc.)';\nCOMMENT ON COLUMN perfis.nivel IS 'Nível hierárquico: 100=Admin, 50=Gerente, 30=Operacional, 20=Professor, 10=Visualizador';\nCOMMENT ON COLUMN perfis.sistema IS 'Perfis de sistema não podem ser excluídos';\n\n-- Índice para busca por nome\nCREATE INDEX IF NOT EXISTS idx_perfis_nome ON perfis(nome);\nCREATE INDEX IF NOT EXISTS idx_perfis_ativo ON perfis(ativo) WHERE ativo = true;\n"}	create_perfis_table	producoes.emla@gmail.com	\N	\N
20260204110727	{"\n-- =====================================================\n-- FASE 1.2: Tabela de Permissões\n-- Armazena todas as permissões granulares do sistema\n-- =====================================================\n\nCREATE TABLE IF NOT EXISTS permissoes (\n  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n  codigo VARCHAR(100) NOT NULL UNIQUE,\n  modulo VARCHAR(50) NOT NULL,\n  acao VARCHAR(50) NOT NULL,\n  descricao TEXT,\n  categoria VARCHAR(20) DEFAULT 'OPERACIONAL',\n  ordem INTEGER DEFAULT 0,\n  ativo BOOLEAN DEFAULT TRUE,\n  created_at TIMESTAMPTZ DEFAULT NOW()\n);\n\n-- Comentários\nCOMMENT ON TABLE permissoes IS 'Permissões granulares do sistema (ex: alunos.ver, alunos.editar)';\nCOMMENT ON COLUMN permissoes.codigo IS 'Código único da permissão no formato modulo.acao (ex: dashboard.ver)';\nCOMMENT ON COLUMN permissoes.categoria IS 'Categoria: SISTEMA, OPERACIONAL, ADMIN';\n\n-- Índices\nCREATE INDEX IF NOT EXISTS idx_permissoes_codigo ON permissoes(codigo);\nCREATE INDEX IF NOT EXISTS idx_permissoes_modulo ON permissoes(modulo);\nCREATE INDEX IF NOT EXISTS idx_permissoes_categoria ON permissoes(categoria);\n"}	create_permissoes_table	producoes.emla@gmail.com	\N	\N
20260204110737	{"\n-- =====================================================\n-- FASE 1.3: Tabela de Relacionamento Perfil-Permissões\n-- Liga perfis às suas permissões\n-- =====================================================\n\nCREATE TABLE IF NOT EXISTS perfil_permissoes (\n  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n  perfil_id UUID NOT NULL REFERENCES perfis(id) ON DELETE CASCADE,\n  permissao_id UUID NOT NULL REFERENCES permissoes(id) ON DELETE CASCADE,\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  UNIQUE(perfil_id, permissao_id)\n);\n\n-- Comentário\nCOMMENT ON TABLE perfil_permissoes IS 'Relacionamento N:N entre perfis e permissões';\n\n-- Índices para performance\nCREATE INDEX IF NOT EXISTS idx_perfil_permissoes_perfil ON perfil_permissoes(perfil_id);\nCREATE INDEX IF NOT EXISTS idx_perfil_permissoes_permissao ON perfil_permissoes(permissao_id);\n"}	create_perfil_permissoes_table	producoes.emla@gmail.com	\N	\N
20260204110759	{"\n-- =====================================================\n-- FASE 1.4: Tabela de Relacionamento Usuário-Perfis\n-- Liga usuários aos seus perfis (com escopo de unidade opcional)\n-- NOTA: usuarios.id é INTEGER, não UUID\n-- =====================================================\n\nCREATE TABLE IF NOT EXISTS usuario_perfis (\n  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n  usuario_id INTEGER NOT NULL REFERENCES usuarios(id) ON DELETE CASCADE,\n  perfil_id UUID NOT NULL REFERENCES perfis(id) ON DELETE CASCADE,\n  unidade_id UUID REFERENCES unidades(id) ON DELETE CASCADE,\n  ativo BOOLEAN DEFAULT TRUE,\n  created_at TIMESTAMPTZ DEFAULT NOW(),\n  updated_at TIMESTAMPTZ DEFAULT NOW()\n);\n\n-- Comentários\nCOMMENT ON TABLE usuario_perfis IS 'Relacionamento N:N entre usuários e perfis, com escopo opcional de unidade';\nCOMMENT ON COLUMN usuario_perfis.unidade_id IS 'Se NULL, o perfil vale para todas as unidades';\n\n-- Índices únicos para evitar duplicatas (tratando NULL corretamente)\nCREATE UNIQUE INDEX IF NOT EXISTS idx_usuario_perfis_unique_with_unidade \nON usuario_perfis(usuario_id, perfil_id, unidade_id) \nWHERE unidade_id IS NOT NULL;\n\nCREATE UNIQUE INDEX IF NOT EXISTS idx_usuario_perfis_unique_without_unidade \nON usuario_perfis(usuario_id, perfil_id) \nWHERE unidade_id IS NULL;\n\n-- Índices para performance\nCREATE INDEX IF NOT EXISTS idx_usuario_perfis_usuario ON usuario_perfis(usuario_id);\nCREATE INDEX IF NOT EXISTS idx_usuario_perfis_perfil ON usuario_perfis(perfil_id);\nCREATE INDEX IF NOT EXISTS idx_usuario_perfis_unidade ON usuario_perfis(unidade_id);\n"}	create_usuario_perfis_table_v2	producoes.emla@gmail.com	\N	\N
20260204110809	{"\n-- =====================================================\n-- FASE 1.5: Tabela de Auditoria de Acesso\n-- Registra todas as ações de permissão (login, alterações, etc.)\n-- =====================================================\n\nCREATE TABLE IF NOT EXISTS auditoria_acesso (\n  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n  usuario_id INTEGER REFERENCES usuarios(id) ON DELETE SET NULL,\n  usuario_nome VARCHAR(255),\n  acao VARCHAR(50) NOT NULL,\n  entidade VARCHAR(50),\n  entidade_id UUID,\n  detalhes JSONB,\n  ip_address VARCHAR(45),\n  user_agent TEXT,\n  created_at TIMESTAMPTZ DEFAULT NOW()\n);\n\n-- Comentários\nCOMMENT ON TABLE auditoria_acesso IS 'Log de auditoria para ações de acesso e permissões';\nCOMMENT ON COLUMN auditoria_acesso.acao IS 'Tipo de ação: login, logout, criar, editar, excluir, atribuir_perfil, etc.';\nCOMMENT ON COLUMN auditoria_acesso.entidade IS 'Entidade afetada: usuario, perfil, permissao, etc.';\n\n-- Índices para consultas de auditoria\nCREATE INDEX IF NOT EXISTS idx_auditoria_usuario ON auditoria_acesso(usuario_id);\nCREATE INDEX IF NOT EXISTS idx_auditoria_acao ON auditoria_acesso(acao);\nCREATE INDEX IF NOT EXISTS idx_auditoria_created ON auditoria_acesso(created_at DESC);\nCREATE INDEX IF NOT EXISTS idx_auditoria_entidade ON auditoria_acesso(entidade, entidade_id);\n"}	create_auditoria_acesso_table	producoes.emla@gmail.com	\N	\N
20260204110830	{"\n-- =====================================================\n-- FASE 1.6: Funções SQL para verificação de permissões\n-- =====================================================\n\n-- Função: Verifica se usuário tem uma permissão específica\n-- Retorna TRUE se:\n-- 1. Usuário é admin antigo (perfil = 'admin' na tabela usuarios)\n-- 2. Usuário tem a permissão via novo sistema (usuario_perfis -> perfil_permissoes -> permissoes)\nCREATE OR REPLACE FUNCTION usuario_tem_permissao(\n  p_usuario_id INTEGER,\n  p_codigo_permissao VARCHAR,\n  p_unidade_id UUID DEFAULT NULL\n)\nRETURNS BOOLEAN\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  v_is_admin_antigo BOOLEAN;\nBEGIN\n  -- 1. Verificar se é admin antigo (mantém compatibilidade)\n  SELECT (perfil = 'admin' AND ativo = true) INTO v_is_admin_antigo\n  FROM usuarios\n  WHERE id = p_usuario_id;\n  \n  IF v_is_admin_antigo THEN\n    RETURN TRUE;\n  END IF;\n  \n  -- 2. Verificar no novo sistema de permissões\n  RETURN EXISTS (\n    SELECT 1\n    FROM usuario_perfis up\n    JOIN perfil_permissoes pp ON pp.perfil_id = up.perfil_id\n    JOIN permissoes p ON p.id = pp.permissao_id\n    WHERE up.usuario_id = p_usuario_id\n      AND up.ativo = true\n      AND p.codigo = p_codigo_permissao\n      AND p.ativo = true\n      AND (\n        up.unidade_id IS NULL  -- Perfil global\n        OR up.unidade_id = p_unidade_id  -- Perfil específico da unidade\n        OR p_unidade_id IS NULL  -- Não está filtrando por unidade\n      )\n  );\nEND;\n$$;\n\n-- Função: Lista todas as permissões de um usuário\nCREATE OR REPLACE FUNCTION usuario_permissoes(p_usuario_id INTEGER)\nRETURNS TABLE(codigo VARCHAR, modulo VARCHAR, acao VARCHAR, descricao TEXT)\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  v_is_admin_antigo BOOLEAN;\nBEGIN\n  -- Verificar se é admin antigo\n  SELECT (perfil = 'admin' AND ativo = true) INTO v_is_admin_antigo\n  FROM usuarios\n  WHERE id = p_usuario_id;\n  \n  IF v_is_admin_antigo THEN\n    -- Admin antigo tem TODAS as permissões\n    RETURN QUERY\n    SELECT p.codigo, p.modulo, p.acao, p.descricao\n    FROM permissoes p\n    WHERE p.ativo = true\n    ORDER BY p.categoria, p.ordem;\n  ELSE\n    -- Buscar permissões do novo sistema\n    RETURN QUERY\n    SELECT DISTINCT p.codigo, p.modulo, p.acao, p.descricao\n    FROM usuario_perfis up\n    JOIN perfil_permissoes pp ON pp.perfil_id = up.perfil_id\n    JOIN permissoes p ON p.id = pp.permissao_id\n    WHERE up.usuario_id = p_usuario_id\n      AND up.ativo = true\n      AND p.ativo = true\n    ORDER BY p.modulo, p.acao;\n  END IF;\nEND;\n$$;\n\n-- Função: Lista todos os perfis de um usuário\nCREATE OR REPLACE FUNCTION usuario_perfis_lista(p_usuario_id INTEGER)\nRETURNS TABLE(\n  perfil_id UUID,\n  perfil_nome VARCHAR,\n  perfil_nivel INTEGER,\n  perfil_icone VARCHAR,\n  perfil_cor VARCHAR,\n  unidade_id UUID,\n  unidade_nome VARCHAR\n)\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nBEGIN\n  RETURN QUERY\n  SELECT \n    pf.id,\n    pf.nome,\n    pf.nivel,\n    pf.icone,\n    pf.cor,\n    up.unidade_id,\n    u.nome\n  FROM usuario_perfis up\n  JOIN perfis pf ON pf.id = up.perfil_id\n  LEFT JOIN unidades u ON u.id = up.unidade_id\n  WHERE up.usuario_id = p_usuario_id\n    AND up.ativo = true\n    AND pf.ativo = true\n  ORDER BY pf.nivel DESC, pf.nome;\nEND;\n$$;\n\n-- Comentários\nCOMMENT ON FUNCTION usuario_tem_permissao IS 'Verifica se usuário tem permissão específica (compatível com admin antigo)';\nCOMMENT ON FUNCTION usuario_permissoes IS 'Lista todas as permissões de um usuário';\nCOMMENT ON FUNCTION usuario_perfis_lista IS 'Lista todos os perfis atribuídos a um usuário';\n"}	create_permission_functions	producoes.emla@gmail.com	\N	\N
