20260204200701	{"-- Tabela para tracking do onboarding de usuários\nCREATE TABLE usuario_onboarding (\n  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n  usuario_id INTEGER REFERENCES usuarios(id) ON DELETE CASCADE,\n  \n  -- Checklist inicial (obrigatório)\n  senha_alterada BOOLEAN DEFAULT false,\n  foto_uploaded BOOLEAN DEFAULT false,\n  perfil_completo BOOLEAN DEFAULT false,\n  checklist_completo BOOLEAN DEFAULT false,\n  \n  -- Tours por página (automático)\n  tour_dashboard BOOLEAN DEFAULT false,\n  tour_alunos BOOLEAN DEFAULT false,\n  tour_comercial BOOLEAN DEFAULT false,\n  tour_professores BOOLEAN DEFAULT false,\n  tour_salas BOOLEAN DEFAULT false,\n  tour_metas BOOLEAN DEFAULT false,\n  tour_projetos BOOLEAN DEFAULT false,\n  tour_administrativo BOOLEAN DEFAULT false,\n  tour_config BOOLEAN DEFAULT false,\n  \n  -- Metadados\n  primeiro_acesso_em TIMESTAMP DEFAULT now(),\n  ultimo_tour_em TIMESTAMP,\n  tours_completados INT DEFAULT 0,\n  \n  created_at TIMESTAMP DEFAULT now(),\n  updated_at TIMESTAMP DEFAULT now(),\n  \n  UNIQUE(usuario_id)\n);\n\n-- Trigger para updated_at\nCREATE OR REPLACE FUNCTION update_onboarding_updated_at()\nRETURNS TRIGGER AS $$\nBEGIN\n  NEW.updated_at = now();\n  RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql;\n\nCREATE TRIGGER update_usuario_onboarding_updated_at\n  BEFORE UPDATE ON usuario_onboarding\n  FOR EACH ROW\n  EXECUTE FUNCTION update_onboarding_updated_at();\n\n-- RLS\nALTER TABLE usuario_onboarding ENABLE ROW LEVEL SECURITY;\n\nCREATE POLICY \\"Usuários podem ver seu próprio onboarding\\"\n  ON usuario_onboarding\n  FOR SELECT\n  USING (usuario_id IN (\n    SELECT id FROM usuarios WHERE auth_user_id = auth.uid()\n  ));\n\nCREATE POLICY \\"Usuários podem atualizar seu próprio onboarding\\"\n  ON usuario_onboarding\n  FOR UPDATE\n  USING (usuario_id IN (\n    SELECT id FROM usuarios WHERE auth_user_id = auth.uid()\n  ));\n\nCREATE POLICY \\"Sistema pode inserir onboarding\\"\n  ON usuario_onboarding\n  FOR INSERT\n  WITH CHECK (usuario_id IN (\n    SELECT id FROM usuarios WHERE auth_user_id = auth.uid()\n  ));\n\n-- Índice para performance\nCREATE INDEX idx_usuario_onboarding_usuario_id ON usuario_onboarding(usuario_id);\n\n-- Comentários\nCOMMENT ON TABLE usuario_onboarding IS 'Tracking do progresso de onboarding de cada usuário';\nCOMMENT ON COLUMN usuario_onboarding.checklist_completo IS 'True quando todas as tarefas obrigatórias do primeiro acesso foram concluídas';"}	create_usuario_onboarding	producoes.emla@gmail.com	\N	\N
20260204111035	{"\n-- =====================================================\n-- FASE 3: Habilitar RLS nas novas tabelas\n-- =====================================================\n\n-- Habilitar RLS\nALTER TABLE perfis ENABLE ROW LEVEL SECURITY;\nALTER TABLE permissoes ENABLE ROW LEVEL SECURITY;\nALTER TABLE perfil_permissoes ENABLE ROW LEVEL SECURITY;\nALTER TABLE usuario_perfis ENABLE ROW LEVEL SECURITY;\nALTER TABLE auditoria_acesso ENABLE ROW LEVEL SECURITY;\n\n-- Políticas para PERFIS (todos podem ler, só admin pode modificar)\nCREATE POLICY \\"Perfis: leitura pública\\" ON perfis\n  FOR SELECT USING (true);\n\nCREATE POLICY \\"Perfis: admin pode tudo\\" ON perfis\n  FOR ALL USING (is_admin());\n\n-- Políticas para PERMISSOES (todos podem ler, só admin pode modificar)\nCREATE POLICY \\"Permissoes: leitura pública\\" ON permissoes\n  FOR SELECT USING (true);\n\nCREATE POLICY \\"Permissoes: admin pode tudo\\" ON permissoes\n  FOR ALL USING (is_admin());\n\n-- Políticas para PERFIL_PERMISSOES (todos podem ler, só admin pode modificar)\nCREATE POLICY \\"Perfil_permissoes: leitura pública\\" ON perfil_permissoes\n  FOR SELECT USING (true);\n\nCREATE POLICY \\"Perfil_permissoes: admin pode tudo\\" ON perfil_permissoes\n  FOR ALL USING (is_admin());\n\n-- Políticas para USUARIO_PERFIS (usuário vê os próprios, admin vê todos)\nCREATE POLICY \\"Usuario_perfis: usuário vê próprios\\" ON usuario_perfis\n  FOR SELECT USING (\n    usuario_id IN (\n      SELECT id FROM usuarios WHERE auth_user_id = auth.uid()\n    )\n    OR is_admin()\n  );\n\nCREATE POLICY \\"Usuario_perfis: admin pode tudo\\" ON usuario_perfis\n  FOR ALL USING (is_admin());\n\n-- Políticas para AUDITORIA_ACESSO (só admin pode ver e inserir)\nCREATE POLICY \\"Auditoria: admin pode ver\\" ON auditoria_acesso\n  FOR SELECT USING (is_admin());\n\nCREATE POLICY \\"Auditoria: sistema pode inserir\\" ON auditoria_acesso\n  FOR INSERT WITH CHECK (true);\n"}	enable_rls_new_tables	producoes.emla@gmail.com	\N	\N
