ENT ON COLUMN usuario_onboarding.tours_completados IS 'Contador de tours completados';"}	create_usuario_onboarding_table	producoes.emla@gmail.com	\N	\N
20260205000435	{"-- Criar a função do trigger\nCREATE OR REPLACE FUNCTION sync_lead_to_leads_diarios()\nRETURNS TRIGGER AS $$\nDECLARE\n    v_tipo TEXT;\n    v_existing_id INTEGER;\nBEGIN\n    -- Determinar o tipo baseado no status do lead\n    CASE NEW.status\n        WHEN 'novo' THEN v_tipo := 'lead';\n        WHEN 'agendado' THEN v_tipo := 'lead';\n        WHEN 'experimental_agendada' THEN v_tipo := 'experimental_agendada';\n        WHEN 'experimental_realizada' THEN v_tipo := 'experimental_realizada';\n        WHEN 'compareceu' THEN v_tipo := 'experimental_realizada';\n        WHEN 'matriculado' THEN v_tipo := 'matricula';\n        WHEN 'convertido' THEN v_tipo := 'matricula';\n        ELSE v_tipo := 'lead';\n    END CASE;\n\n    -- Verificar se já existe um registro em leads_diarios para este lead\n    SELECT id INTO v_existing_id\n    FROM leads_diarios\n    WHERE observacoes LIKE 'lead_id:' || NEW.id::TEXT || '%'\n    LIMIT 1;\n\n    IF v_existing_id IS NOT NULL THEN\n        -- Atualizar registro existente\n        UPDATE leads_diarios\n        SET \n            tipo = v_tipo,\n            aluno_nome = NEW.nome,\n            canal_origem_id = NEW.canal_origem_id,\n            curso_id = NEW.curso_interesse_id,\n            professor_experimental_id = NEW.professor_experimental_id,\n            data = COALESCE(\n                CASE \n                    WHEN v_tipo = 'matricula' THEN NEW.data_conversao\n                    WHEN v_tipo LIKE 'experimental%' THEN NEW.data_experimental\n                    ELSE NEW.data_contato\n                END,\n                NEW.data_contato\n            )::DATE,\n            updated_at = NOW()\n        WHERE id = v_existing_id;\n    ELSE\n        -- Inserir novo registro\n        INSERT INTO leads_diarios (\n            unidade_id,\n            data,\n            tipo,\n            quantidade,\n            aluno_nome,\n            canal_origem_id,\n            curso_id,\n            professor_experimental_id,\n            observacoes,\n            created_at\n        ) VALUES (\n            NEW.unidade_id,\n            NEW.data_contato::DATE,\n            v_tipo,\n            1,\n            NEW.nome,\n            NEW.canal_origem_id,\n            NEW.curso_interesse_id,\n            NEW.professor_experimental_id,\n            'lead_id:' || NEW.id::TEXT || ' - Sincronizado do RP-EMUSES',\n            NOW()\n        );\n    END IF;\n\n    RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql;\n\n-- Criar o trigger na tabela leads\nDROP TRIGGER IF EXISTS trigger_sync_lead_to_leads_diarios ON leads;\n\nCREATE TRIGGER trigger_sync_lead_to_leads_diarios\n    AFTER INSERT OR UPDATE ON leads\n    FOR EACH ROW\n    EXECUTE FUNCTION sync_lead_to_leads_diarios();"}	sync_leads_to_leads_diarios_trigger	producoes.emla@gmail.com	\N	\N
20260205002048	{"-- Criar a função do trigger para sincronizar alunos (matrículas) para leads_diarios\nCREATE OR REPLACE FUNCTION sync_aluno_to_leads_diarios()\nRETURNS TRIGGER AS $$\nDECLARE\n    v_existing_id INTEGER;\nBEGIN\n    -- Só sincronizar se for uma nova matrícula (INSERT) ou mudança de status para ativo\n    IF TG_OP = 'INSERT' OR (TG_OP = 'UPDATE' AND NEW.status = 'ativo' AND OLD.status != 'ativo') THEN\n        \n        -- Verificar se já existe um registro em leads_diarios para este aluno\n        SELECT id INTO v_existing_id\n        FROM leads_diarios\n        WHERE observacoes LIKE 'aluno_id:' || NEW.id::TEXT || '%'\n        LIMIT 1;\n\n        IF v_existing_id IS NULL THEN\n            -- Inserir novo registro de matrícula\n            INSERT INTO leads_diarios (\n                unidade_id,\n                data,\n                tipo,\n                quantidade,\n                aluno_nome,\n                curso_id,\n                professor_experimental_id,\n                valor_passaporte,\n                valor_parcela,\n                observacoes,\n                created_at\n            ) VALUES (\n                NEW.unidade_id,\n                NEW.data_matricula::DATE,\n                'matricula',\n                1,\n                NEW.nome,\n                NEW.curso_id,\n                NEW.professor_experimental_id,\n                NEW.valor_passaporte,\n                NEW.valor_parcela,\n                'aluno_id:' || NEW.id::TEXT || ' - Sincronizado do RP-EMUSES',\n                NOW()\n            );\n        END IF;\n    END IF;\n\n    RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql;\n\n-- Criar o trigger na tabela alunos\nDROP TRIGGER IF EXISTS trigger_sync_aluno_to_leads_diarios ON alunos;\n\nCREATE TRIGGER trigger_sync_aluno_to_leads_diarios\n    AFTER INSERT OR UPDATE ON alunos\n    FOR EACH ROW\n    EXECUTE FUNCTION sync_aluno_to_leads_diarios();"}	sync_alunos_to_leads_diarios_trigger	producoes.emla@gmail.com	\N	\N
