n    ELSE 'cyan'\n  END as avatar_cor\nFROM usuarios \nWHERE perfil = 'admin' AND nome IN ('Quintela', 'Ju', 'Krissya', 'Ana', 'Hugo')\nON CONFLICT DO NOTHING;"}	create_projeto_equipe_membros_and_config	producoes.emla@gmail.com	\N	\N
